INSERT INTO `categories` (`id`, `name`, `keyword`, `description`, `created_at`, `updated_at`) VALUES
(1, 'Huyền Huyễn', 'Huyền Huyễn', 'Truyện có những yếu tố huyền bí, khoa học khó giải thích.', NULL, '2020-09-08 17:14:00'),
(2, 'Kiếm Hiệp', 'Kiếm Hiệp', 'Truyện thường xoay quanh cuộc đời của nhân vật chính, quá trình rèn luyện, trưởng thành, tìm kiếm, học tập các bí kíp võ công, cùng những cuộc phiêu lưu, truy đuổi, chém giết... đầy nguy hiểm và cơ hội trong giới võ lâm giang hồ.', NULL, '2020-09-08 17:14:12'),
(3, 'Lịch Sử', 'Lịch Sử', 'Lịch Sử', NULL, NULL),
(4, 'Ngôn Tình', 'Ngôn Tình', 'Truyện thuộc kiểu lãng mạn, kể về những sự kiện vui buồn trong tình yêu của nhân vật chính.', NULL, '2020-09-08 17:14:37'),
(5, 'Sắc Hiệp', 'Sắc Hiệp', 'Những bộ truyện này thường có yếu nhạy cảm, cân nhắc trước khi xem.', NULL, '2020-09-08 17:15:10'),
(6, 'Tiên Hiệp', 'Tiên Hiệp', 'Truyện tiên hiệp thường kể về quá trình tu luyện và khám phá thế giới tu sĩ thần tiên đầy bí ẩn của nhân vật chính.', NULL, '2020-09-08 17:13:28'),
(7, 'Dị Giới', 'Dị Giới', 'Trong truyện có xuất hiện những thế giới kỳ dị, khác với thế giới chúng ta đang sinh sống.', NULL, '2020-09-08 17:15:30'),
(8, 'Đô Thị', 'Đô Thị', 'Những truyện có nội dung chủ yếu diễn ra trong môi trường đô thị hiện đại.', NULL, '2020-09-08 17:11:36'),
