INSERT INTO `chapters` (`id`, `name`, `subname`, `content`, `view`, `story_id`, `user_id`, `created_at`, `updated_at`) VALUES
(24, 'Mang thai 7 tháng', 'Chương 1', '<p>Nhiệt độ n&oacute;ng rực như thể nham thạch n&oacute;ng chảy trong l&ograve;ng n&uacute;i lửa dường như muốn đốt ch&aacute;y c&ocirc;, thứ duy nhất c&oacute; thể giải tho&aacute;t cho c&ocirc; chỉ c&oacute; người đ&agrave;n &ocirc;ng trước mặt.......<br />\r\n<br />\r\nB&aacute;m chặt v&agrave;o l&agrave;n da l&agrave;nh lạnh như đ&aacute; cẩm thạch, bản năng muốn sống trong c&ocirc; cuối c&ugrave;ng cũng khiến c&ocirc; bu&ocirc;ng tha cho sự chống cự....<br />\r\n<br />\r\nĐau đớn pha lẫn kho&aacute;i cảm từng ch&uacute;t từng ch&uacute;t một bốc l&ecirc;n, như một những đợt ph&aacute;o hoa kh&ocirc;ng ngừng nổ tung trong đầu c&ocirc;, khiến cho c&ocirc; như một con thuyền c&ocirc; độc dấn th&acirc;n v&agrave;o biển lửa đang ch&aacute;y rừng rực.....<br />\r\n<br />\r\nCảm gi&aacute;c bấp b&ecirc;nh khi ch&igrave;m khi nổi, nhưng lại kh&oacute; m&agrave; kiềm chế được bản th&acirc;n m&igrave;nh ch&igrave;m đắm v&agrave;o đ&oacute;.<br />\r\n&quot;N&agrave;y, c&ocirc; tỉnh lại đi.... ở đ&acirc;y lạnh lắm, đừng ngủ ở đ&acirc;y rồi để cảm lạnh.&quot;<br />\r\n<br />\r\nVai bị đ&egrave; nặng khiến cho Ninh Tịch đột nhi&ecirc;n tỉnh lại, đ&ocirc;i mắt mơ m&agrave;ng bắt gặp &aacute;nh mắt &acirc;n cần của c&ocirc; y t&aacute;, nhất thời cảm thấy chột dạ đến mức mặt mũi đỏ hồng, xấu hổ đến mức kh&ocirc;ng biết chui v&agrave;o đ&acirc;u để trốn.<br />\r\n<br />\r\nĐ&aacute;ng chết, một thời gian d&agrave;i đ&atilde; tr&ocirc;i qua kể từ sau c&aacute;i đ&ecirc;m say rượu l&agrave;m loạn đ&oacute;, c&aacute;i đ&ecirc;m c&ocirc; l&agrave;m loạn với T&ocirc; Diễn thi thoảng vẫn xuất hiện trong giấc mơ của c&ocirc;.<br />\r\n<br />\r\nCũng may l&agrave; c&ocirc; say đến mức bất tỉnh nh&acirc;n sự chẳng nhớ g&igrave; về đ&ecirc;m đ&oacute; cả, bằng kh&ocirc;ng c&ocirc; thật kh&ocirc;ng biết n&ecirc;n đối mặt với anh Diễn nữa.<br />\r\n<br />\r\nY t&aacute; thấy c&ocirc; tỉnh t&aacute;o lại liền đưa cho c&ocirc; tờ giấy trong tay m&igrave;nh: &quot;C&ocirc; qu&ecirc;n kh&ocirc;ng cầm kết quả kh&aacute;m thai n&agrave;y, b&aacute;c sĩ Trương bảo c&ocirc; cuối tuần nhớ quay lại đấy.&quot;<br />\r\n<br />\r\nNinh Tịch cầm lấy kết quả kh&aacute;m thai, ngọt ng&agrave;o tươi cười, c&ocirc; cẩn thận cất n&oacute; v&agrave;o trong t&uacute;i x&aacute;ch.<br />\r\n<br />\r\nT&ocirc; Diễn ra nước ngo&agrave;i khảo s&aacute;t thực tế, h&ocirc;m nay sẽ trở về, Ninh Tịch nghĩ đến cuộc hẹn buổi tối m&agrave; trong l&ograve;ng kh&ocirc;ng khỏi cảm thấy hồi hộp.<br />\r\n<br />\r\nBởi v&igrave; nơi T&ocirc; Diễn đến kh&aacute; xa x&ocirc;i hẻo l&aacute;nh, cho n&ecirc;n khi đ&atilde; mang thai được 7 th&aacute;ng c&ocirc; mới li&ecirc;n lạc được với anh ta.<br />\r\n<br />\r\nNhớ đến vẻ mặt khiếp sợ l&uacute;c T&ocirc; Diễn biết c&ocirc; mang thai, Ninh Tịch c&oacute; ch&uacute;t căng thẳng.</p>\r\n\r\n<p>Chẳng lẽ l&agrave; v&igrave; khi mang thai c&ocirc; qu&aacute; nhạy cảm, n&ecirc;n mới c&oacute; cảm gi&aacute;c T&ocirc; Diễn chẳng hề vui vẻ như c&ocirc; đ&atilde; nghĩ.<br />\r\n<br />\r\nB&aacute;c sĩ an ủi c&ocirc;, khi đ&agrave;n &ocirc;ng c&oacute; đứa con đầu l&ograve;ng, t&acirc;m l&yacute; đều c&oacute; ch&uacute;t kh&ocirc;ng ổn định, chưa quen được, ai cũng như thế cả.<br />\r\n<br />\r\nNhưng.... chuyện kết h&ocirc;n đ&acirc;u thể để phụ nữ như c&ocirc; chủ động n&oacute;i ra được chứ...<br />\r\n<br />\r\nRa khỏi bệnh viện, mặt trời đ&atilde; l&ecirc;n cao, &aacute;nh nắng ch&oacute;i chang tỏa khắp mọi nơi.<br />\r\n<br />\r\nNinh Tịch gắng sức đỡ lấy eo, c&ocirc; đang định gọi taxi đột nhi&ecirc;n một chiếc xe đỏ rực x&ocirc;ng thẳng về ph&iacute;a c&ocirc;. C&ocirc; th&oacute;t tim, hoảng hốt vội lui về sau mấy bước liền.<br />\r\n<br />\r\nTiếng phanh xe vang l&ecirc;n &quot;k&eacute;t&quot; một tiếng, chiếc xe s&aacute;t lướt s&agrave;n sạn sạt qua người c&ocirc;, sau đ&oacute; mới dừng lại.<br />\r\n<br />\r\nNinh Tịch sợ th&oacute;t tim, kh&oacute; khăn lắm mới đứng vững, ngẩng l&ecirc;n liền nh&igrave;n thấy Ninh Tuyết Lạc mặc bộ v&aacute;y đỏ &ocirc;m s&aacute;t người đầy quyến rũ, m&aacute;i t&oacute;c xoăn gợn s&oacute;ng bước xuống từ tr&ecirc;n xe.&ldquo;Ninh Tuyết Lạc, m&agrave;y đi&ecirc;n rồi &agrave;?&rdquo;<br />\r\n<br />\r\nNinh Tuyết Lạc nh&igrave;n c&ocirc;, cười đến ẩn &yacute;, c&ocirc; ta khoanh tay, sải những bước ch&acirc;n catwalk đầy uyển chuyển, c&ocirc; ta đứng trước mặt Ninh Tịch, ỷ v&agrave;o m&igrave;nh đang đi gi&agrave;y cao g&oacute;t, ki&ecirc;u ngạo nh&igrave;n xuống v&ugrave;ng bụng to ra v&igrave; mang thai của c&ocirc;: &ldquo;Sao? Sợ tao đ&acirc;m chết đứa con hoang trong bụng m&agrave;y &agrave;?&rdquo;<br />\r\n<br />\r\nNinh Tịch v&ocirc; thức &ocirc;m lấy bụng m&igrave;nh, l&ugrave;i về ph&iacute;a sau một bước, nh&igrave;n c&ocirc; ta với &aacute;nh mắt đầy cảnh gi&aacute;c: &ldquo;Ninh Tuyết Lạc, m&agrave;y đừng c&oacute; m&agrave; qu&aacute; đ&aacute;ng!&rdquo;<br />\r\n<br />\r\nTuy biết rằng từ trước đến nay Ninh Tuyết Lạc vẫn lu&ocirc;n bất h&ograve;a với c&ocirc;, nhưng Ninh Tịch kh&ocirc;ng ngờ c&ocirc; ta lại c&oacute; thể độc mồm độc miệng đến như vậy.<br />\r\n<br />\r\n&ldquo;Tao qu&aacute; đ&aacute;ng? M&agrave;y mới l&agrave; kẻ qu&aacute; đ&aacute;ng th&igrave; c&oacute;! Uống say bất tỉnh nh&acirc;n sự rồi b&ograve; l&ecirc;n giường với thằng n&agrave;o, chửa hoang rồi c&ograve;n muốn T&ocirc; Diễn chịu tr&aacute;ch nhiệm hay sao, ha ha&hellip; Ninh Tịch, m&agrave;y đ&uacute;ng l&agrave; kh&ocirc;ng biết xấu hổ!&rdquo;<br />\r\n<br />\r\nNinh Tịch sững sờ: &ldquo;M&agrave;y đang n&oacute;i vớ n&oacute;i vẩn c&aacute;i g&igrave; thế?&rdquo;<br />\r\n<br />\r\n&ldquo;&Ocirc;, thế m&agrave;y tin rằng người đ&atilde; l&ecirc;n giường với m&igrave;nh tối h&ocirc;m đ&oacute; l&agrave; T&ocirc; Diễn thật đấy &agrave;?&rdquo; Ninh Tuyết Lạc cười ngả nghi&ecirc;ng: &ldquo;Lu&ocirc;n mồm n&oacute;i m&igrave;nh lớn l&ecirc;n c&ugrave;ng T&ocirc; Diễn, l&agrave; thanh mai tr&uacute;c m&atilde;, ngay cả đến v&oacute;c d&aacute;ng th&acirc;n thể của anh ấy như thế n&agrave;o m&agrave; cũng kh&ocirc;ng biết &agrave;?&rdquo;<br />\r\n<br />\r\nVẻ mặt của Ninh Tịch c&agrave;ng l&uacute;c c&agrave;ng trắng, c&ocirc; sắp đứng kh&ocirc;ng nổi nữa, cả người như đang rơi v&agrave;o hầm băng.<br />\r\n<br />\r\nKh&ocirc;ng sai, người đ&agrave;n &ocirc;ng tối h&ocirc;m đ&oacute;&hellip;..<br />\r\n<br />\r\nKhi đ&oacute; c&ocirc; chỉ nghĩ l&agrave;&hellip;. sau khi T&ocirc; Diễn trưởng th&agrave;nh, c&oacute; lẽ cao to hơn so với tưởng tượng của m&igrave;nh.<br />\r\n<br />\r\nB&acirc;y giờ bị Ninh Tuyết Lạc &aacute;c &yacute; nhắc nhở, c&ocirc; mới đột nhi&ecirc;n nghĩ ra, ngoại trừ v&oacute;c d&aacute;ng, người đ&agrave;n &ocirc;ng đ&ecirc;m h&ocirc;m đ&oacute; h&igrave;nh như&hellip;.. đ&uacute;ng l&agrave; c&oacute; qu&aacute; nhiều điểm kh&aacute;c biệt ho&agrave;n to&agrave;n với T&ocirc; Diễn.</p>', NULL, 1, 1, '2020-09-07 18:10:12', NULL),
(26, 'Đêm', 'Chương 1', '<p>&quot; C&ocirc; nương, đ&atilde; đến giữa giờ Tuất rồi. &quot; Tỳ nữ A Man đi v&agrave;o trong ph&ograve;ng, nhấc m&agrave;n lụa m&agrave;u xanh mưa trời treo l&ecirc;n m&oacute;c gi&aacute; tr&ecirc;n giường, nhẹ giọng gọi thiếu nữ nằm nghi&ecirc;ng tr&ecirc;n giường.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&uacute;c n&agrave;y đ&atilde; l&agrave; đầu hạ, b&ecirc;n ngo&agrave;i trời vừa mới ho&agrave;n to&agrave;n tối xuống, b&oacute;ng đ&ecirc;m mờ ảo bao phủ khu&ocirc;n mặt của thiếu nữ, mượn &aacute;nh nến tr&ecirc;n b&agrave;n, lờ mờ có th&ecirc;̉ thấy r&otilde; diện mạo của thiếu nữ trong trướng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thiếu nữ l&ocirc;ng m&agrave;y như n&uacute;i xa, mũi ngọc tinh xảo m&ocirc;i anh đ&agrave;o, m&aacute; đ&agrave;o da tuyết, đ&uacute;ng l&agrave; một tiểu mỹ nh&acirc;n đẹp xuất sắc.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thiếu nữ ch&iacute;nh l&agrave; c&ocirc; nương đứng h&agrave;ng thứ tư của Đ&ocirc;ng B&igrave;nh B&aacute; phủ Khương gia, t&ecirc;n chỉ một chữ Tự .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>A Man thấy d&aacute;ng vẻ của Khương Tự, trong l&ograve;ng liền d&acirc;ng l&ecirc;n một cơn lửa giận, bất b&igrave;nh thay cho c&ocirc; nương nh&agrave; m&igrave;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Vị Tam c&ocirc;ng tử của An quốc c&ocirc;ng phủ hẳn l&agrave; mắt bị m&ugrave; rồi, bằng v&agrave;o diện mạo của c&ocirc; nương tiến cung l&agrave;m nương nương cũng được, ấy thế m&agrave; hắn lại kh&ocirc;ng thấy th&iacute;ch th&uacute; cửa h&ocirc;n sự n&agrave;y, chẳng lẽ cảm thấy c&ocirc; nương kh&ocirc;ng xứng với hắn ư?</p>\r\n\r\n<p>Lửa giận của A Man bắt ngu&ocirc;̀n từ một buổi hội thơ ng&agrave;y xu&acirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hội thơ đ&oacute; l&agrave; do một &iacute;t danh m&ocirc;n c&ocirc;ng tử trong kinh tổ chức, đơn giản ch&iacute;nh l&agrave; một &iacute;t người trẻ tuổi tụ tập với nhau uống rượu ng&acirc;m thơ t&igrave;m niềm vui, đến khi rượu đ&atilde; hơi ng&agrave; say, liền c&oacute; người bắt đầu tr&ecirc;u chọc Tam c&ocirc;ng tử Qu&yacute; S&ugrave;ng Dịch của An Quốc C&ocirc;ng phủ, trong ng&ocirc;n ngữ c&oacute; phần ghen tị hắn sắp được th&agrave;nh h&ocirc;n với tiểu mỹ nh&acirc;n nổi danh trong kinh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ai ngờ Qu&yacute; S&ugrave;ng Dịch mang theo men say tự giễu cười một tiếng, n&oacute;i c&acirc;u: &quot; Đẹp th&igrave; thế n&agrave;o , chẳng qua cũng chỉ l&agrave; một bộ t&uacute;i da th&ocirc;i, nữ tử b&acirc;y giờ lấy phẩm t&iacute;nh &ocirc;n nhu lương thiện l&agrave;m trọng.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Vốn l&agrave; lời say của người trẻ tuổi, nghe một ch&uacute;t cũng liền qua đi, tỉnh rượu rồi tự nhi&ecirc;n như gi&oacute; thoảng kh&ocirc;ng dấu vết, ai ngờ lời n&agrave;y kh&ocirc;ng biết l&agrave;m sao lại truyền ra ngo&agrave;i, Tứ c&ocirc; nương của Khương gia lập tức trở th&agrave;nh tr&ograve; cười của mọi người trong l&uacute;c uống tr&agrave; ẩm rượu.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đ&ocirc;ng B&igrave;nh B&aacute; phủ vốn căn cơ n&ocirc;ng cạn, tước vị chỉ c&oacute; thể kế tục ba đời, đến thế hệ phụ th&acirc;n của Khương Tự l&agrave; Đ&ocirc;ng B&igrave;nh B&aacute; đ&atilde; l&agrave; ba đời, v&igrave; lẽ đ&oacute; m&agrave; huynh trưởng của Khương Tự ngay cả thế tử cũng chưa được xin phong.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>N&oacute;i c&aacute;ch kh&aacute;c, sau trăm năm nữa, Đ&ocirc;ng B&igrave;nh B&aacute; phủ sẽ từ trong v&ograve;ng luẩn quẩn hu&acirc;n qu&yacute; lui ra ngo&agrave;i, trở th&agrave;nh người b&igrave;nh thường.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&iacute;nh l&agrave; c&ocirc; nương một nh&agrave; như vậy, lại đ&atilde; đ&iacute;nh h&ocirc;n với An Quốc C&ocirc;ng phủ, trước kh&ocirc;ng n&oacute;i đến cơ duy&ecirc;n trong đ&oacute;, th&igrave; điều n&agrave;y cũng đủ để khiến rất nhiều người nh&igrave;n kh&ocirc;ng vừa mắt với Khương Tự tr&egrave;o cao l&ecirc;n An Quốc C&ocirc;ng phủ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tam c&ocirc;ng tử Qu&yacute; S&ugrave;ng Dịch của An Quốc C&ocirc;ng phủ n&oacute;i nữ tử mỹ mạo kh&ocirc;ng quan trọng, hắn coi trọng t&iacute;nh t&igrave;nh hơn, c&aacute;i n&agrave;y ngụ &yacute;, c&ograve;n kh&ocirc;ng phải ch&iacute;nh l&agrave; gh&eacute;t bỏ Khương Tứ c&ocirc; nương t&iacute;nh t&igrave;nh kh&ocirc;ng tốt sao?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>V&ocirc; luận Qu&yacute; S&ugrave;ng Dịch n&oacute;i lời n&agrave;y l&agrave; hữu t&acirc;m hay l&agrave; v&ocirc; t&igrave;nh, th&igrave; lời n&agrave;y vừa truyền ra lập tức khiến Khương Tự mất hết mặt mũi, lại ra ngo&agrave;i tham gia tụ hội của nh&oacute;m qu&yacute; nữ, liền nghe được một bụng lời đ&agrave;m tiếu.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khương Tự l&agrave; một người n&oacute;ng t&iacute;nh, trở về liền đổ bệnh, vừa bệnh ch&iacute;nh l&agrave; nửa th&aacute;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khương Tự nằm ở tr&ecirc;n giường nhắm mắt dưỡng thần bỗng nhi&ecirc;n mở mắt ra.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đ&ocirc;i mắt của n&agrave;ng đường cong cực đẹp, đến đu&ocirc;i mắt hơi nhếch l&ecirc;n, phác hoạ ra n&eacute;t phong lưu diễm lệ kh&oacute; m&agrave; diễn tả bằng lời.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&uacute;c n&agrave;y hai con ngươi cực đẹp đối diện với A Man, lộ ra &yacute; cười mờ nhạt: &quot; B&agrave;y ra d&aacute;ng vẻ muốn ăn thịt người thế l&agrave;m g&igrave;?&quot;</p>\r\n\r\n<p>&quot; Nghĩ đến người n&agrave;o đ&oacute; c&oacute; mắt kh&ocirc;ng tr&ograve;ng, n&ocirc; tỳ liền thấy tức thay c&ocirc; nương.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&Yacute; cười nơi đ&aacute;y mắt của Khương Tự nhanh ch&oacute;ng mất đi, độ cong kh&oacute;e miệng lại s&acirc;u hơn, thản nhi&ecirc;n n&oacute;i: &quot; Người đ&oacute; c&ograve;n chưa từng gặp ta, kh&ocirc;ng thể n&oacute;i l&agrave; c&oacute; mắt kh&ocirc;ng tr&ograve;ng.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot; C&ocirc; nương, người c&ograve;n n&oacute;i đỡ cho hắn nữa! &quot; Nh&igrave;n c&ocirc; nương chỉ mới nửa th&aacute;ng ngắn ngủi đ&atilde; gầy đi một v&ograve;ng, A Man liền thấy đau l&ograve;ng c&ugrave;ng kh&ocirc;ng phục.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nửa th&aacute;ng trước c&ocirc; nương đi Ph&oacute; Vĩnh Xương B&aacute; phủ ngắm hoa yến trở về liền kh&oacute;c lớn một trận, ngay cả vật trang tr&iacute; Ngọc Tỳ Hưu y&ecirc;u th&iacute;ch nhất cũng đập vỡ, nhắc đến Tam c&ocirc;ng tử của An Quốc C&ocirc;ng phủ c&agrave;ng hận đến nghiến răng nghiến lợi, sao b&acirc;y giờ lại thay đổi rồi?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot; Kh&ocirc;ng phải n&oacute;i đỡ cho hắn, một c&acirc;u n&oacute;i say th&ocirc;i m&agrave;&quot;. Khương Tự chuyển mắt, nh&igrave;n về ph&iacute;a một tỳ nữ kh&aacute;c đứng ở cạnh b&igrave;nh phong A Xảo, ph&acirc;n ph&oacute; n&oacute;i, &quot; A Xảo, đi lấy hai bộ y phục mấy ng&agrave;y trước k&ecirc;u ngươi l&agrave;m ra đ&acirc;y đi.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kh&ocirc;ng bao l&acirc;u A Xảo n&acirc;ng đến hai bộ y phục, trong đ&oacute; một bộ cho A Man, một bộ kh&aacute;c th&igrave; hầu hạ Khương Tự mặc v&agrave;o.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>A Man vừa mặc bộ y phục l&ecirc;n người vừa căm giận n&oacute;i: &quot; Một c&acirc;u lời say m&agrave; l&agrave;m hại c&ocirc; nương bị người ta ch&ecirc; cười đ&acirc;́y.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đ&aacute;y mắt Khương Tự &yacute; lạnh s&acirc;u hơn, dứt kho&aacute;t nhắm lại con ngươi, n&oacute;i khẽ: &quot; Đ&acirc;y được t&iacute;nh l&agrave; g&igrave;?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>N&agrave;ng bất hạnh cả đời, ch&iacute;nh l&agrave; bắt đầu từ cuộc h&ocirc;n nh&acirc;n m&ocirc;n kh&ocirc;ng đăng hộ kh&ocirc;ng đối n&agrave;y.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhớ khi đ&oacute;, tuổi trẻ v&ocirc; tri, n&agrave;ng l&agrave; cỡ n&agrave;o đắc &yacute; v&igrave; c&oacute; thể đ&iacute;nh h&ocirc;n với c&ocirc;ng tử của An Quốc C&ocirc;ng phủ, ai ngờ vị Tam c&ocirc;ng tử Qu&yacute; S&ugrave;ng Dịch đ&oacute; đ&atilde; sớm c&oacute; người trong l&ograve;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Người trong l&ograve;ng Qu&yacute; S&ugrave;ng Dịch l&agrave; một vị d&acirc;n nữ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>N&agrave;ng gả qua rồi sau đ&oacute; mới dần dần biết được, vị d&acirc;n nữ đ&oacute; cơ duy&ecirc;n xảo hợp cứu được Qu&yacute; S&ugrave;ng Dịch đi ra ngo&agrave;i du ngoạn gặp nạn, Qu&yacute; S&ugrave;ng Dịch ở trong nh&agrave; nữ tử đ&oacute; dưỡng thương mấy ng&agrave;y mới được Quốc C&ocirc;ng phủ t&igrave;m thấy, hai người đ&atilde; nảy sinh t&igrave;nh cảm, sau đ&oacute; vẫn lu&ocirc;n vụng trộm qua lại.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; trong l&uacute;c n&agrave;ng c&ograve;n tr&agrave;n ngập kh&aacute;t khao c&ugrave;ng đắc &yacute; với đoạn h&ocirc;n nh&acirc;n n&agrave;y, th&igrave; Qu&yacute; S&ugrave;ng Dịch v&igrave; c&oacute; thể c&ugrave;ng người trong l&ograve;ng ở b&ecirc;n nhau m&agrave; đ&atilde; rất nhiều lần phản kh&aacute;ng trưởng bối trong nh&agrave;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>H&ocirc;n sự đ&atilde; gần ngay trước mắt, An Quốc C&ocirc;ng phủ đương nhi&ecirc;n kh&ocirc;ng cho ph&eacute;p Qu&yacute; S&ugrave;ng Dịch nh&aacute;o loạn, huống chi hắn muốn cưới ch&iacute;nh l&agrave; nữ tử b&igrave;nh d&acirc;n ngay cả Khương gia cũng kh&ocirc;ng bằng, Qu&yacute; S&ugrave;ng Dịch phản kh&aacute;ng c&ugrave;ng bất m&atilde;n tự nhi&ecirc;n kh&ocirc;ng hề c&oacute; ch&uacute;t t&aacute;c dụng n&agrave;o.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khương Tự nghĩ đến c&acirc;u n&oacute;i l&uacute;c say rượu của Qu&yacute; S&ugrave;ng Dịch, liền cảm thấy khi đ&oacute; m&igrave;nh thật ngu xuẩn, tức giận qua đi lại nhịn kh&ocirc;ng được thay hắn t&igrave;m l&yacute; do, cho l&agrave; hắn kh&ocirc;ng để &yacute; thế tục, kh&ocirc;ng phải như những nam tử dung tục chỉ để &yacute; dung mạo của nữ tử, n&oacute;i c&acirc;u n&oacute;i kia chỉ l&agrave; việc n&agrave;o ra việc đ&oacute; th&ocirc;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qua việc n&agrave;o ra việc đ&oacute; của hắn, ngay tại đ&ecirc;m nay, tối mười lăm th&aacute;ng tư năm mười t&aacute;m Cảnh Minh, vị qu&yacute; c&ocirc;ng tử danh m&ocirc;n kh&ocirc;ng để &yacute; thế tục n&agrave;y lại c&ugrave;ng người trong l&ograve;ng c&ugrave;ng nhau chạy đến ven hồ Mạc Ưu, nhảy hồ tự vẫn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Sau đ&oacute; Qu&yacute; S&ugrave;ng Dịch được cứu l&ecirc;n, người trong l&ograve;ng của hắn lại hương ti&ecirc;u ngọc vẫn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>V&igrave; che lấp chuyện n&agrave;y, h&ocirc;n sự m&agrave; bọn họ vốn định v&agrave;o đầu m&ugrave;a đ&ocirc;ng tự nhi&ecirc;n lại tổ chức trước thời hạn mấy th&aacute;ng, m&agrave; từ sau khoảng thời gian n&agrave;ng l&ograve;ng tr&agrave;n đầy vui vẻ gả qua cho đến khi Qu&yacute; S&ugrave;ng Dịch ngo&agrave;i &yacute; muốn bỏ m&igrave;nh, th&igrave; trong gần một năm thời gian đ&oacute;, người nam nh&acirc;n như &aacute;nh trăng s&aacute;ng n&agrave;y đều chưa từng chạm v&agrave;o n&agrave;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Rồi sau đ&oacute;, c&agrave;ng nhiều biến cố xảy ra hơn, cho đến khi n&agrave;ng chết thảm rồi lại mở mắt ra, về lại năm mười lăm tuổi n&agrave;y.</p>\r\n\r\n<p>C&oacute; thể n&oacute;i, tất cả mọi bất hạnh của n&agrave;ng đều bắt đầu từ l&uacute;c gả cho Qu&yacute; S&ugrave;ng Dịch, m&agrave; b&acirc;y giờ có th&ecirc;̉ l&agrave;m lại lần nữa, việc cấp b&aacute;ch của n&agrave;ng ch&iacute;nh l&agrave; giải quyết cọc h&ocirc;n sự n&agrave;y, từ đ&acirc;y ph&acirc;n r&otilde; giới hạn với qu&yacute; Tam c&ocirc;ng tử kh&ocirc;ng lưu thế tục, với An Quốc C&ocirc;ng phủ cao kh&ocirc;ng thể chạm, cả đời kh&ocirc;ng bao giờ qua lại với nhau nữa!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trong khoảnh khắc Khương Tự đ&atilde; mặc xong y phục rồi ra ngo&agrave;i, gật đầu với A Man: &quot; A Man, đi th&ocirc;i.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>A Man đem tay nải đặt ở tr&ecirc;n ghế x&aacute;ch l&ecirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>A Xảo do dự một ch&uacute;t, ngăn lại Khương Tự rồi chần chừ n&oacute;i: &quot; C&ocirc; nương, đ&atilde; trễ thế n&agrave;y, c&ocirc; nương thật sự muốn đi ra ngo&agrave;i sao? Chỗ nhị m&ocirc;n đ&atilde; kh&oacute;a rồi--&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot; Kh&ocirc;ng sao, những c&aacute;i đ&oacute; đều chuẩn bị xong cả rồi. A Xảo, ngươi tr&ocirc;ng coi s&acirc;n viện cho tốt l&agrave; được.&quot; Khương Tự n&eacute;t mặt ki&ecirc;n quyết.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nếu như c&oacute; thể, n&agrave;ng đương nhi&ecirc;n kh&ocirc;ng muốn đ&ecirc;m khuya đi ra ngo&agrave;i mạo hiểm, nhưng m&agrave; hiện giờ trong phủ ngoại trừ hai thiếp th&acirc;n nha ho&agrave;n, th&igrave; n&agrave;ng cũng kh&ocirc;ng t&igrave;m thấy người n&agrave;o c&oacute; thể tin được để hỗ trợ cả.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>A Xảo thấy vậy đ&agrave;nh phải gật đầu thật mạnh, n&oacute;i một tiếng &quot; c&ocirc; nương y&ecirc;n t&acirc;m&quot;, rồi tr&aacute;nh đường.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khương Tự mang theo A Man lặng lẽ ra khỏi Hải Đường Cư chỗ ở của n&agrave;ng, mượn trăm hoa tươi tốt che lấp xuy&ecirc;n qua vườn hoa c&ugrave;ng tr&ugrave;ng tr&ugrave;ng cổng v&ograve;m, đi v&agrave;o nhị m&ocirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot; C&ocirc; nương -- &quot; A Man nh&igrave;n c&aacute;nh cửa đ&oacute;ng chặt, thấp giọng gọi một tiếng.</p>', NULL, 8, 1, '2020-09-07 18:13:37', NULL),
(28, 'Sống lại, trước khi ly hôn!', 'Chương 1', '<p>&quot;Bịch!&quot;<br />\r\n<br />\r\nTrong ph&ograve;ng giam lạnh lẽo, Qu&yacute; No&atilde;n kh&ocirc;ng đứng dậy, ngẩng đầu nh&igrave;n t&ecirc;n quản gi&aacute;o đứng ở cửa ph&ograve;ng n&eacute;m phần cơm nguội ngắt xuống đất.<br />\r\n<br />\r\n&quot;Giả vờ thanh cao g&igrave; chứ? C&oacute;ăn hay kh&ocirc;ng!&quot;<br />\r\n<br />\r\nT&ecirc;n quản gi&aacute;o th&ograve; ch&acirc;n qua cửa ph&ograve;ng, giẫm mạnh v&agrave;i c&aacute;i xuống phần cơm dưới đất.<br />\r\n<br />\r\nHắn nghe n&oacute;i ả Qu&yacute; No&atilde;n n&agrave;y cố&yacute; s&aacute;t hại V&acirc;n tiểu thư, n&ecirc;n nh&agrave; họ V&acirc;n dặn kỹ rằng phải h&agrave;nh hạả thẳng tay.<br />\r\n<br />\r\nQu&yacute; No&atilde;n ngồi trong g&oacute;c, mơ hồ nh&igrave;n phần cơm bị bẩn đến kh&ocirc;ng nuốt nổi.<br />\r\n<br />\r\nBỗng, tiếng tivi truyền đến từ ph&ograve;ng gi&aacute;m thị.<br />\r\n<br />\r\n&quot;Mặc Cảnh Th&acirc;m, Tổng gi&aacute;m đốc tập đo&agrave;n Shine vừa về nước h&ocirc;m nay, hiện đ&atilde;đến Hải Th&agrave;nh.&quot;<br />\r\n<br />\r\n&quot;Tập đo&agrave;n Shine l&agrave; c&ocirc;ng ty t&agrave;i ch&iacute;nh lớn nhất ch&acirc;u &Aacute;. Bảy năm trước, Mặc Cảnh Th&acirc;m ch&iacute;nh thức tiếp quản tập đo&agrave;n. Từđ&oacute;đến nay, địa vị tr&ugrave;m thương mại vẫn kh&ocirc;ng hề lay chuyển&hellip;&quot;<br />\r\n<br />\r\nKhu&ocirc;n mặt kh&ocirc;ng biểu cảm của Qu&yacute; No&atilde;n tho&aacute;ng cứng đờ. C&ocirc; bỗng lao đến b&ecirc;n cửa, cố gắng vươn cổ nh&igrave;n ra ngo&agrave;i.</p>\r\n\r\n<p>Quản gi&aacute;o quay đầu lại, thấy c&ocirc; chật vật hoảng hốt ngồi xổm b&ecirc;n trong, tay siết chặt song sắt lạnh băng, đ&ocirc;i mắt hoen đỏ nh&igrave;n chằm chằm v&agrave;o tivi.<br />\r\n<br />\r\n&quot;Nh&igrave;n c&aacute;i g&igrave;? C&oacute; biết người trong bản tin l&agrave; ai kh&ocirc;ng? Loại đ&agrave;n b&agrave; như c&ocirc; l&agrave;m g&igrave; c&oacute; tư c&aacute;ch x&aacute;ch gi&agrave;y cho người ta!&quot;<br />\r\n<br />\r\nQu&yacute; No&atilde;n c&uacute;i đầu, đ&aacute;y mắt ẩn hiện &yacute; cười chua ch&aacute;t.<br />\r\n<br />\r\nPhải rồi, c&ocirc; của h&ocirc;m nay, ngay cả tư c&aacute;ch x&aacute;ch gi&agrave;y cho Mặc Cảnh Th&acirc;m cũng kh&ocirc;ng c&oacute;.<br />\r\n<br />\r\nCho d&ugrave; mười năm trước, người đ&agrave;n &ocirc;ng trở th&agrave;nh Tổng gi&aacute;m đốc của tập đo&agrave;n Shine n&agrave;y đ&atilde; từng l&agrave; chồng c&ocirc;!<br />\r\n<br />\r\nL&agrave; c&ocirc; tự cao tựđại, ki&ecirc;u căng t&ugrave;y hứng, ki&ecirc;n quyết muốn ly h&ocirc;n, cuối c&ugrave;ng cũng th&agrave;nh c&ocirc;ng xua đuổi người đ&agrave;n &ocirc;ng vẫn lu&ocirc;n n&acirc;ng niu chiều chuộng c&ocirc;.<br />\r\n<br />\r\nSuốt mười năm qua, anh chưa từng lộ diện.<br />\r\n<br />\r\nNhưng tivi vừa đưa tin g&igrave;?<br />\r\n<br />\r\nMặc Cảnh Th&acirc;m đ&atilde; trở lại? Anh đ&atilde; từ Mỹ trở về rồi sao?<br />\r\n<br />\r\nTivi vang l&ecirc;n tiếng ph&oacute;ng vi&ecirc;n tranh nhau đuổi theo phỏng vấn: &quot;Mặc tổng! Nghe n&oacute;i hai th&aacute;ng trước ng&agrave;i đ&atilde; kết h&ocirc;n ở Mỹ. Lần n&agrave;y về nước, ng&agrave;i c&oacute; dẫn vợ mới cưới về c&ugrave;ng kh&ocirc;ng?&quot;<br />\r\n<br />\r\nQu&yacute; No&atilde;n run l&ecirc;n.<br />\r\n<br />\r\nAnh&hellip;đ&atilde; kết h&ocirc;n rồi ư?<br />\r\n<br />\r\nCũng phải, chưa n&oacute;i tới th&acirc;n phận hiện tại, mười năm trước, anh đ&atilde; l&agrave; nh&acirc;n vật nổi tiếng l&agrave;m mưa l&agrave;m gi&oacute;ở Hải Th&agrave;nh. Anh tuấn t&uacute; như thần, tr&ecirc;n đời c&oacute; một kh&ocirc;ng hai, biết bao thi&ecirc;n kim h&agrave;o m&ocirc;n xếp h&agrave;ng muốn kết h&ocirc;n với anh.<br />\r\n<br />\r\nGiờđ&acirc;y, chắc hẳn anh đang rất hạnh ph&uacute;c.<br />\r\n<br />\r\n&quot;Mặc tổng, ng&agrave;i rất &iacute;t xuất hiện trước truyền th&ocirc;ng. Hiếm khi c&oacute; dịp về nước, xin ng&agrave;i n&oacute;i v&agrave;i lời về tập đo&agrave;n Shine hiện nay&hellip;&quot;<br />\r\n<br />\r\n&quot;Vị tiểu thưđi theo sau c&oacute; phải l&agrave; b&agrave; Mặc kh&ocirc;ng? B&agrave; Mặc quả l&agrave; rất xinh đẹp&hellip;&quot;<br />\r\n<br />\r\nNgay sau đ&oacute;, một giọng nữ ngọt ng&agrave;o vang l&ecirc;n: &quot;Xin lỗi, &ocirc;ng Mặc kh&ocirc;ng nhận phỏng vấn của truyền th&ocirc;ng, xin mọi người nhường đường cho.&quot;<br />\r\n<br />\r\nNghe thấy giọng n&oacute;i ấy, Qu&yacute; No&atilde;n lập tức như bị s&eacute;t đ&aacute;nh.<br />\r\n<br />\r\nGiọng n&oacute;i đ&oacute;&hellip; l&agrave; của em g&aacute;i c&ocirc;, Qu&yacute; Mộng Nhi&ecirc;n!</p>\r\n\r\n<p>L&agrave; Qu&yacute; Mộng Nhi&ecirc;n đ&atilde; mất t&iacute;ch trước khi nh&agrave; họ Qu&yacute; ph&aacute; sản!<br />\r\n<br />\r\nVụ ly h&ocirc;n ầm ĩ trước kia l&agrave; do c&ocirc; em g&aacute;i tử tế n&agrave;y của c&ocirc; giật d&acirc;y, x&uacute;i c&ocirc; l&agrave;m đủ tr&ograve;đi&ecirc;n rồ, kể cả cắt cổ tay tự s&aacute;t, khiến khoảng c&aacute;ch giữa c&ocirc; v&agrave; Mặc Cảnh Th&acirc;m c&agrave;ng l&uacute;c c&agrave;ng xa, cho đến khi ly h&ocirc;n.<br />\r\n<br />\r\nQu&yacute; No&atilde;n bỗng bật cười, nỗi đau thấu tận t&acirc;m can.<br />\r\n<br />\r\n&quot;Nếu Mặc tổng kh&ocirc;ng nhận phỏng vấn, vậy th&igrave; b&agrave; Mặc c&oacute; thể ph&aacute;t biểu v&agrave;i c&acirc;u được kh&ocirc;ng?&quot;<br />\r\n<br />\r\nPh&oacute;ng vi&ecirc;n đặt c&acirc;u hỏi dồn dập, Qu&yacute; Mộng Nhi&ecirc;n lu&ocirc;n đi sau lưng Mặc Cảnh Th&acirc;m mặt m&agrave;y tươi r&oacute;i.<br />\r\n<br />\r\nNhưng đ&uacute;ng l&uacute;c n&agrave;y, Mặc Cảnh Th&acirc;m l&ecirc;n tiếng, giọng n&oacute;i b&igrave;nh thản m&agrave; lạnh lẽo, kh&ocirc;ng ch&uacute;t hơi ấm: &quot;C&ocirc; ta kh&ocirc;ng phải b&agrave; Mặc.&quot;<br />\r\n<br />\r\nGương mặt vui vẻ của Qu&yacute; Mộng Nhi&ecirc;n lập tức cứng đờ. C&ocirc; ta nhếch m&ocirc;i che giấu sự xấu hổ.<br />\r\n<br />\r\nQuả thật kh&ocirc;ng phải c&ocirc; ta&hellip;<br />\r\n<br />\r\nĐ&aacute;m ph&oacute;ng vi&ecirc;n xung quanh lập tức im bặt, m&atilde;i cho đến khi c&oacute; một giọng n&oacute;i khẽ vang l&ecirc;n: &quot;Nghe n&oacute;i sau khi ly h&ocirc;n v&agrave;o mười năm trước, cho đến nay Mặc tổng vẫn chưa t&aacute;i gi&aacute;&hellip;&quot;<br />\r\n<br />\r\nQu&yacute; No&atilde;n kh&ocirc;ng c&ograve;n nghe r&otilde;&acirc;m thanh tr&ecirc;n tivi nữa. Bụng c&ocirc;đột nhi&ecirc;n quặn đau, c&ocirc; chật vật co người lại, ng&atilde; nho&agrave;i xuống đất. M&aacute;u nhưứ tr&agrave;n trong lồng ngực, tr&agrave;o qua cổ họng.<br />\r\n<br />\r\nĐ&aacute;m người kia đ&atilde; bỏ thuốc độc m&atilde;n t&iacute;nh v&agrave;o cơm t&ugrave; của c&ocirc; suốt ba th&aacute;ng qua.<br />\r\n<br />\r\nChẳng lẽ, t&iacute;nh mạng c&ocirc;đ&atilde;đi tới bước đường c&ugrave;ng rồi sao?<br />\r\n<br />\r\n***<br />\r\n<br />\r\nN&oacute;ng&hellip;<br />\r\n<br />\r\nN&oacute;ng qu&aacute;&hellip;<br />\r\n<br />\r\nQu&yacute; No&atilde;n dần dần tỉnh lại, nửa người dưới truyền đến cơn đau x&eacute; da x&eacute; thịt như bịđ&acirc;m th&ocirc; bạo!<br />\r\n<br />\r\n&quot;Ưm&hellip;&quot;<br />\r\nTo&agrave;n th&acirc;n đau buốt cứng đờ, c&ocirc; r&ecirc;n l&ecirc;n, nhưng lập tức lại bị người đ&agrave;n &ocirc;ng tr&ecirc;n người d&ugrave;ng nụ h&ocirc;n phủ k&iacute;n miệng.<br />\r\n<br />\r\nC&ocirc; tỉnh lại lần nữa, mở mắt, thứđầu ti&ecirc;n tr&ocirc;ng thấy l&agrave; chiếc đ&egrave;n thủy tinh xa hoa đắt đỏ tr&ecirc;n đầu.<br />\r\n<br />\r\nB&ecirc;n dưới l&agrave; tấm chăn tơ tằm sang trọng mịn m&agrave;ng, c&ocirc; nhưđang nằm tr&ecirc;n một đ&aacute;m m&acirc;y mềm mại, v&ocirc; c&ugrave;ng thoải m&aacute;i.<br />\r\n<br />\r\nĐ&acirc;y l&agrave;đ&acirc;u?<br />\r\n<br />\r\nC&ocirc; ngồi dậy, ngạc nhi&ecirc;n nh&igrave;n xung quanh.<br />\r\n<br />\r\nĐ&acirc;y r&otilde; r&agrave;ng l&agrave; Ngự Vi&ecirc;n ở Hải Th&agrave;nh, ph&ograve;ng cưới trước kia của c&ocirc; v&agrave; Mặc Cảnh Th&acirc;m!<br />\r\n<br />\r\nSao c&ocirc; vềđ&acirc;y được?<br />\r\n<br />\r\nChẳng phải v&igrave; bỏ hoang qu&aacute; l&acirc;u n&ecirc;n Ngự Vi&ecirc;n đ&atilde; bị ni&ecirc;m phong từ mấy năm trước rồi sao?<br />\r\n<br />\r\nNửa người dưới đau nhức nhắc nhở c&ocirc; rằng đ&acirc;y kh&ocirc;ng phải l&agrave; mơ. C&ocirc; c&uacute;i đầu nh&igrave;n bản th&acirc;n, cơ thể trẻ trung trắng n&otilde;n mềm mại, như chưa từng trải qua năm th&aacute;ng vất vả phong sương, bờ vai trần v&agrave; xương quai xanh đầy những vết h&ocirc;n mập mờ&hellip;<br />\r\n<br />\r\nC&ocirc; vội v&agrave;ng v&eacute;n chăn xuống giường, cố chịu sự kh&oacute; chịu giữa hai ch&acirc;n, bước nhanh v&agrave;o ph&ograve;ng tắm, nh&igrave;n m&igrave;nh trong gương.<br />\r\n<br />\r\nMười năm trước, Qu&yacute; No&atilde;n vốn l&agrave; thi&ecirc;n kim ho&agrave;n mỹ bậc nhất ở Hải Th&agrave;nh. C&ocirc; sở hữu gương mặt xinh đẹp kh&ocirc;ng ai s&aacute;nh bằng, người người ca ngợi. Đ&agrave;n &ocirc;ng nh&igrave;n thấy c&ocirc; th&igrave; xương cốt lập tức mềm nhũn. Chỉ cần c&ocirc; khẽ mỉm cười, v&ocirc; sốđ&agrave;n &ocirc;ng sẽ sốt sắng d&acirc;ng cả t&iacute;nh mạng cho c&ocirc;.<br />\r\n<br />\r\nQu&yacute; No&atilde;n kh&ocirc;ng d&aacute;m tin, c&ocirc; nh&igrave;n chăm chăm v&agrave;o gương mặt cuốn h&uacute;t trong gương.</p>\r\n\r\n<p>C&ocirc;&hellip;đ&atilde; sống lại?!<br />\r\n<br />\r\nTrong l&uacute;c tinh thần c&ocirc; c&ograve;n đang hoảng loạn th&igrave; cửa ph&ograve;ng bất chợt bịđẩy ra. Một giọng n&oacute;i ngọt ng&agrave;o c&oacute; phần thăm d&ograve; vang l&ecirc;n: &quot;Chị, tối qua chị v&agrave; anh Cảnh Th&acirc;m&hellip;&quot;<br />\r\n<br />\r\nNh&igrave;n dấu vết tr&ecirc;n người Qu&yacute; No&atilde;n, giọng n&oacute;i kia lập tức trở n&ecirc;n the th&eacute;: &quot;Hai người đ&atilde; l&ecirc;n giường với nhau?&quot;<br />\r\n<br />\r\nQu&yacute; No&atilde;n giật m&igrave;nh, quay đầu lại liền bắt gặp sắc mặt kh&oacute; coi của Qu&yacute; Mộng Nhi&ecirc;n.<br />\r\n<br />\r\nCảnh n&agrave;y qu&aacute;đỗi quen thuộc&hellip;<br />\r\n<br />\r\nKh&ocirc;ng chỉ sống lại, c&ocirc; c&ograve;n quay trở về một th&aacute;ng trước khi c&ocirc; v&agrave; Mặc Cảnh Th&acirc;m ly h&ocirc;n v&agrave;o mười năm trước!<br />\r\n<br />\r\nĐ&ecirc;m qua l&agrave; lần đầu ti&ecirc;n c&ocirc; v&agrave; Mặc Cảnh Th&acirc;m chung ph&ograve;ng sau nửa năm kết h&ocirc;n!<br />\r\n<br />\r\nC&ocirc; vẫn lu&ocirc;n chống đối cuộc li&ecirc;n h&ocirc;n giữa hai gia tộc, n&ecirc;n bốc đồng muốn ly h&ocirc;n.<br />\r\n<br />\r\nV&igrave; lu&ocirc;n quan t&acirc;m đến cảm gi&aacute;c của c&ocirc; n&ecirc;n Mặc Cảnh Th&acirc;m chưa từng cưỡng &eacute;p.<br />\r\n<br />\r\nMột th&aacute;ng trước khi ly h&ocirc;n, anh vẫn nhẫn nhịn t&iacute;nh t&igrave;nh c&aacute;u kỉnh của c&ocirc;. V&igrave; c&ocirc; kh&ocirc;ng muốn nh&igrave;n thấy anh m&agrave; anh phải ở lại c&ocirc;ng ty l&agrave;m th&ecirc;m giờđến khuya. Đợi c&ocirc; ngủ rồi anh mới về nh&agrave;, hoặc ở hẳn c&ocirc;ng ty.<br />\r\n<br />\r\nTất cả mọi việc xảy ra tối h&ocirc;m qua&hellip;đều l&agrave;&yacute; của Qu&yacute; Mộng Nhi&ecirc;n.<br />\r\n<br />\r\nC&ocirc; ta n&oacute;i, chỉ cần chuốc thuốc v&agrave;o rượu của Mặc Cảnh Th&acirc;m, t&igrave;m cho anh một người phụ nữ, đẩy anh v&agrave;o tội ngoại t&igrave;nh, l&agrave; Qu&yacute; No&atilde;n c&oacute; thểđơn phương xin ly h&ocirc;n.<br />\r\n<br />\r\nNhưng Mặc Cảnh Th&acirc;m l&agrave; ai chứ? Mặc d&ugrave; kh&ocirc;ng ph&ograve;ng bị, nhưng sau khi ph&aacute;t hiện rượu c&oacute; vấn đề, anh đ&atilde; lạnh l&ugrave;ng thẳng thừng từ chối kh&ocirc;ng tiếp x&uacute;c với bất kỳ ai. Trong cơn phẫn nộ, anh k&eacute;o Qu&yacute; No&atilde;n đang định trốn ra ngo&agrave;i v&agrave;o ph&ograve;ng cưới của hai người!<br />\r\n<br />\r\nCũng ch&iacute;nh đ&ecirc;m đ&oacute;, lần đầu ti&ecirc;n Qu&yacute; No&atilde;n nhận ra bộ mặt kh&aacute;c của Mặc Cảnh Th&acirc;m.<br />\r\n<br />\r\nAnh kh&ocirc;ng phong độ dịu d&agrave;ng như vẻ b&ecirc;n ngo&agrave;i, c&agrave;ng kh&ocirc;ng thể m&atilde;i dung t&uacute;ng cho những tr&ograve; xằng bậy của c&ocirc;. Anh ấn c&ocirc; xuống giường, mặc cho c&ocirc; g&agrave;o kh&oacute;c đấm đ&aacute;, kh&ocirc;ng những ho&agrave;n to&agrave;n chứng thực danh phận vợ chồng của họ m&agrave; c&ograve;n gi&agrave;y v&ograve; c&ocirc; suốt một đ&ecirc;m!</p>', NULL, 4, 1, '2020-09-07 18:17:50', NULL),
(29, 'Sơn biên tiểu thôn', 'Chương 1', '<p><em><em><em><em><em>&quot;Anh ngố&quot;</em></em></em></em></em>&nbsp;trợn trừng hai mắt, nh&igrave;n chằm chằm v&agrave;o n&oacute;c nh&agrave; được tạo th&agrave;nh từ cỏ dại v&agrave; b&ugrave;n trộn lẫn. To&agrave;n th&acirc;n hắn được tr&ugrave;m bởi một c&aacute;i &aacute;o b&ocirc;ng đ&atilde; cũ, ố v&agrave;ng, nh&igrave;n kh&ocirc;ng c&ograve;n ra h&igrave;nh dạng ban đ&acirc;u, phảng phất t&aacute;n ph&aacute;t ra một &iacute;t m&ugrave;i ẩm mốc.<br />\r\n<br />\r\nB&ecirc;n cạnh hắn c&ograve;n c&oacute; một người nữa, l&agrave; nhị ca H&agrave;n Ch&uacute;, đang ngủ rất say sưa. Thỉnh thoảng c&oacute; tiếng ng&aacute;y nh&egrave; nhẹ ph&aacute;t ra từ đ&oacute;.<br />\r\n<br />\r\nC&aacute;ch giường chừng nửa trượng, l&agrave; một v&aacute;ch tường đất đổ n&aacute;t, v&igrave; thời gian đ&atilde; qu&aacute; l&acirc;u, tr&ecirc;n v&aacute;ch tường đ&atilde; xuất hiện v&agrave;i vết nứt d&agrave;i. Từ những vết nứt đ&oacute;, lo&aacute;ng tho&aacute;ng truyền đến thanh &acirc;m o&aacute;n th&aacute;n của H&agrave;n mẫu, ngo&agrave;i ra c&ograve;n c&oacute; thanh &acirc;m H&agrave;n phụ đang h&uacute;t thuốc rất l&agrave; hấp dẫn.<br />\r\n<br />\r\n&#39;Anh ngố&#39; từ từ nhắm đ&ocirc;i mắt c&oacute; ch&uacute;t bức bối lại. Muốn thật nhanh ch&igrave;m v&agrave;o giấc ngủ s&acirc;u. Trong l&ograve;ng hắn biết r&otilde; r&agrave;ng, nếu b&acirc;y giờ m&agrave; c&ograve;n kh&ocirc;ng ngủ ngay, ng&agrave;y mai kh&ocirc;ng thể n&agrave;o dậy sớm được, cũng kh&ocirc;ng thể c&ugrave;ng đ&aacute;m bạn c&ugrave;ng đi đốn củi được.<br />\r\n<br />\r\n<em><em><em><em><em>&quot;Anh ngố&quot;</em></em></em></em></em>&nbsp;họ H&agrave;n t&ecirc;n Lập, loại danh tự c&oacute; &yacute; nghĩa như thế n&agrave;y cha mẹ hắn kh&ocirc;ng c&oacute; khả năng đưa ra. C&aacute;i n&agrave;y l&agrave; do phụ th&acirc;n hắn d&ugrave;ng rượu oa đầu chế bởi th&ocirc; lương, cầu l&atilde;o Trương trong th&ocirc;n đặt cho.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&atilde;o Trương khi c&ograve;n trẻ, đ&atilde; từng l&agrave;m thư đồng mấy năm cho một nh&agrave; c&oacute; tiền trong th&agrave;nh. L&agrave; người duy nhất trong th&ocirc;n nhận biết được v&agrave;i chữ. T&ecirc;n gọi của hầu hết tiểu h&agrave;i tử trong th&ocirc;n, đều l&agrave; do l&atilde;o Trương đặt cho.<br />\r\n<br />\r\nH&agrave;n Lập bị người trong th&ocirc;n gọi l&agrave;&nbsp;<em><em><em><em><em>&quot;Anh ngố&quot;</em></em></em></em></em>&nbsp;kh&ocirc;ng phải l&agrave; do hắn ngố hay đần thật sự, ngược lại, hắn c&ograve;n l&agrave; đứa trẻ th&ocirc;ng minh nhất l&agrave;ng, ngo&agrave;i ra tr&ocirc;ng hắn so với những đứa trẻ kh&aacute;c trong l&agrave;ng kh&ocirc;ng c&oacute; g&igrave; kh&aacute;c biệt. Trừ những người trong nh&agrave; ra, hắn rất &iacute;t khi nghe thấy nguời ta gọi t&ecirc;n ch&iacute;nh thức H&agrave;n Lập của hắn, m&agrave; hầu như chỉ l&agrave;&nbsp;<em><em><em><em><em>&quot;Anh ngố&quot;</em></em></em></em></em>, v&agrave; c&aacute;i t&ecirc;n&nbsp;<em><em><em><em><em>&quot;Anh ngố&quot;</em></em></em></em></em>&nbsp;n&agrave;y vẫn được sử dụng cho đến tận b&acirc;y giờ.<br />\r\n<br />\r\nSở dĩ H&agrave;n Lập bị mọi người ban cho hỗn danh&nbsp;<em><em>&quot;anh ngố&quot;</em></em>&nbsp;l&agrave; v&igrave; trong th&ocirc;n vốn đ&atilde; c&oacute; một&nbsp;<em>&quot;anh ngốc&quot;</em>&nbsp;rồi.<br />\r\n<br />\r\nĐiều n&agrave;y cũng kh&ocirc;ng c&oacute; g&igrave; to t&aacute;t cả, tất cả những đứa trẻ kh&aacute;c trong th&ocirc;n đều c&oacute; hỗn danh như&nbsp;<em>&quot;cẩu oa&quot;</em>&nbsp;hay&nbsp;<em>&quot;nhị đản&quot;</em>, so với danh tự&nbsp;<em><em>&quot;anh ngố&quot;</em></em>&nbsp;th&igrave; c&ograve;n kh&oacute; nghe hơn.<br />\r\n<br />\r\nCũng bởi vậy, H&agrave;n Lập mặc d&ugrave; kh&ocirc;ng th&iacute;ch c&aacute;ch xưng h&ocirc; n&agrave;y nhưng cũng chỉ c&oacute; thể tự an ủi m&igrave;nh m&agrave; th&ocirc;i.<br />\r\n<br />\r\nH&agrave;n Lập bề ngo&agrave;i tr&ocirc;ng kh&ocirc;ng được vừa mắt, da tay th&igrave; đen đ&uacute;a, đ&iacute;ch thực l&agrave; một đứa trẻ b&igrave;nh thường chốn l&agrave;ng qu&ecirc;. Tuy nhi&ecirc;n, nội t&acirc;m của cậu b&eacute; th&igrave; kh&ocirc;ng hề n&ocirc;ng nổi, so với những đứa b&eacute; c&ugrave;ng lứa tuổi th&igrave; ch&iacute;n chắn hơn nhiều. Hắn từ nhỏ đ&atilde; hướng tới thế giới phồn hoa b&ecirc;n ngo&agrave;i, mơ rằng c&oacute; một ng&agrave;y, hắn c&oacute; thể ra khỏi th&ocirc;n l&agrave;ng, đi xem xem c&aacute;i thế giới ph&ugrave; hoa m&agrave; l&atilde;o Trương thường n&oacute;i đến.<br />\r\n<br />\r\nKhi H&agrave;n Lập nghĩ đến &yacute; tưởng n&agrave;y, hắn kh&ocirc;ng d&aacute;m đề cập ra cho người kh&aacute;c biết. Nếu kh&ocirc;ng, nhất định l&agrave;m cho mọi người trong th&ocirc;n cảm thấy ngạc nhi&ecirc;n, một tiểu h&agrave;i tử miệng c&ograve;n chưa kh&ocirc; m&ugrave;i sữa, thế m&agrave; d&aacute;m mơ tưởng đến những &yacute; nghĩ xa vời m&agrave; ngay cả một người lớn cũng chưa d&aacute;m nghĩ đến. Cần phải biết rằng, những đưa trẻ kh&aacute;c c&ugrave;ng tuổi với H&agrave;n Lập th&igrave; tầm tuổi n&agrave;y chỉ biết đuổi g&agrave;, bắt ch&oacute;, tất nhi&ecirc;n l&agrave; ở đ&acirc;y sẽ kh&ocirc;ng n&oacute;i đến những kẻ c&oacute; &yacute; nghĩ tha huơng cầu thực.<br />\r\n<br />\r\nGia đ&igrave;nh H&agrave;n Lập c&oacute; bảy miệng ăn, tr&ecirc;n hắn c&oacute; hai vị huynh trưởng, một tỷ tỷ, hắn trong nh&agrave; đứng thứ tư, ngo&agrave;i ra hắn c&ograve;n c&oacute; một tiểu muội muội nữa. Năm nay hắn vừa mới mười tuổi, gia cảnh bần h&agrave;n, cả năm cũng kh&ocirc;ng c&oacute; mấy bữa được ăn no. Mọi người trong nh&agrave; đều chỉ mong được ăn đủ no, mặc đủ ấm.<br />\r\n<br />\r\nH&agrave;n Lập l&uacute;c n&agrave;y, đang mơ mơ m&agrave;ng m&agrave;ng, tuy ngủ m&agrave; chưa ngủ, trong đầu vẫn c&ograve;n phảng phất &yacute; niệm: Ng&agrave;y mai l&ecirc;n n&uacute;i, nhất định sẽ mang về cho tiểu muội muội m&agrave; hắn y&ecirc;u thương nhất thật nhiều hồng tương quả*, loại quả m&agrave; muội muội hắn th&iacute;ch nhất.<br />\r\n<br />\r\nV&agrave;o giữa trưa ng&agrave;y thứ hai, H&agrave;n Lập dưới &aacute;nh nắng ch&oacute;i trang, lưng g&ugrave;i b&oacute; củi cao bằng nửa người hắn, trước ngực th&igrave; &ocirc;m một nắm đầy hồng tương quả, đang từ ngọn n&uacute;i trở về nh&agrave;. L&uacute;c n&agrave;y, hắn kh&ocirc;ng hề biết rằng trong nh&agrave; đang c&oacute; một vị kh&aacute;ch đến chơi, m&agrave; vị kh&aacute;ch n&agrave;y, ch&iacute;nh l&agrave; người cải biến vận mệnh của hắn.</p>\r\n\r\n<p>QUẢNG C&Aacute;O</p>\r\n\r\n<p><a href=\"https://widgets.mgid.com/vi/?utm_source=truyenfull.vn&amp;utm_medium=referral&amp;utm_campaign=widgets&amp;utm_content=863748\" rel=\"nofollow noopener\" target=\"_blank\"><img alt=\"Mgid\" src=\"https://cdn.mgid.com/images/logos/ByMGID.svg\" title=\"Mgid\" /></a></p>', NULL, 12, 1, '2020-09-08 18:19:11', NULL),
(30, 'Sống lại', 'Chương 1', '<p>Vũ Quốc, Lăng Gia ở Thương V&acirc;n Trấn, trăng treo giữa trời.<br />\r\n<br />\r\nLăng H&agrave;n bỏ ra r&ograve;ng r&atilde; mười gi&acirc;y, mới rốt cục khẳng định, hắn x&aacute;c thực chuyển thế sống lại.<br />\r\n<br />\r\nĐối với một cường giả Thi&ecirc;n Nh&acirc;n Cảnh m&agrave; n&oacute;i, cần mất mười gi&acirc;y mới c&oacute; thể khẳng định một chuyện, đ&acirc;y tuyệt đối kh&oacute; m&agrave; tin nổi, nhưng cũng đồng dạng chứng minh, sự t&igrave;nh ph&aacute;t sinh ở tr&ecirc;n người hắn, l&agrave; kh&oacute; m&agrave; tin nổi cỡ n&agrave;o.<br />\r\n<br />\r\nĐời trước, hắn đứng tr&ecirc;n đỉnh cao của v&otilde; đạo, ở đan đạo cũng c&oacute; th&agrave;nh tựu cổ kim chưa thấy, khai s&aacute;ng Tam Hỏa Dẫn thuật, gợi ra một hồi c&aacute;ch mạng ở Luyện Đan giới, được người t&ocirc;n l&agrave; Đan Đế.<br />\r\n<br />\r\nNhưng hắn vẫn chưa thỏa m&atilde;n, c&ograve;n muốn tiến th&ecirc;m một bước, đạt đến Ph&aacute; Hư Cảnh, Ph&aacute; To&aacute;i Hư Kh&ocirc;ng, h&oacute;a Ph&agrave;m th&agrave;nh Thần trong truyền thuyết. V&igrave; thế, hắn thăm d&ograve; v&ocirc; số di t&iacute;ch, cuối c&ugrave;ng tiến v&agrave;o Hắc Huyết Cốc, trải qua tầng tầng nguy hiểm, sau đ&oacute; t&igrave;m tới một Cổ Th&aacute;p thần b&iacute;.<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; để hắn thất vọng, tr&ecirc;n Cổ Th&aacute;p c&oacute; văn tự m&agrave;u v&agrave;ng, ch&iacute;nh l&agrave; một bộ c&ocirc;ng ph&aacute;p v&ocirc; thượng, t&ecirc;n l&agrave; Bất Diệt Thi&ecirc;n Kinh, luyện đến cảnh giới tối cao, th&acirc;n thể bất hư bất hủ, c&ugrave;ng thi&ecirc;n địa đồng thọ!<br />\r\n<br />\r\nNhưng cho d&ugrave; lấy tu vi Thi&ecirc;n Nh&acirc;n Cảnh của Lăng H&agrave;n, vẫn cảm thấy Bất Diệt Thi&ecirc;n Kinh kia kh&oacute; hiểu mịt mờ, thật giống như xem một quyển thi&ecirc;n thư, ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; manh mối.<br />\r\n<br />\r\nHắn mạnh mẽ ghi nhớ bản c&ocirc;ng ph&aacute;p kia, đang muốn nghi&ecirc;n cứu Cổ Th&aacute;p một ch&uacute;t, th&igrave; Cổ Th&aacute;p lại chấn động, bắn ra một đạo thần quang v&ocirc; lượng, trong nh&aacute;y mắt liền đ&aacute;nh n&aacute;t cơ thể của hắn. Nhưng qu&aacute;i lạ ch&iacute;nh l&agrave;, linh hồn của hắn lại kh&ocirc;ng ti&ecirc;u tan, rơi v&agrave;o một loại trạng th&aacute;i tỉnh tỉnh m&ecirc; m&ecirc;, k&eacute;o d&agrave;i hơn vạn năm.<br />\r\n<br />\r\nỞ chỗ n&agrave;y vạn năm, linh hồn của hắn vẫn suy diễn Bất Diệt Thi&ecirc;n Kinh, ngoại trừ chuyện đ&oacute;, th&igrave; hắn kh&ocirc;ng l&agrave;m được chuyện g&igrave;, mười ng&agrave;n năm sau, hắn rốt cục lĩnh ngộ tầng thứ nhất của c&ocirc;ng ph&aacute;p.<br />\r\n<br />\r\nMột cường giả Thi&ecirc;n Nh&acirc;n Cảnh, bỏ ra 10000 năm, mới lĩnh ngộ được tầng thứ nhất của một m&ocirc;n c&ocirc;ng ph&aacute;p, kh&aacute;i niệm n&agrave;y nghĩa l&agrave; g&igrave;?<br />\r\n<br />\r\nPhải biết cường giả Thi&ecirc;n Nh&acirc;n Cảnh, tuổi thọ cũng kh&ocirc;ng tới ngàn năm, b&igrave;nh thường m&agrave; n&oacute;i, thế gian căn bản kh&ocirc;ng ai c&oacute; thể tu th&agrave;nh Bất Diệt Thi&ecirc;n Kinh, bởi v&igrave; c&ograve;n chưa c&oacute; bắt đầu đ&atilde; chết gi&agrave;.</p>\r\n\r\n<p>Nhưng Lăng H&agrave;n lại lấy loại phương thức qu&aacute;i lạ n&agrave;y, nắm giữ tầng thứ nhất của Bất Diệt Thi&ecirc;n Kinh, sau đ&oacute;, hắn lại đột nhi&ecirc;n tr&ugrave;ng sinh, chuyển thế sống lại ở tr&ecirc;n người một thiếu ni&ecirc;n mười s&aacute;u tuổi, đồng dạng t&ecirc;n l&agrave; Lăng H&agrave;n.<br />\r\n<br />\r\nKh&oacute; m&agrave; tin nổi!<br />\r\n<br />\r\n- Mặc kệ xảy ra chuyện g&igrave;, n&oacute;i chung ta đ&atilde; sống lại!<br />\r\n<br />\r\n- Tuy bộ th&acirc;n thể n&agrave;y mới chỉ l&agrave; Luyện Thể tầng hai, phế vật đ&ecirc;́n kh&ocirc;ng thể lại phế vật, nhưng ta đ&atilde; từng l&agrave; cường giả Thi&ecirc;n Nh&acirc;n Cảnh, đứng ở đỉnh phong của v&otilde; đạo, lại l&agrave; đan đạo đại sư, tư chất k&eacute;m th&igrave; d&ugrave;ng đan dược tu bổ, kh&ocirc;ng tin kh&ocirc;ng thể trở lại đỉnh cao.<br />\r\n<br />\r\n- Hơn nữa, ta rốt cục c&oacute; thể tu luyện Bất Diệt Thi&ecirc;n Kinh, đ&acirc;y l&agrave; một c&ocirc;ng ph&aacute;p v&ocirc; thượng, thậm ch&iacute;... Khả năng kh&ocirc;ng phải ở Ph&agrave;m Giới, m&agrave; đến từ Thần giới trong truyền thuyết, bằng kh&ocirc;ng l&agrave;m sao c&oacute; khả năng cần mười ng&agrave;n năm mới c&oacute; thể hiểu được tầng thứ nhất?<br />\r\n<br />\r\n- Đời n&agrave;y, ta nhất định c&oacute; thể vượt qua tiền nh&acirc;n, th&agrave;nh tựu Thần vị!<br />\r\n<br />\r\n- C&ograve;n c&oacute;, th&acirc;n thể n&agrave;y đến tột c&ugrave;ng l&agrave; xảy ra chuyện g&igrave;, mười s&aacute;u tuổi mới tu luyện tới Luyện Thể tầng hai, lẽ n&agrave;o l&agrave; bởi v&igrave; v&otilde; đạo b&acirc;y giờ qu&aacute; mức h&eacute;o t&agrave;n?<br />\r\n<br />\r\nOanh, v&ocirc; số k&yacute; ức tr&agrave;n v&agrave;o trong đầu, đ&acirc;y l&agrave; k&yacute; ức thuộc về một Lăng H&agrave;n kh&aacute;c, m&agrave; trong qu&aacute; tr&igrave;nh n&agrave;y, hai người ho&agrave;n to&agrave;n kh&aacute;c nhau đ&atilde; dung hợp l&agrave;m một.<br />\r\n<br />\r\n- Ta đ&atilde; r&otilde; r&agrave;ng.<br />\r\n<br />\r\nLăng H&agrave;n gật đầu, sở dĩ hiện tại hắn mới l&agrave; Luyện Thể tầng hai, nguy&ecirc;n nh&acirc;n ở chỗ linh căn của hắn qu&aacute; k&eacute;m.<br />\r\n<br />\r\nMuốn tu luyện th&agrave;nh V&otilde; Giả, c&oacute; một điều kiện ti&ecirc;n quyết, ch&iacute;nh l&agrave; nhất định phải nắm giữ linh căn. C&oacute; linh căn, mới c&oacute; thể hấp thu linh kh&iacute; trong trời đất, r&egrave;n luyện bản th&acirc;n, h&oacute;a th&agrave;nh nguy&ecirc;n lực của bản th&acirc;n.<br />\r\n<br />\r\nC&oacute; linh căn hấp thu linh kh&iacute; nhanh, c&oacute; linh căn th&igrave; chậm, bởi vậy linh căn chia l&agrave;m bốn đẳng cấp Thi&ecirc;n Địa Huyền Ho&agrave;ng, mỗi đẳng cấp lại chia l&agrave;m ba phẩm giai thượng trung hạ, lấy Thi&ecirc;n Cấp thượng phẩm tốt nhất, Ho&agrave;ng Cấp hạ phẩm k&eacute;m nhất.<br />\r\n<br />\r\nPh&aacute;n đo&aacute;n linh căn ưu khuyết, then chốt ở tr&igrave;nh độ tinh khiết, c&agrave;ng tinh khiết c&agrave;ng tốt, m&agrave; linh căn của Lăng H&agrave;n, lại l&agrave; Ngũ h&agrave;nh đủ cả, thuộc loại hỗn độn nhất, ở thế giới v&otilde; đạo, đ&acirc;y l&agrave; loại linh căn k&eacute;m nhất, t&iacute;nh v&agrave;o Ho&agrave;ng Cấp hạ phẩm cũng chỉ c&oacute; thể n&oacute;i miễn cưỡng, bởi vậy năm nay hắn đ&atilde; mười s&aacute;u tuổi, nhưng chỉ tu luy&ecirc;̣n đến Luyện Thể tầng hai, m&agrave; bạn c&ugrave;ng lứa tuổi, k&eacute;m nhất cũng đạt đến Luyện Thể tầng bốn, tầng năm.<br />\r\n<br />\r\nĐời trước, Lăng H&agrave;n l&agrave; Thi&ecirc;n linh căn, hơn nữa c&ograve;n l&agrave; Cửu Dương Hỏa Linh Căn Thi&ecirc;n Cấp thượng phẩm, lại th&ecirc;m thi&ecirc;n ph&uacute; đan đạo kh&ocirc;ng ai bằng, lấy đan phụ vũ, vẻn vẹn chỉ d&ugrave;ng hai trăm năm, liền đạt đến Thi&ecirc;n Nh&acirc;n Cảnh, đ&acirc;y l&agrave; kỷ lục cổ kim chưa c&oacute;.<br />\r\n<br />\r\n- Ngũ H&agrave;nh Tạp Linh Căn, đ&acirc;y l&agrave; linh căn th&acirc;́p nh&acirc;́t a, chẳng tr&aacute;ch, tuy tiền th&acirc;n cực kỳ nỗ lực, nhưng chỉ c&oacute; thể tới Luyện Thể tầng hai.<br />\r\n<br />\r\n- Với ta m&agrave; n&oacute;i, tuy Ngũ H&agrave;nh Tạp Linh Căn c&oacute; ch&uacute;t vướng tay ch&acirc;n, nhưng dựa v&agrave;o đan dược, nhiều nhất bốn trăm năm, ta lại tới đỉnh cao!<br />\r\n<br />\r\n- Ồ!<br />\r\n<br />\r\nTrong l&ograve;ng Lăng H&agrave;n kinh ngạc thốt l&ecirc;n, tr&ecirc;n mặt lộ ra vẻ khiếp sợ kh&oacute; c&oacute; thể tin tưởng, hắn sửng sốt một hồi l&acirc;u, mới lấy hồn lực mạnh mẽ quan s&aacute;t linh căn ở đan điền, vẻ mặt kinh ngạc nhất thời biến th&agrave;nh mừng như đi&ecirc;n.<br />\r\n<br />\r\n- C&aacute;i n&agrave;y kh&ocirc;ng phải Ngũ H&agrave;nh Tạp Linh Căn, Ngũ h&agrave;nh ho&agrave;n to&agrave;n c&acirc;n đối, h&igrave;nh th&agrave;nh một đ&oacute;a Đạo Li&ecirc;n! Đ&acirc;y l&agrave; Ngũ H&agrave;nh Hỗn Độn Li&ecirc;n, cực phẩm trong cực phẩm, so với Cửu Dương Hỏa Linh Căn c&ograve;n qu&yacute; hiếm, c&oacute; thể coi l&agrave; Linh Căn Thần Cấp!<br />\r\n<br />\r\n- Tiền th&acirc;n tu luyện chậm, l&agrave; bởi v&igrave; Linh Căn Thần Cấp, đều cần c&ocirc;ng ph&aacute;p đặc biệt mới c&oacute; thể ph&aacute;t huy ra uy năng ch&acirc;n ch&iacute;nh.<br />\r\n<br />\r\n- M&agrave; ta... lại nắm giữ Ngũ H&agrave;nh Thi&ecirc;n Cực C&ocirc;ng, xứng đ&ocirc;i với Ngũ H&agrave;nh Hỗn Độn Li&ecirc;n!<br />\r\n<br />\r\n- Một trăm năm! Kh&ocirc;ng, chỉ cần năm mươi năm, ta liền c&oacute; thể đạt đến Thi&ecirc;n Nh&acirc;n Cảnh lần nữa!<br />\r\n<br />\r\nD&ugrave; lấy tu vi Thi&ecirc;n Nh&acirc;n Cảnh kiếp trước, Lăng H&agrave;n cũng kh&ocirc;ng nhịn được lộ vẻ vui mừng, linh căn khi sinh ra đ&atilde; định, hậu thi&ecirc;n kh&ocirc;ng c&aacute;ch n&agrave;o thay đổi. Kiếp trước hắn v&igrave; Ph&aacute; Hư th&agrave;nh Thần, thăm viếng qua v&ocirc; số di t&iacute;ch, được lượng lớn c&ocirc;ng ph&aacute;p b&iacute; thuật, Ngũ H&agrave;nh Thi&ecirc;n Cực C&ocirc;ng ch&iacute;nh l&agrave; một loại trong đ&oacute;.<br />\r\n<br />\r\nĐời trước, hắn đứng ở đỉnh cao v&otilde; đạo, đời n&agrave;y, hắn lại lập huy ho&agrave;ng, viết ra thần thoại bất hủ.</p>', NULL, 5, 1, '2020-09-08 10:07:38', NULL);
INSERT INTO `chapters` (`id`, `name`, `subname`, `content`, `view`, `story_id`, `user_id`, `created_at`, `updated_at`) VALUES
(31, 'Hắn tên là Bạch Tiểu Thuần', 'Chương 1', '<p><em>Dịch: H&agrave;n Thi&ecirc;n Long</em><br />\r\n&nbsp;</p>\r\n\r\n<p>Mạo Nhi Sơn l&agrave; một ngọn n&uacute;i ở b&ecirc;n trong Đ&ocirc;ng L&acirc;m sơn mạch. Ở dưới n&uacute;i c&oacute; một th&ocirc;n l&agrave;ng, d&acirc;n phong ở đ&acirc;y thuần ph&aacute;c, thường c&agrave;y ruộng m&agrave; sống, đoạn tuyệt với thế giới b&ecirc;n ngo&agrave;i.<br />\r\n<br />\r\nMột buổi s&aacute;ng sớm...<br />\r\n<br />\r\nỞ trước cổng ch&iacute;nh của th&ocirc;n trang, to&agrave;n bộ hương th&acirc;n trong th&ocirc;n đang đứng tiễn đưa một thiếu ni&ecirc;n chừng mười lăm mười s&aacute;u tuổi. Thiếu ni&ecirc;n n&agrave;y tr&ocirc;ng gầy yếu nhưng trắng trẻo sạch sẽ, thoạt nh&igrave;n rất l&agrave; nhu thuận. Y phục tr&ecirc;n người hắn chỉ l&agrave; một bộ thanh sam b&igrave;nh thường nhưng đ&atilde; bị phai m&agrave;u trở th&agrave;nh m&agrave;u trắng, hai mắt to&aacute;t l&ecirc;n vẻ lanh lợi, trong s&aacute;ng.<br />\r\n<br />\r\nHắn t&ecirc;n l&agrave; Bạch Tiểu Thuần.<br />\r\n<br />\r\n&ldquo;C&aacute;c hương th&acirc;n phụ l&atilde;o, ta phải đi tu ti&ecirc;n rồi, nhưng ta kh&ocirc;ng nỡ xa c&aacute;c ngươi&rdquo;. Vẻ mặt thiếu ni&ecirc;n tr&agrave;n ngập vẻ kh&ocirc;ng nỡ, bộ d&aacute;ng của hắn vốn nhu thuận, l&uacute;c n&agrave;y thoạt nh&igrave;n lại c&agrave;ng trở n&ecirc;n chất ph&aacute;c.<br />\r\n<br />\r\nC&aacute;c vị hương th&acirc;n quay mặt nh&igrave;n nhau, cũng l&agrave;m ra vẻ kh&ocirc;ng nỡ rời xa hắn.</p>', NULL, 9, 1, '2020-09-08 10:10:26', NULL),
(32, 'Tiểu tư quét rác', 'Chương 1', '<p>Trời mới tờ mờ s&aacute;ng, Dương Khai đ&atilde; tỉnh dậy, rửa mặt qua loa rồi cầm c&aacute;i chổi trong g&oacute;c bước ra khỏi căn ph&ograve;ng nhỏ đơn độc.</p>\r\n\r\n<p>Đứng ở cửa vươn vai một c&aacute;i, nh&igrave;n thấy ph&iacute;a đường ch&acirc;n trời đ&atilde; hiện l&ecirc;n ch&uacute;t &aacute;nh s&aacute;ng. Hắn nhắm mắt tập trung để hưởng thụ khoảnh khắc thanh tịnh n&agrave;y rồi lại ngay lập tức mở mắt ra bắt đầu cầm chổi qu&eacute;t dọn, chăm ch&uacute; dọn sạch đ&aacute;m bụi bặm v&agrave; l&aacute; rụng trước mặt.</p>\r\n\r\n<p>Y phục m&agrave;u xanh, giản dị m&agrave; sạch sẽ. M&agrave;u sắc gi&agrave; dặn của bộ trang phục v&ocirc; t&igrave;nh l&agrave;m cho cậu thiếu ni&ecirc;n gi&agrave; đi mấy tuổi. Lưng Dương Khai thẳng tắp như c&acirc;y lao. Mặc d&ugrave; đang l&agrave;m c&ocirc;ng việc thấp h&egrave;n nhưng thần sắc tr&ecirc;n mặt cũng tỏ r&otilde; sự cẩn thận, tỉ mỉ. Động t&aacute;c rất trầm ổn, hai tay cầm chổi cũng kh&ocirc;ng d&ugrave;ng nhiều lực, cũng kh&ocirc;ng đong đưa người nhiều, chỉ bằng sự chuyển động của cổ tay, c&aacute;i chổi kia trở n&ecirc;n dễ d&agrave;ng sai khiến, đưa đi đưa lại, di chuyển theo từng bước ch&acirc;n hắn. Tr&ecirc;n mặt đất, từng đ&aacute;m bụi đất v&agrave; l&aacute; rụng chuyển động một c&aacute;ch thần kỳ, dường như bỗng nhi&ecirc;n mọc th&ecirc;m đ&ocirc;i ch&acirc;n.</p>\r\n\r\n<p>Dương Khai l&agrave; đệ tử của Lăng Ti&ecirc;u c&aacute;c, ba năm trước đ&atilde; gia nhập t&ocirc;ng m&ocirc;n bắt đầu tu luyện nhưng cho đến nay vẫn chỉ mới tu luyện đến Thối Thể cảnh tầng ba. C&aacute;c sư huynh đệ c&ugrave;ng hắn nhập m&ocirc;n đ&atilde; sớm vượt xa giai đoạn n&agrave;y, c&oacute; những người c&ograve;n c&oacute; được cơ duy&ecirc;n b&aacute;i nhập c&aacute;c vị cao nh&acirc;n tọa hạ trong m&ocirc;n ph&aacute;i v&agrave; thăng tiến rất nhanh. C&ograve;n hắn chỉ c&oacute; thể lực bất t&ograve;ng t&acirc;m.</p>\r\n\r\n<p>Ba năm Thối Thể tầng ba, loại tư chất n&agrave;y kh&ocirc;ng thể d&ugrave;ng hai chữ b&igrave;nh thường để h&igrave;nh dung ra được m&agrave; phải n&oacute;i l&agrave; v&ocirc; c&ugrave;ng b&igrave;nh thường.</p>\r\n\r\n<p>Kh&ocirc;ng c&ograve;n c&aacute;ch n&agrave;o kh&aacute;c, Dương Khai chỉ c&oacute; thể tiếp tục ở lại t&ocirc;ng m&ocirc;n qu&eacute;t r&aacute;c, vừa duy tr&igrave; kế sinh nhai, vừa khổ sở tu luyện.</p>\r\n\r\n<p>Lăng Ti&ecirc;u c&aacute;c l&agrave; một m&ocirc;n ph&aacute;i kh&aacute; đặc biệt. Đặc biệt ở chỗ c&aacute;c đệ tử m&ocirc;n hạ phải cạnh tranh t&agrave;n khốc. Trong m&ocirc;n ph&aacute;i n&agrave;y, người c&oacute; năng lực sẽ được thăng tiến, người kh&ocirc;ng c&oacute; năng lực sẽ bị đ&agrave;o thải. Quy luật c&aacute; lớn nuốt c&aacute; b&eacute; ở Lăng Ti&ecirc;u c&aacute;c n&agrave;y diễn ra v&ocirc; c&ugrave;ng thuần thục.</p>\r\n\r\n<p>C&aacute;c t&ocirc;ng m&ocirc;n kh&aacute;c c&oacute; lẽ c&ograve;n c&oacute; ch&uacute;t t&igrave;nh bạn đồng m&ocirc;n hay t&igrave;nh nghĩa thủ t&uacute;c, nhưng ở Lăng Ti&ecirc;u c&aacute;c n&agrave;y tuyệt đối kh&ocirc;ng c&oacute;! Nếu muốn thăng tiến chỉ c&oacute; một con đường duy nhất l&agrave; đạp l&ecirc;n vai những người gọi l&agrave; sư huynh đệ, giẫm l&ecirc;n m&aacute;u tươi của họ, như vậy mới được coi l&agrave; c&oacute; tư c&aacute;ch.</p>\r\n\r\n<p>Lăng Ti&ecirc;u c&aacute;c ra quy định rất nghi&ecirc;m ngặt, l&agrave; m&ocirc;n ph&aacute;i danh tiếng lẫy lừng trong Đại H&aacute;n triều n&agrave;y. D&ugrave; kh&ocirc;ng phải đại m&ocirc;n ph&aacute;i cao si&ecirc;u g&igrave; nhưng sự tranh đấu t&agrave;n khốc giữa c&aacute;c m&ocirc;n hạ đệ tử th&igrave; đ&uacute;ng l&agrave; số một, kh&ocirc;ng m&ocirc;n ph&aacute;i n&agrave;o s&aacute;nh bằng! Cũng ch&iacute;nh v&igrave; vậy m&agrave; c&aacute;c đệ tử ai nấy đều v&otilde; phong dũng m&atilde;nh, tr&ecirc;n giang hồ kh&ocirc;ng c&oacute; mấy ai d&aacute;m tr&ecirc;u chọc.</p>\r\n\r\n<p>Lăng Ti&ecirc;u c&aacute;c c&oacute; một quy tắc, c&aacute;c đệ tử dưới mười bốn tuổi bất kể l&agrave; ai, trong ba năm kể từ khi nhập m&ocirc;n được xem như thời gian th&iacute; luyện. Trong v&ograve;ng ba năm n&agrave;y, việc ăn ở đều do t&ocirc;ng m&ocirc;n phụ tr&aacute;ch, đệ tử chỉ cần tập trung tu luyện. Trong thời gian ba năm, nếu c&oacute; thể ph&aacute; được Thối Thể kỳ th&igrave; sẽ c&oacute; tư c&aacute;ch b&aacute;i nhập c&aacute;c cao thủ trong t&ocirc;ng m&ocirc;n l&agrave;m đồ đệ, được c&aacute;c vị cao thủ n&agrave;y chỉ dạy tu luyện. Đương nhi&ecirc;n cũng c&oacute; thể kh&ocirc;ng b&aacute;i sư m&agrave; tự m&igrave;nh tu luyện, nhưng con đường tu luyện c&oacute; lương sư dạy bảo v&agrave; tự m&igrave;nh tu luyện cũng ho&agrave;n to&agrave;n kh&aacute;c nhau. Tới một mức độ n&agrave;o đ&oacute; m&agrave; n&oacute;i, quy tắc n&agrave;y của Lăng Ti&ecirc;u c&aacute;c cũng c&oacute; một ch&uacute;t tự do.</p>', NULL, 11, 1, '2020-09-08 10:44:57', NULL),
(33, 'Tiết tử', 'Chương 1-1', '<p>Edit: Tử Mộc<br />\r\nBeta: Nguyệt Trang<br />\r\nN&agrave;ng đ&atilde; chết&hellip; N&agrave;ng chậm r&atilde;i mở mắt ra đ&acirc;y l&agrave; c&oacute; chuyện g&igrave;? trước mắt n&agrave;ng l&agrave; một c&aacute;i thổ ph&ograve;ng, đ&uacute;ng vậy l&agrave; một c&aacute;i thổ ph&ograve;ng ở. Mở to mắt nh&igrave;n xem chung quanh, như thế n&agrave;o nh&igrave;n giống cổ đại? Kh&ocirc;ng thẻ n&agrave;o n&agrave;ng&hellip; xuy&ecirc;n qua?<br />\r\n<br />\r\nNữ oa nhi thế n&agrave;o kh&ocirc;ng kh&oacute;c? Một phụ nh&acirc;n nh&igrave;n n&agrave;ng.<br />\r\n<br />\r\nB&igrave;nh thường, đứa trẻ sinh ra sẽ kh&oacute;c. Một phụ nh&acirc;n&nbsp;&nbsp;kh&aacute;c n&oacute;i.</p>\r\n\r\n<p>Vừa sinh hạ đến&hellip; C&aacute;i g&igrave;?! N&agrave;ng vừa sinh hạ đến?? N&agrave;ng kia hiện tại sẽ kh&ocirc;ng phải&hellip; phải&hellip; trẻ con! Thế giới thật nhiều sự bất ngờ. N&agrave;ng ở thế kỷ 21 vẫn l&agrave; 18 tuổi. Đến đ&acirc;y cư nhi&ecirc;n l&agrave;&hellip; 0 tuổi. Th&acirc;n thể 0 tuổi&hellip; &yacute; nghĩ 18 tuổi?<br />\r\n<br />\r\n&ldquo;-Mặc kệ sao, d&ugrave; sao tướng c&ocirc;ng ta cũng kh&ocirc;ng t&iacute;nh lưu n&agrave;ng lại.&rdquo; Một phụ nh&acirc;n nằm ở b&ecirc;n cạnh n&oacute;i. Nếu n&agrave;ng kh&ocirc;ng nhầm th&igrave; lời n&oacute;i kia của &ldquo;nương&rdquo; n&agrave;ng nha. Người mẹ th&acirc;n sinh n&agrave;ng kh&ocirc;ng cần n&agrave;ng! Thi&ecirc;n~~~ người như thế n&agrave;o để ta xuy&ecirc;n qua th&acirc;n thể n&agrave;y~~~<br />\r\nV&ugrave;~~~ một trận gi&oacute; m&aacute;t quấy rầy n&agrave;ng mộng đẹp. Mở mắt ra, n&agrave;ng bị t&igrave;nh cảnh trước mắt hoảng sợ, n&agrave;ng đang hướng v&aacute;ch n&uacute;i rơi xuống nha! N&agrave;ng vừa mới tử một hồi. Như thế n&agrave;o như vậy suy? Quy&ecirc;n đi, n&agrave;ng nhận mệnh,&nbsp;&nbsp;d&ugrave; sao cũng chết một lần rồi. Nhắm mắt lại, chờ đợi tử vong bu&ocirc;ng xuống. Nhưng n&agrave;ng lại bị một người &ocirc;m lấy, c&aacute;i &ocirc;m n&agrave;y cải biến vận mệnh n&agrave;ng về sau. Từ nay về sau n&agrave;ng liền c&oacute; cha- Kh&uacute;c Vệ Cổ X&iacute;ch Gi&aacute;o bang chủ. Cũng c&oacute; nương &mdash; Trữ Mị T&acirc;m Cổ X&iacute;ch Gi&aacute;o phu nh&acirc;n. Hai năm sau, n&agrave;ng c&oacute; một c&aacute;i muội muội &ndash; Trữ Nhi Ph&agrave;m Cổ X&iacute;ch Gi&aacute;o nhị tiểu thư. N&agrave;ng cũng c&oacute; t&ecirc;n Kh&uacute;c Huyễn Y&ecirc;n.<br />\r\n<br />\r\nN&agrave;ng c&ugrave;ng muội muội Trữ Nhi Ph&agrave;m ở Cổ X&iacute;ch Gi&aacute;o kho&aacute;i hoạt trưởng th&agrave;nh. C&aacute;c n&agrave;ng luyện v&otilde;, luyện cầm, ng&acirc;m thơ, đọc s&aacute;ch, vẽ tranh, chơi cờ&hellip; Cầm kỳ thi hoạ- mọi thứ đều tinh th&ocirc;ng ch&iacute;nh l&agrave; muội muội n&agrave;ng. Bởi v&igrave; kh&ocirc;ng th&iacute;ch học cầm k&igrave; thi hoạ n&ecirc;n n&agrave;ng kh&ocirc;ng chăm học, n&agrave;ng chịu kh&oacute; học chỉ sợ l&agrave; bởi v&igrave; ở thế kỷ 21 n&agrave;ng kh&ocirc;ng hảo hảo học tập cho n&ecirc;n hiện tại nghĩ kỹ n&agrave;ng một lần nữa nghe lời, ngoan ngo&atilde;n học đi. Muội muội v&otilde; c&ocirc;ng so với n&agrave;ng học kh&aacute; hơn, thời điểm luận v&otilde; với muội muội, n&agrave;ng lu&ocirc;n tho&aacute;ng c&oacute; điểm chật vật.<br />\r\n<br />\r\nN&agrave;ng thời thơ ấu cứ như vậy hạnh ph&uacute;c tr&ocirc;i qua.<br />\r\n<br />\r\nThẳng đến khi ấy, n&agrave;ng 15 tuổi Cổ X&iacute;ch Gi&aacute;o gi&uacute;p triều đ&igrave;nh đại chiến. Địch qu&acirc;n thế lực cường đại, Cổ X&iacute;ch Gi&aacute;o cuối c&ugrave;ng chiến bại. Trước đ&oacute;, Kh&uacute;c Vệ đem n&agrave;ng v&agrave; muội muội đưa ra khỏi Cổ X&iacute;ch Gi&aacute;o để c&aacute;c n&agrave;ng hảo hảo sống s&oacute;t&hellip; Cứ như vậy&hellip; C&aacute;c n&agrave;ng lựa chọn ở lại Thanh L&acirc;u sinh sống&hellip;</p>', NULL, 15, 4, '2020-09-08 10:51:12', '2020-09-08 10:51:42'),
(34, 'Thông Bối Quyền', 'Chương 2', '<p>S&aacute;ng sớm, sương m&ugrave; d&agrave;y đặc bao phủ cả ngọn n&uacute;i tĩnh lặng n&agrave;y, l&agrave;m tầm mắt của mỗi người trở n&ecirc;n nh&igrave;n kh&ocirc;ng r&otilde;.<br />\r\n<br />\r\n<em>&quot;H&ocirc; ~ h&ocirc; ~&quot;</em><br />\r\n<br />\r\nTrong một khu rừng ở ph&iacute;a sau n&uacute;i, đột nhi&ecirc;n truyền ra tiếng thở dốc kịch liệt, đưa tầm mắt đến gần c&oacute; thể nh&igrave;n thấy tr&ecirc;n một khu đất trống ở trong rừng, một th&acirc;n ảnh nho nhỏ đang d&ugrave;ng cả hai tay &ocirc;m lấy một th&acirc;n c&acirc;y cao lớn, th&acirc;n thể hắn mượn lực k&eacute;o của hai tay, kh&ocirc;ng ngừng nhấp nh&ocirc; l&ecirc;n xuống, hơn nữa trong l&uacute;c l&ecirc;n xuống, th&acirc;n thể hắn b&agrave;y ra một loại tư thế c&oacute; ch&uacute;t qu&aacute;i dị, loại tư thế n&agrave;y l&agrave;m cho khắp cơ thể đều vận động c&ugrave;ng l&uacute;c.<br />\r\n<br />\r\nMặt kh&aacute;c, ngay tại c&aacute;nh tay v&agrave; cổ ch&acirc;n của hắn c&ograve;n treo mấy khối sắt đen kịt, từng giọt mồ h&ocirc;i thẫm thấu ra từ trong quần &aacute;o, lan tr&agrave;n khắp cả mấy khối sắt kia.<br />\r\n<br />\r\nTh&acirc;n ảnh nho nhỏ n&agrave;y tất nhi&ecirc;n l&agrave; L&acirc;m Động, tuổi thơ của hắn chưa từng hưởng qua ph&uacute; qu&yacute; bao giờ, hơn nữa lại c&oacute; một phụ th&acirc;n nghi&ecirc;m khắc lu&ocirc;n dạy hắn những thứ quan trọng nhất trong tu luyện - nghị lực v&agrave; chăm chỉ, những thứ n&agrave;y ch&iacute;nh l&agrave; tiền vốn v&agrave; cũng l&agrave; điều kiện duy nhất hắn c&oacute; để cạnh tranh với những người kh&aacute;c.<br />\r\n<br />\r\nMồ h&ocirc;i chảy v&agrave;o trong mắt, cảm gi&aacute;c ch&aacute;t v&agrave; x&oacute;t l&agrave;m hắn cắn chặt h&agrave;m răng, hắn c&oacute; thể cảm gi&aacute;c được sau khi trải qua luyện tập cao độ, cả cơ thể hắn ph&aacute;t ra loại cảm gi&aacute;c &ecirc; ẩm v&agrave; mỏi mệt, l&uacute;c n&agrave;y rất nhiều người sẽ lựa chọn nghỉ ngơi, nhưng m&agrave; phụ th&acirc;n hắn từng n&oacute;i cho hắn biết, chỉ c&oacute; tại loại thời điểm như vậy mới c&oacute; thể đột ph&aacute; cực hạn n&ecirc;n... nhất định phải ki&ecirc;n tr&igrave;!<br />\r\n<br />\r\nTuyệt đối kh&ocirc;ng thể thả lỏng!<br />\r\n<br />\r\nV&igrave; tộc hội nửa năm sau, hắn buộc phải liều mạng tu luyện!</p>\r\n\r\n<p>Cảm gi&aacute;c tiếp cận cực hạn l&agrave;m đầu cho&aacute;ng v&aacute;ng, hoa mắt, bất qu&aacute; c&ugrave;ng với sự ki&ecirc;n tr&igrave; của L&acirc;m Động, đột nhi&ecirc;n một tia nhiệt lực kỳ dị mạnh mẽ xuất hiện trong cơ thể, nhiệt lực n&agrave;y vừa xuất hiện l&agrave;m tinh thần L&acirc;m Động rung l&ecirc;n.<br />\r\n<br />\r\nL&agrave; dược lực của X&iacute;ch Tham!<br />\r\n<br />\r\nMấy ng&agrave;y trước, gốc X&iacute;ch Tham kia vốn d&ugrave;ng để trị liệu thương thế cho L&acirc;m Khiếu, dưới sự ki&ecirc;n tr&igrave; của hắn, cuối c&ugrave;ng đều bị đưa v&agrave;o trong cơ thể L&acirc;m Động.<br />\r\n<br />\r\nKh&ocirc;ng thể kh&ocirc;ng n&oacute;i, loại linh dược n&agrave;y c&oacute; hiệu quả rất lớn với L&acirc;m Động đang bị v&acirc;y trong Thối Thể kỳ, tu luyện mấy ng&agrave;y ngắn ngủn m&agrave; L&acirc;m Động c&oacute; thể cảm gi&aacute;c r&otilde; r&agrave;ng th&acirc;n thể hắn &iacute;t nhất mạnh gấp đ&ocirc;i so với mấy ng&agrave;y trước. c&aacute;i loại th&acirc;n thể cường h&oacute;a n&agrave;y hắn c&oacute; thể cảm gi&aacute;c được rất r&otilde; r&agrave;ng.<br />\r\n<br />\r\nĐương nhi&ecirc;n, dược lực X&iacute;ch Tham c&oacute; hiệu quả rất tốt với L&acirc;m Động, nhưng loại dược lực n&agrave;y d&ugrave; sao cũng kh&ocirc;ng phải v&ocirc; c&ugrave;ng v&ocirc; tận, d&ugrave; sao X&iacute;ch Tham cũng vẻn vẹn chỉ l&agrave; nhất phẩm linh dược m&agrave; th&ocirc;i, bởi vậy dược lực của n&oacute; ph&aacute;t huy trong năm ng&agrave;y rồi dần dần tản ra.<br />\r\n<br />\r\nBất qu&aacute; loại tản ra n&agrave;y kh&ocirc;ng phải ho&agrave;n to&agrave;n biến mất, th&acirc;n thể L&acirc;m Động chỉ mới bắt đầu cường h&oacute;a, tất nhi&ecirc;n kh&ocirc;ng thể ho&agrave;n to&agrave;n hấp thu tất cả dược lực được, bởi vậy một &iacute;t dược lực sẽ lắng đọng s&acirc;u trong th&acirc;n thể hắn, nhưng đến khi cơ thể L&acirc;m Động đạt đến đ&oacute;i kh&aacute;t cực độ, loại lắng đọng n&agrave;y cuối c&ugrave;ng cũng bị bức ra, bị g&acirc;n cốt cơ thể L&acirc;m Động giống như quỷ đ&oacute;i tham lam cắn nuốt...<br />\r\n<br />\r\nMột tia dược lực &ocirc;n nhuận chậm r&atilde;i tiến v&agrave;o cơ thể, l&uacute;c n&agrave;y L&acirc;m Động như nghe được v&ocirc; số tiếng reo h&ograve; từ tế b&agrave;o trong cơ thể, loại cảm gi&aacute;c &ecirc; ẩm mệt mỏi y&ecirc;n lặng ti&ecirc;u t&aacute;n đi, thay v&agrave;o đ&oacute; l&agrave; một loại cảm gi&aacute;c tinh lực dư thừa v&ocirc; c&ugrave;ng v&ocirc; tận.<br />\r\n<br />\r\n<em>&quot;Hắc...&quot;</em><br />\r\n<br />\r\nL&acirc;m Động nắm lấy th&acirc;n c&acirc;y, th&acirc;n thể mạnh mẽ b&uacute;ng l&ecirc;n, lăng kh&ocirc;ng lộn ra ph&iacute;a sau, vững v&agrave;ng đứng tr&ecirc;n mặt đất, xoay lưng lắc cổ, xương cốt cả người đột nhi&ecirc;n vang l&ecirc;n một tiếng răng rắc, sau đ&oacute; L&acirc;m Động vui sướng ph&aacute;t hiện th&acirc;n thể hắn tựa hồ tho&aacute;ng cường tr&aacute;ng hơn.<br />\r\n<br />\r\n<em>&quot;Thối Thể đệ tam trọng!&quot;</em><br />\r\n<br />\r\nNắm tay nhỏ b&eacute; L&acirc;m Động nắm chặt lại, cảm thụ được cỗ lực lượng xuất hiện khi gi&atilde;n cơ thể ra, khu&ocirc;n mặt nhỏ nhắn lộ vẻ sợ h&atilde;i xen lẫn vui mừng, từ khi tu luyện đến nay, bởi gia cảnh ngh&egrave;o h&agrave;n n&ecirc;n hắn rất &iacute;t c&oacute; cơ hội d&ugrave;ng linh dược, kh&ocirc;ng ngờ lần n&agrave;y d&ugrave;ng thử lại c&oacute; hiệu quả lớn đến vậy.<br />\r\n<br />\r\n<em>&quot;Ừm, cũng kh&ocirc;ng tệ lắm...&quot;</em><br />\r\n<br />\r\nKhi L&acirc;m Động đang vui sướng v&igrave; c&ocirc;ng lực tăng tiến, một &acirc;m thanh truyền đến từ b&ecirc;n cạnh, hắn vội v&agrave;ng quay đầu, vừa l&uacute;c nh&igrave;n thấy L&acirc;m Khiếu mặc một th&acirc;n quần &aacute;o mộc mạc đang đứng dưới một đại thụ, tr&ecirc;n khu&ocirc;n mặt ng&agrave;y thường kh&ocirc;ng lộ ra nửa tia cười vui l&uacute;c n&agrave;y cũng xuất hiện một ch&uacute;t tươi cười.<br />\r\n<br />\r\n<em>&quot;Cha!&quot;</em><br />\r\n<br />\r\nNh&igrave;n thấy L&acirc;m Khiếu, L&acirc;m Động cũng kinh hỉ k&ecirc;u l&ecirc;n.</p>', NULL, 6, 1, '2020-09-08 10:59:15', NULL),
(35, 'Tai nghe là giả', 'Chương 2', '<p>B&oacute;ng đ&ecirc;m th&acirc;m trầm, lục m&ocirc;n khắc hoa khắc cỏ ch&igrave;m dưới &aacute;nh trăng s&aacute;ng tỏ phủ xuống, lộ ra vẻ an b&igrave;nh tĩnh mịch.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khương Tự gật đầu với A Man một c&aacute;i, nhẹ giọng n&oacute;i: &quot; Đi th&ocirc;i.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>A Man được ph&acirc;n ph&oacute;, từ trong t&uacute;i tiền b&ecirc;n h&ocirc;ng lấy ra một c&aacute;i ch&igrave;a kh&oacute;a, nhẹ ch&acirc;n nhẹ tay đi ra ph&iacute;a trước mở kh&oacute;a.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Theo ch&igrave;a kho&aacute; nhẹ nh&agrave;ng chuyển động, kh&oacute;a cửa ph&aacute;t ra tiếng tạch tạch rất nhỏ, đồng thời mở ra.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&ograve;ng b&agrave;n tay A Man cầm ch&igrave;a kho&aacute; đ&atilde; ươn ướt, nhẹ nh&agrave;ng thở ra đồng thời chỉ cảm thấy tim đập như sấm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khương Tự thấy thế, lộ ra nụ cười nhạt.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trước đ&oacute; kh&ocirc;ng l&acirc;u n&agrave;ng ph&acirc;n ph&oacute; A Man uống rượu c&ugrave;ng b&agrave; tử quản ch&igrave;a kh&oacute;a nhị m&ocirc;n, đợi khi b&agrave; tử uống đ&atilde; nhiều, th&igrave; thừa cơ t&igrave;m ch&igrave;a kho&aacute; rồi ấn mấy c&aacute;i l&ecirc;n mấy cục x&agrave; b&ocirc;ng thơm đ&atilde; chuẩn bị sẵn, c&acirc;̀m ra b&ecirc;n ngo&agrave;i đ&aacute;nh mấy c&aacute;i ch&igrave;a kho&aacute; mới.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chỉ c&oacute; điều tạo ra ch&igrave;a kho&aacute; như vậy c&oacute; thể mở cửa hay kh&ocirc;ng to&agrave;n bộ c&ograve;n nhờ v&agrave;o vận kh&iacute;, cũng may trong năm c&aacute;i ch&igrave;a kho&aacute; cuối c&ugrave;ng cũng c&oacute; một ch&igrave;a l&agrave; c&oacute; thể mở được.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>A Man từng ch&uacute;t từng ch&uacute;t đẩy cửa ra, hai mắt l&oacute;e s&aacute;ng :&quot; C&ocirc; nương --&quot;</p>\r\n\r\n<p>L&uacute;c n&agrave;y bỗng nhi&ecirc;n nghe được một tiếng cọt kẹt vang l&ecirc;n, ở trong m&agrave;n đ&ecirc;m chỉ nghe thấy tiếng c&ocirc;n tr&ugrave;ng k&ecirc;u vang th&igrave; c&oacute; vẻ ph&aacute; lệ ch&oacute;i tai.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chủ tớ hai người nh&igrave;n nhau, đều nh&igrave;n tới trong mắt đối phương vẻ hoảng sợ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khương Tự rất nhanh phản ứng lại, dắt A Man c&uacute;i thấp người lướt đi, đột nhi&ecirc;n nh&igrave;n thấy b&agrave; tử canh cửa đi tới, xoa xoa mắt đi hướng nh&agrave; x&iacute;, lại kh&ocirc;ng hề nh&igrave;n về b&ecirc;n n&agrave;y ch&uacute;t n&agrave;o.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khương Tự vội chui qua cửa h&ocirc;ng, A Man theo s&aacute;t ph&iacute;a sau, nhẹ nh&agrave;ng đ&oacute;ng cửa lại.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Sợ b&oacute;ng sợ gi&oacute; qua đi, A Man lộ ra nụ cười may mắn: &quot; Nguy hiểm thật!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khương Tự đ&atilde; chỉnh l&yacute; tốt t&acirc;m t&igrave;nh, thản nhi&ecirc;n n&oacute;i: &quot; Kh&ocirc;ng n&ecirc;n nhiều lời, mau mau đi th&ocirc;i.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chủ tớ hai người men theo g&oacute;c tường đi về ph&iacute;a trước, sau khi đi ước chừng thời gian uống cạn ch&eacute;n tr&agrave; nhỏ Khương Tự bỗng nhi&ecirc;n ngừng lại.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>A Man ngắm nh&igrave;n bốn ph&iacute;a, c&oacute; ch&uacute;t mờ mịt: &quot; C&ocirc; nương, ch&uacute;ng ta l&agrave;m sao ra ngo&agrave;i nha?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>N&agrave;ng có th&ecirc;̉ nghĩ c&aacute;ch lấy được ch&igrave;a kh&oacute;a mở nhị m&ocirc;n, nhưng đại m&ocirc;n th&igrave; lại kh&ocirc;ng thể, cũng kh&ocirc;ng c&oacute; đạo l&yacute; thiếp th&acirc;n nha ho&agrave;n của c&ocirc; nương lại đi t&igrave;m l&atilde;o đầu g&aacute;c cổng uống rượu được.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot; Đi theo ta. &quot; Khương Tự v&ograve;ng qua một l&ugrave;m hoa mộc, xoay người đẩy ra cỏ xanh tươi tốt ở g&oacute;c tường, th&igrave;nh l&igrave;nh lộ ra một c&aacute;i cửa hang.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>A Man đột nhi&ecirc;n mở to hai mắt: &quot; C&ocirc; nương, nơi n&agrave;y sao lại c&oacute; một c&aacute;i hang?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khương Tự cũng kh&ocirc;ng trả lời, m&agrave; l&agrave; c&uacute;i người từ cửa hang chui ra ngo&agrave;i, bị gi&oacute; đ&ecirc;m ngo&agrave;i phủ thổi đến, ngước nh&igrave;n bầu trời đ&ecirc;m c&oacute; một l&aacute;t xuất thần.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khi đ&oacute;, huynh trưởng Khương Trạm ở trong mắt n&agrave;ng l&agrave; một người kh&ocirc;ng học vấn kh&ocirc;ng nghề nghiệp, n&agrave;ng vẫn lu&ocirc;n xa c&aacute;ch hắn, c&oacute; một lần ngẫu nhi&ecirc;n nh&igrave;n thấy hắn b&ograve; ra từ trong c&aacute;i hang n&agrave;y, hiển nhi&ecirc;n l&agrave; chuồn &ecirc;m ra ngo&agrave;i chơi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>N&agrave;ng l&uacute;c ấy chỉ cười lạnh một tiếng, đối với hắn c&agrave;ng th&ecirc;m coi thường, thậm ch&iacute; ngay cả t&acirc;m tư th&ocirc;ng b&aacute;o với quản sự lấp c&aacute;i hang n&agrave;y lại cũng kh&ocirc;ng c&oacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Dưới c&aacute;i nh&igrave;n của n&agrave;ng, huynh trưởng của n&agrave;ng ch&iacute;nh l&agrave; b&ugrave;n nh&atilde;o kh&ocirc;ng đỡ nổi tường, đ&atilde; kh&ocirc;ng c&ograve;n bất kỳ điều g&igrave; cứu v&atilde;n nữa, c&ograve;n kh&ocirc;ng bằng tr&aacute;nh xa ch&uacute;t cho thanh tĩnh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thế nhưng Khương Trạm lại chết v&agrave;o cuối m&ugrave;a thu sau khi n&agrave;ng xuất gi&aacute;, khi nghe tin dữ n&agrave;ng mới th&igrave;nh l&igrave;nh ph&aacute;t hiện h&oacute;a ra n&agrave;ng cũng sẽ thương t&acirc;m.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Vị huynh trưởng m&agrave; d&ugrave; sau khi bị phụ th&acirc;n d&ugrave;ng roi quất cho một trận vẫn cất c&ocirc;ng đem b&aacute;nh ngọt hoa hồng li&ecirc;n dung mua được tr&ecirc;n đường tới cho n&agrave;ng đ&atilde; kh&ocirc;ng c&ograve;n nữa rồi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot; C&ocirc; nương -- &quot; A Man từ cửa hang chui ra ngo&agrave;i nh&igrave;n thấy Khương Tự xuất thần, khẽ gọi một tiếng.</p>\r\n\r\n<p>Khương Tự thu hồi suy nghĩ, tự giễu cười một tiếng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khi đ&oacute; m&igrave;nh thật sự l&agrave; mỡ heo l&agrave;m t&acirc;m tr&iacute; m&ecirc; muội, Đại đường huynh nh&agrave; Nhị th&uacute;c d&ugrave; xuất sắc vượt trội cũng sẽ kh&ocirc;ng cho n&agrave;ng b&aacute;nh ngọt hoa hồng li&ecirc;n dung, m&agrave; huynh trưởng của n&agrave;ng d&ugrave; kh&ocirc;ng biết cố gắng thế n&agrave;o, th&igrave; trong l&ograve;ng r&otilde; r&agrave;ng rất y&ecirc;u thương n&agrave;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot; Đi th&ocirc;i. &quot; Khương Tự kh&ocirc;i phục b&igrave;nh tĩnh, t&iacute;nh to&aacute;n thời gian một ch&uacute;t, mang theo A Man bước nhanh về hướng hồ Mạc Ưu.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cũng may đương triều hủy bỏ lệnh cấm đi lại ban đ&ecirc;m, m&agrave; hồ Mạc Ưu c&ugrave;ng phủ Đ&ocirc;ng B&igrave;nh B&aacute; đều ở th&agrave;nh t&acirc;y, n&ecirc;n rất thuận tiện cho Khương Tự.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chủ tớ hai người vội v&agrave;ng đuổi tới nơi đ&oacute;, mượn &aacute;nh trăng s&aacute;ng tỏ, từ xa liền thấy được một đ&ocirc;i th&acirc;n ảnh đứng lặng ở b&ecirc;n hồ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>A Man l&uacute;c ấy liền kinh ngạc, hạ giọng n&oacute;i: &quot; C&ocirc; nương, thật sự c&oacute; người!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khương Tự mặt kh&ocirc;ng biểu t&igrave;nh chỉ chỉ một khối đ&aacute; cứng viết ba chữ to &quot; Mạc Ưu Hồ &quot; đứng sừng sững b&ecirc;n bờ hồ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khối đ&aacute; kia cao chừng nửa trượng, người tr&ocirc;́n ở sau tảng đ&aacute; dư sức.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>A Man hiểu &yacute;, đi theo Khương Tự n&uacute;p ở chỗ kia.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khương Tự tay vịn v&aacute;ch đ&aacute;, trong l&ograve;ng b&agrave;n tay truyền đến ấm &aacute;p nh&agrave;n nhạt, l&agrave; hơi n&oacute;ng m&agrave; ban ng&agrave;y v&aacute;ch đ&aacute; t&iacute;ch tụ c&ograve;n chưa ti&ecirc;u tan.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Rất nhanh liền c&oacute; tiếng kh&oacute;c nức nở theo l&agrave;n gi&oacute; b&ecirc;n hồ thổi đến, Khương Tự nhịn kh&ocirc;ng được nh&igrave;n qua.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ánh trăng s&aacute;ng ngời, soi s&aacute;ng d&aacute;ng vẻ của hai người.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nam tử th&acirc;n h&igrave;nh hơi gầy, cao hơn nữ tử gần một c&aacute;i đầu, ch&iacute;nh l&agrave; vị h&ocirc;n phu Qu&yacute; S&ugrave;ng Dịch của Khương Tự.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&Aacute;nh mắt Khương Tự từ tr&ecirc;n khu&ocirc;n mặt tuấn mỹ của Qu&yacute; S&ugrave;ng Dịch lướt qua, rơi v&agrave;o tr&ecirc;n mặt nữ tử.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>N&agrave;ng vẫn lu&ocirc;n rất t&ograve; m&ograve;, vị nữ tử c&oacute; thể l&agrave;m cho Qu&yacute; S&ugrave;ng Dịch giữ g&igrave;n kh&ocirc;ng động v&agrave;o vị th&ecirc; tử t&acirc;n h&ocirc;n hoa như ngọc lấy một ch&uacute;t rốt cuộc tr&ocirc;ng như thế n&agrave;o.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&uacute;c n&agrave;ng gả qua, th&igrave; nữ tử kia đ&atilde; kh&ocirc;ng c&ograve;n tr&ecirc;n nh&acirc;n thế, cho đến tối nay mới c&oacute; cơ hội d&ograve;m ng&oacute; ch&acirc;n dung.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nữ tử d&aacute;ng người nhỏ nhắn xinh xắn, dưới h&agrave;ng m&agrave;y liễu l&agrave; một đ&ocirc;i mắt to ngậm sương m&ugrave;, cứ việc đứng dưới ánh trăng, nhưng vẫn có th&ecirc;̉ nh&igrave;n ra được m&agrave;u da kh&ocirc;ng t&iacute;nh l&agrave; trắng n&otilde;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>T&acirc;m t&igrave;nh của Khương Tự trong nh&aacute;y mắt c&oacute; ch&uacute;t phức tạp.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>B&igrave;nh tĩnh m&agrave; xem x&eacute;t, th&igrave; nữ tử n&agrave;y cũng được coi l&agrave; mi thanh mục t&uacute; tiểu gia b&iacute;ch ngọc, nhưng so với tiểu mỹ nh&acirc;n đứng đầu vẫn c&oacute; ch&ecirc;nh lệch rất lớn, hơn nữa n&agrave;ng về sau c&ograve;n nghe n&oacute;i vị d&acirc;n nữ n&agrave;y cũng kh&ocirc;ng đọc qua s&aacute;ch vở g&igrave;......</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&Aacute;nh mắt Khương Tự lần nữa chuyển sang mặt của Qu&yacute; S&ugrave;ng Dịch, nh&igrave;n thấy tr&ecirc;n mặt hắn r&otilde; r&agrave;ng c&oacute; sự thống khổ c&ugrave;ng n&ocirc;n n&oacute;ng, kh&ocirc;ng thể kh&ocirc;ng thừa nhận, n&agrave;ng đ&acirc;y l&agrave; bại bởi t&igrave;nh y&ecirc;u ch&acirc;n th&agrave;nh nha.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot; Dịch lang, ch&agrave;ng, ch&agrave;ng mau hồi phủ đi, đ&atilde; khuya lắm rồi, nếu như bị ph&aacute;t hiện sẽ phiền to&aacute;i đ&oacute;. &quot; Nữ tử c&uacute;i đầu, thanh &acirc;m mang theo nghẹn ng&agrave;o.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&yacute; S&ugrave;ng Dịch vươn tay đỡ lấy hai vai nữ tử, ngữ kh&iacute; k&iacute;ch động: &quot; Ta kh&ocirc;ng đi. Xảo Nương, n&agrave;ng chẳng lẽ kh&ocirc;ng biết ta sắp th&agrave;nh h&ocirc;n? Trong nh&agrave; vốn dĩ đ&atilde; nh&igrave;n ta chằm chằm, ta đi lần n&agrave;y chỉ sợ đến trước khi th&agrave;nh th&acirc;n sẽ kh&ocirc;ng gặp được n&agrave;ng nữa......&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&Aacute;nh mắt Khương Tự đột nhi&ecirc;n chuyển sang lạnh lẽo.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>H&oacute;a ra người trong l&ograve;ng Qu&yacute; S&ugrave;ng Dịch k&ecirc;u Xảo Nương.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tr&aacute;ch kh&ocirc;ng được sau ng&agrave;y thứ hai bọn họ th&agrave;nh th&acirc;n A Xảo tiến v&agrave;o hầu hạ n&agrave;ng, nghe n&agrave;ng gọi một tiếng &#39; A Xảo &#39;, th&igrave; &aacute;nh mắt của Qu&yacute; S&ugrave;ng Dịch liền như đao qu&eacute;t đến, sau đ&oacute; phẩy tay &aacute;o bỏ đi, ngay cả k&iacute;nh tr&agrave; cũng đến trễ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Phu nh&acirc;n An Quốc C&ocirc;ng, cũng ch&iacute;nh l&agrave; mẹ chồng n&agrave;ng đương nhi&ecirc;n sẽ kh&ocirc;ng tr&aacute;ch tội con trai m&igrave;nh, lại cho rằng l&agrave; n&agrave;ng kh&ocirc;ng hiểu cấp bậc lễ nghĩa, l&uacute;c k&iacute;nh tr&agrave; cố t&igrave;nh l&agrave;m kh&oacute; n&agrave;ng một phen mới coi như bỏ qua.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Xảo Nương th&ecirc; lương cười một tiếng: &quot; Hiện tại kh&ocirc;ng đi th&igrave; thế n&agrave;o? Dịch lang, ch&agrave;ng vẫn phải về nh&agrave;, thời gian sớm hay muộn đối với ch&uacute;ng ta m&agrave; n&oacute;i c&oacute; g&igrave; kh&aacute;c nhau đ&acirc;u? Về phần sau n&agrave;y...... Nếu ch&agrave;ng th&agrave;nh th&acirc;n th&igrave; h&atilde;y đối tốt với th&ecirc; tử của ch&agrave;ng, qu&ecirc;n ta đi, ta, ta cũng sẽ qu&ecirc;n ch&agrave;ng --&quot;</p>', NULL, 8, 1, '2020-09-08 11:02:58', '2020-09-08 11:03:14'),
(36, 'Lâm Động', 'Chương 1', '<p><em>A...&quot;</em><br />\r\n<br />\r\nKhi L&acirc;m Động hao hết kh&iacute; lực mở ra đ&ocirc;i mi nặng trĩu, nhất thời căn ph&ograve;ng đơn sơ m&agrave; sạch sẽ xuất hiện trong mắt, h&igrave;nh ảnh quen thuộc l&agrave;m hắn ngẩn người ra, chợt vội quay đầu lại, quả nhi&ecirc;n nh&igrave;n thấy hai đạo th&acirc;n ảnh, một nam một nữ ngồi b&ecirc;n cạnh b&agrave;n.<br />\r\n<br />\r\n<em>&quot;Cha, mẹ&nbsp;...&quot;</em><br />\r\n<br />\r\nNh&igrave;n hai đạo th&acirc;n ảnh kia, L&acirc;m Động vội gắng gượng tinh thần, nhỏ giọng gọi.<br />\r\n<br />\r\n<em>&quot;Động nhi, con tỉnh?&quot;</em><br />\r\n<br />\r\nNghe thấy tiếng k&ecirc;u, n&agrave;ng kia quay đầu sang, nh&igrave;n thấy L&acirc;m Động vừa mở mắt, nhất thời vui sướng n&oacute;i.<br />\r\n<br />\r\nCả người nữ tử kia c&oacute; ch&uacute;t mộc mạc, nh&igrave;n qua chừng ba mươi, khu&ocirc;n mặt hơi thanh t&uacute; l&agrave;m người ta c&oacute; loại cảm gi&aacute;c dịu d&agrave;ng nhu h&ograve;a, m&agrave; n&agrave;ng đ&uacute;ng l&agrave; mẫu th&acirc;n L&acirc;m Động - Liễu Nghi&ecirc;n.<br />\r\n<br />\r\n<em>&quot;Học nghệ chưa tinh m&agrave; đ&atilde; đ&aacute;nh nhau với người ta, tự chuốc lấy khổ.&quot;</em><br />\r\n<br />\r\nNgồi b&ecirc;n cạnh nữ tử l&agrave; một vị nam tử nh&igrave;n qua chừng ba bốn mươi tuổi, th&acirc;n thể c&oacute; ch&uacute;t đơn bạc, c&oacute; thể mơ hồ nh&igrave;n thấy một ch&uacute;t sắc b&eacute;n trong &aacute;nh mắt hắn, chẳng qua giống như c&oacute; thương t&iacute;ch trong người, khu&ocirc;n mặt hơi c&oacute; vẻ t&aacute;i nhợt, che lấp hơn nửa tia sắc b&eacute;n kia, hắn l&agrave; phụ th&acirc;n L&acirc;m Động - L&acirc;m Khiếu.<br />\r\n<br />\r\nL&acirc;m Động hiển nhi&ecirc;n c&oacute; ch&uacute;t e ngại với vị phụ th&acirc;n xưa nay nghi&ecirc;m khắc n&agrave;y, rụt cổ lại, chợt lại c&oacute; ch&uacute;t kh&ocirc;ng phục n&oacute;i:&nbsp;<em>&quot;Ai bảo những t&ecirc;n kia đứng trước mặt ta mắng cha l&agrave; phế vật...&quot;</em></p>', NULL, 6, 1, '2020-09-10 07:48:26', NULL),
(37, 'Test', 'Chương 2', '<p>asdasd</p>', NULL, 11, 1, '2020-09-10 07:49:50', NULL);
INSERT INTO `chapters` (`id`, `name`, `subname`, `content`, `view`, `story_id`, `user_id`, `created_at`, `updated_at`) VALUES
(38, 'Vòng tay của quỷ', 'Chương 1', '<p><em>(Truyện ngắn nằm trong series truyện ngắn kinh dị L&atilde;nh Đạm V&agrave; Chết Ch&oacute;c của t&aacute;c giả Ngạ Quỷ)</em><br />\r\n<br />\r\n&Ocirc;ng B&igrave;nh chăm ch&uacute; theo d&otilde;i v&agrave;o m&agrave;n h&igrave;nh ti vi v&agrave; thi thoảng lại quay xuống nh&igrave;n chiếc đồng hồ đeo tay.<br />\r\n<br />\r\nMười một giờ đ&ecirc;m m&agrave; Trọng vẫn chưa về, th&ocirc;ng thường &ocirc;ng chưa từng thấy Trọng đi đ&ecirc;m về muộn như vậy.<br />\r\n<br />\r\n***<br />\r\n<br />\r\nKể đến Trọng th&igrave; phải n&oacute;i, hắn l&agrave; một người th&agrave;nh đạt giỏi giang, với c&ocirc;ng việc văn ph&ograve;ng trong một tập đo&agrave;n lớn. Hắn ta chưa lấy vợ, cũng như chưa c&oacute; &yacute; định lấy vợ v&igrave; l&yacute; do qu&aacute; trẻ. Kể từ khi đi l&agrave;m cho đến nay đ&atilde; l&agrave; năm năm. Nhưng Trọng l&uacute;c n&agrave;o cũng kh&ocirc;ng th&iacute;ch t&aacute;ch biệt ra ngo&agrave;i sống v&igrave; vẫn c&ograve;n kh&ocirc;ng muốn xa bố mẹ.<br />\r\n<br />\r\nTheo th&oacute;i quen, Trọng thường ra ngo&agrave;i tản bộ v&agrave;o l&uacute;c bảy giờ tối, v&agrave; th&ocirc;ng thường th&igrave; sẽ trở về nh&agrave; v&agrave;o l&uacute;c ch&iacute;n giờ. Đ&acirc;y l&agrave; lần đầu ti&ecirc;n &ocirc;ng B&igrave;nh thấy Trọng đ&atilde; muộn như vậy m&agrave; vẫn chưa về.<br />\r\n<br />\r\nĐ&ecirc;m nay, đ&atilde; hai lần &ocirc;ng gọi điện thoại cho Trọng, m&agrave; hồi chu&ocirc;ng điện thoại vang l&ecirc;n chỉ được một l&uacute;c rồi lại tắt ngấm.<br />\r\n<br />\r\nMột l&uacute;c sau, khi &ocirc;ng B&igrave;nh toan đứng l&ecirc;n đi ra ngo&agrave;i t&igrave;m con. Th&igrave; đột nhi&ecirc;n tiếng chốt cửa từ b&ecirc;n ngo&agrave;i lại bắt đầu vang l&ecirc;n c&agrave;nh cạch. &Ocirc;ng B&igrave;nh hơi khẩn trương, cũng c&oacute; cảm gi&aacute;c nhẹ nh&otilde;m ngồi xuống ghế v&igrave; biết l&agrave; Trọng đ&atilde; về.<br />\r\n<br />\r\nTrọng từ b&ecirc;n ngo&agrave;i bước v&agrave;o trong nh&agrave;, to&agrave;n th&acirc;n ướt sũng với đ&ocirc;i mắt hết sức kỳ lạ. Lạ ở chỗ l&agrave; đ&ocirc;i mắt h&ocirc;m nay của hắn đờ đẫn như kh&ocirc;ng c&oacute; hồn. Lạ ở chỗ nữa l&agrave; hắn đi thẳng v&agrave;o ph&ograve;ng m&igrave;nh m&agrave; kh&ocirc;ng hề ch&agrave;o &ocirc;ng B&igrave;nh một tiếng.<br />\r\n<br />\r\nMọi lần, Trọng từ b&ecirc;n ngo&agrave;i tản bộ về nh&agrave;, đều sẽ đi qua tr&ograve; chuyện với &ocirc;ng B&igrave;nh một ch&uacute;t. Nhưng h&ocirc;m nay hắn kh&ocirc;ng l&agrave;m như vậy, tại sao lại như vậy? L&agrave; bởi v&igrave; hắn nhất định đang c&oacute; chuyện giấu giếm, c&oacute; thể l&agrave; một b&iacute; mật n&agrave;o đ&oacute; chăng?<br />\r\n<br />\r\n&Ocirc;ng B&igrave;nh suy nghĩ hồi l&acirc;u, lặng lẽ đi tới gần cửa để kh&eacute;p c&aacute;nh cửa nh&agrave; lại, v&igrave; khi n&atilde;y khi Trọng bước v&agrave;o, hắn đ&atilde; v&ocirc; t&igrave;nh qu&ecirc;n kh&ocirc;ng đ&oacute;ng c&aacute;nh cửa ấy.<br />\r\n<br />\r\nKhi chạm tay v&agrave;o tay cầm của c&aacute;nh cửa gỗ, &ocirc;ng B&igrave;nh đột nhi&ecirc;n c&oacute; một loại cảm gi&aacute;c, giống như m&igrave;nh đ&atilde; vừa chạm phải một thứ g&igrave; đ&oacute; nhớp nh&aacute;p tanh tưởi. Một m&ugrave;i tanh nồng bốc l&ecirc;n xộc v&agrave;o mũi của &ocirc;ng. &Ocirc;ng B&igrave;nh nhăn mặt, x&ograve;e b&agrave;n tay ngửa l&ecirc;n v&agrave; xem x&eacute;t. &Ocirc;ng ph&aacute;t hiện ra trong l&ograve;ng b&agrave;n tay của &ocirc;ng đ&atilde; bị d&iacute;nh phải những thứ chất nhầy đen k&igrave;n kịt, cũng kh&ocirc;ng biết l&agrave; chất nhầy từ thứ g&igrave;.<br />\r\n<br />\r\n&Ocirc;ng B&igrave;nh hơi bực tức, qu&aacute;t vọng về ph&iacute;a ph&ograve;ng của Trọng:<br />\r\n<br />\r\n- M&agrave;y lại chui r&uacute;c v&agrave;o đ&acirc;u thế hả con? Từ d&agrave;y về muộn l&agrave; phải gọi điện b&aacute;o cho bố biết một tiếng. L&agrave;m g&igrave; c&oacute; c&aacute;i kiểu con c&aacute;i n&agrave;o m&agrave; bố mẹ gọi cũng kh&ocirc;ng nghe, hỏi cũng kh&ocirc;ng chịu trả lời như thế hả?<br />\r\n<br />\r\nVẫn kh&ocirc;ng hề c&oacute; &acirc;m thanh đ&aacute;p lại từ ph&iacute;a Trọng. &Ocirc;ng B&igrave;nh cau m&agrave;y một chốc rồi cũng để đấy. &Ocirc;ng xoay m&igrave;nh bước v&agrave;o căn ph&ograve;ng của m&igrave;nh v&agrave; trải chăn nằm xuống giường.<br />\r\n<br />\r\n&Ocirc;ng B&igrave;nh nao nao nhớ lại c&aacute;i thời m&agrave; b&agrave; Thơm c&ograve;n sống, khi ấy Trọng quấn lấy mẹ lắm. L&uacute;c n&agrave;o cũng chỉ chăm chăm đi ra ngo&agrave;i rồi về nh&agrave; với mẹ, đ&uacute;ng với c&aacute;i c&acirc;u n&oacute;i con trai th&igrave; gần mẹ, con g&aacute;i th&igrave; gần bố. Chắc c&oacute; thể Trọng cũng l&agrave; người như vậy. Khi ấy Trọng vui vẻ v&agrave; n&oacute;i chuyện nhiều hơn b&acirc;y giờ.<br />\r\n<br />\r\nTừ khi b&agrave; Thơm mất, Trọng đ&acirc;m ra buồn rầu hẳn, cũng &iacute;t n&oacute;i. Nhưng chưa lần n&agrave;o như lần n&agrave;y, ngay cả miệng cũng kh&ocirc;ng th&egrave;m h&eacute; ra d&ugrave; chỉ một ch&uacute;t.<br />\r\n<br />\r\nBẵng đi một l&aacute;t, &ocirc;ng Trọng tho&aacute;t khỏi gi&acirc;y ph&uacute;t thất thần chỉ v&igrave; một tiếng động. Từ ph&ograve;ng b&ecirc;n, c&oacute; tiếng &acirc;m thanh của người lầm rầm ph&aacute;t ra. Những tiếng th&igrave; th&agrave;o một c&aacute;ch kh&oacute; chịu, những tiếng c&aacute;u kỉnh của nhiều người chứ r&otilde; r&agrave;ng chẳng phải l&agrave; một người. Sao thế? Tiếng động đ&oacute; tại sao lại ph&aacute;t ra từ căn ph&ograve;ng của Trọng?<br />\r\n<br />\r\n&Ocirc;ng B&igrave;nh nh&igrave;n lại đồng hồ, th&igrave; đ&atilde; thấy giờ điểm đ&uacute;ng l&uacute;c mười hai giờ. C&aacute;i tiếng động x&igrave; x&agrave;o ở gian ph&ograve;ng b&ecirc;n cạnh vẫn kh&ocirc;ng ngớt, thậm ch&iacute; c&ograve;n c&oacute; khuynh hướng ng&agrave;y c&agrave;ng d&agrave;y đặc hơn. &Ocirc;ng B&igrave;nh cố nhắm chặt mắt lại để ngủ, m&agrave; tiếng động ấy kh&ocirc;ng sao tho&aacute;t nổi tai một người mẫn cảm với &acirc;m thanh như &ocirc;ng. C&agrave;ng về gi&agrave; th&igrave; &ocirc;ng lại c&agrave;ng bị những thứ &acirc;m thanh ảnh hưởng, cho d&ugrave; c&oacute; l&agrave; nhỏ cỡ n&agrave;o đi chăng nữa th&igrave; cũng khiến &ocirc;ng rất kh&oacute; ngủ.<br />\r\n<br />\r\nQuằn quại một l&uacute;c l&acirc;u m&agrave; kh&ocirc;ng thấy tiếng &acirc;m thanh dừng lại, &ocirc;ng B&igrave;nh phải qu&aacute;t l&ecirc;n:<br />\r\n<br />\r\n- Trọng! Mười hai giờ đ&ecirc;m rồi m&agrave; m&agrave;y c&ograve;n th&igrave; th&agrave;o c&aacute;i g&igrave; thế? C&ograve;n bật đ&agrave;i hay gọi điện cho người y&ecirc;u hả? C&oacute; định cho bố ngủ kh&ocirc;ng đ&acirc;y?<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; tiếng &acirc;m thanh đ&aacute;p lại. Nhưng dẫu vậy, những tiếng động th&igrave; th&agrave;o kia cũng đ&atilde; biến mất, v&agrave; đưa to&agrave;n bộ kh&ocirc;ng gian trở về như ban đầu, im l&igrave;m đến mức c&ocirc; quạnh. Chỉ c&ograve;n c&oacute; thể nghe thấy tiếng muỗi thi thoảng k&ecirc;u vo ve ở b&ecirc;n tai.<br />\r\n<br />\r\n&Ocirc;ng B&igrave;nh lại nằm xuống nhắm mắt, v&agrave; từ đ&oacute; th&igrave; y&ecirc;n giấc ngủ ngon tới tận s&aacute;ng m&agrave; kh&ocirc;ng hề bị bất cứ &acirc;m thanh n&agrave;o l&agrave;m phiền.<br />\r\n<br />\r\n...<br />\r\n<br />\r\nSớm h&ocirc;m sau, &ocirc;ng B&igrave;nh thức dậy thật sớm. &Ocirc;ng toan đi chuẩn bị bữa s&aacute;ng để cho Trọng v&agrave; &ocirc;ng c&ugrave;ng ăn trước khi hắn đi l&agrave;m. Trọng thường hay dậy rất muộn, n&ecirc;n bữa s&aacute;ng một l&agrave; hắn ăn ở nh&agrave; do bố nấu, hai l&agrave; hắn sẽ ra ngo&agrave;i ăn.<br />\r\n<br />\r\nĐ&atilde; l&agrave; s&aacute;u giờ s&aacute;ng, khi &ocirc;ng B&igrave;nh rửa mặt mũi xong, bước ra s&acirc;n tập v&agrave;i đường Th&aacute;i Cực Quyền cho thoải m&aacute;i cơ thể. Th&igrave; thật kỳ lạ, &ocirc;ng đ&atilde; thấy Trọng ngồi sẵn ở chiếc ghế ngo&agrave;i s&acirc;n, đ&ocirc;i mắt hắn mơ hồ, miệng vẫn cứ kh&ocirc;ng ngừng mấp m&aacute;y.<br />\r\n<br />\r\nDưới ch&acirc;n hắn, c&oacute; một vệt n&acirc;u d&agrave;i kh&ocirc; cứng. Đ&oacute; l&agrave; m&aacute;u, phải, n&oacute; ch&iacute;nh l&agrave; m&aacute;u của một ai đ&oacute; hay một thứ sinh vật n&agrave;o đ&oacute;.<br />\r\n<br />\r\nTrọng ngồi thẳng người, b&agrave;n tay giấu giếm một thứ g&igrave; đ&oacute;. &Ocirc;ng B&igrave;nh nheo mắt nh&igrave;n kĩ nhưng kh&ocirc;ng thể ph&aacute;t hiện th&ecirc;m ra được điều g&igrave;.<br />\r\n<br />\r\n&Ocirc;ng lại thấy bộ quần &aacute;o m&agrave; tối qua Trọng mặc khi đi về vẫn c&ograve;n nguy&ecirc;n sự bẩn thỉu, v&agrave; nhơ nhớp d&iacute;nh những thứ m&agrave;u đen. &Ocirc;ng B&igrave;nh bước đến trước mặt Trọng v&agrave; hối th&uacute;c:<br />\r\n<br />\r\n- Tối qua m&agrave;y chui r&uacute;c v&agrave;o c&aacute;i vũng n&agrave;o m&agrave; bẩn hết cả người thế n&agrave;y hả con? Sao kh&ocirc;ng đi thay quần &aacute;o tắm rửa rồi c&ograve;n chuẩn bị đi l&agrave;m?<br />\r\n<br />\r\n&Ocirc;ng B&igrave;nh nh&igrave;n chằm chằm v&agrave;o mặt của Trọng. Trọng th&igrave; dường như kh&ocirc;ng nghe thấy lời &ocirc;ng n&oacute;i, trong tay hắn đang b&oacute;p mạnh v&agrave;o một thứ g&igrave; đ&oacute;, hắn khom người kh&ocirc;ng để cho &ocirc;ng B&igrave;nh nh&igrave;n thấy thứ m&agrave; hắn đang giấu ở trong l&ograve;ng.<br />\r\n<br />\r\n&Ocirc;ng B&igrave;nh chậm r&atilde;i c&uacute;i xuống, lại nh&igrave;n thấy từ dưới hạ bộ của hắn nhỏ m&aacute;u th&agrave;nh tia th&igrave; liền kinh h&atilde;i k&ecirc;u l&ecirc;n:<br />\r\n<br />\r\n- Trời ơi Trọng, con l&agrave;m sao thế?<br />\r\n<br />\r\n&Ocirc;ng B&igrave;nh mặt chuyển sang x&aacute;m ngắt lo sợ, liệu c&oacute; khi n&agrave;o Trọng đ&atilde; bị thương, hay bị ai đ&acirc;m cho một nh&aacute;t dao n&ecirc;n mới chảy nhiều m&aacute;u như vậy?<br />\r\n<br />\r\n&Ocirc;ng chạy tới, giằng lấy c&aacute;nh tay của Trọng ra, để lộ ra thứ m&agrave; hắn đang giấu ở trong l&ograve;ng. Nhưng thứ m&agrave; hắn giấu ở trong l&ograve;ng, chẳng phải l&agrave; thứ g&igrave; như &ocirc;ng nghĩ, m&agrave; chỉ l&agrave; một con vật, một con vật hết sức đ&aacute;ng thương. Đ&oacute; l&agrave; một con m&egrave;o đen.<br />\r\n<br />\r\nCon m&egrave;o đen nằm trong l&ograve;ng của Trọng, đang bị vặn cổ ngược trở lại, hai mắt n&oacute; mở to như muốn lồi ra ngo&agrave;i. Lại từ miệng kh&ocirc;ng ngừng nhễu xuống m&aacute;u đỏ tươi, chảy xuống đũng quần của Trọng.<br />\r\n<br />\r\nTrọng đột nhi&ecirc;n cất giọng n&oacute;i hối hả như thanh minh:<br />\r\n<br />\r\n- Dạ con kh&ocirc;ng sao. S&aacute;ng nay con đang ngủ m&agrave; con m&egrave;o đen n&agrave;y n&oacute; k&ecirc;u to qu&aacute;, con tức qu&aacute; n&ecirc;n ra ngo&agrave;i bắt n&oacute; rồi bẻ g&atilde;y cổ n&oacute;. Con m&egrave;o hoang n&agrave;y ng&agrave;y n&agrave;o cũng chạy rầm rầm ở tr&ecirc;n đầu nh&agrave; m&igrave;nh, con kh&ocirc;ng chịu nổi... n&ecirc;n con... con mới giết n&oacute;.<br />\r\n<br />\r\n&Ocirc;ng B&igrave;nh cau m&agrave;y, cũng hơi sửng sốt v&igrave; biết t&iacute;nh t&igrave;nh của Trọng xưa nay rất y&ecirc;u qu&yacute; động vật. Vậy m&agrave; lần n&agrave;y hắn lại c&oacute; thể nhẫn t&acirc;m bẻ g&atilde;y cổ con m&egrave;o đến chết như vậy th&igrave; quả thực l&agrave; kh&ocirc;ng hợp với t&iacute;nh c&aacute;ch của hắn. Ngẫm lại, &ocirc;ng B&igrave;nh cảm thấy lời n&oacute;i của Trọng cũng c&oacute; l&yacute;. Bởi v&igrave; tiếng k&ecirc;u của con m&egrave;o đen n&agrave;y l&acirc;u nay &ocirc;ng cũng đ&atilde; biết r&otilde;. Đ&atilde; kh&ocirc;ng &iacute;t lần &ocirc;ng cảm thấy phiền h&agrave; về con m&egrave;o đen n&agrave;y n&ecirc;n đ&atilde; t&iacute;nh mua bả về để bỏ độc chết n&oacute;. Con m&egrave;o đen đ&ecirc;m n&agrave;o cũng k&ecirc;u l&ecirc;n những &acirc;m thanh như động đực, rồi c&agrave;o x&eacute; k&ecirc;u g&agrave;o c&aacute;i m&aacute;i t&ocirc;n ở tầng thượng ruỳnh ruỳnh khiến &ocirc;ng kh&ocirc;ng sao ngủ nổi.<br />\r\n<br />\r\nNhưng con m&egrave;o đen n&agrave;y n&oacute;i ra cũng thật th&ocirc;ng minh. Nhiều lần &ocirc;ng d&ugrave;ng đủ mọi c&aacute;ch để bắt n&oacute; m&agrave; n&oacute; vẫn c&oacute; thể kh&ocirc;n ngoan trốn tho&aacute;t. N&oacute; nhanh như gi&oacute;, chỉ v&egrave;o một c&aacute;i l&agrave; kh&ocirc;ng thấy tăm hơi đ&acirc;u.</p>\r\n\r\n<p>C&aacute;i giống m&egrave;o hoang vốn dữ dằn, lại khỏe mạnh. Cho d&ugrave; ch&uacute;ng c&oacute; th&acirc;n thể c&ograve;m c&otilde;i đến như thế n&agrave;o th&igrave; cũng khỏe gấp mấy lần m&egrave;o nh&agrave;, lại c&ograve;n rất hung &aacute;c. Bắt được ch&uacute;ng đi chăng nữa, th&igrave; cũng phải bị ch&uacute;ng c&agrave;o cho r&aacute;ch da r&aacute;ch thịt, c&ograve;n v&ugrave;ng vằng cắn x&eacute;. Chỉ hội đủ những l&yacute; do ấy th&ocirc;i l&agrave; đ&atilde; thấy chuyện c&oacute; thể bắt, v&agrave; giết được con m&egrave;o đen n&agrave;y kh&oacute; như thế n&agrave;o rồi.<br />\r\n<br />\r\nVậy m&agrave; Trọng lại c&oacute; thể tay kh&ocirc;ng bắt lấy n&oacute;. Hơn nữa lại vặn g&atilde;y cổ n&oacute; &ocirc;m trong l&ograve;ng m&agrave; ch&acirc;n tay da thịt kh&ocirc;ng một ch&uacute;t xước s&aacute;t g&igrave; th&igrave; cũng đ&uacute;ng thật l&agrave; kỳ lạ.<br />\r\n<br />\r\n&Ocirc;ng B&igrave;nh cau m&agrave;y nhớ lại, khi &ocirc;ng nằm trong nh&agrave; ngủ từ đ&ecirc;m qua đến s&aacute;ng nay. Đ&acirc;u c&oacute; nghe thấy tiếng con m&egrave;o đen n&agrave;y k&ecirc;u đ&acirc;u. Con m&egrave;o đen n&agrave;y hay quấy quả những giấc ngủ của &ocirc;ng, nhưng cũng chỉ t&ugrave;y từng h&ocirc;m, kh&ocirc;ng phải l&agrave; h&ocirc;m n&agrave;o n&oacute; cũng xuất hiện. &Ocirc;ng B&igrave;nh vốn l&agrave; một người rất mẫn cảm với &acirc;m thanh, lại th&ecirc;m việc tiếng m&egrave;o đen k&ecirc;u g&agrave;o nghe rất ch&oacute;i. Với những l&yacute; lẽ ấy th&igrave; tiếng k&ecirc;u g&agrave;o của con m&egrave;o đen n&agrave;y lại đủ sức đ&aacute;nh thức một người ăn ngủ khỏe như Trọng đang trong thời kỳ trai tr&aacute;ng. M&agrave; lại chẳng đủ sức đ&aacute;nh thức một &ocirc;ng gi&agrave; th&igrave; đ&uacute;ng l&agrave; một điều kh&ocirc;ng hợp l&yacute;.<br />\r\n<br />\r\nTuy rất nhiều điểm khiến &ocirc;ng B&igrave;nh nghi ngờ rằng Trọng đang c&oacute; điều g&igrave; đ&oacute; giấu giếm &ocirc;ng. Nhưng &ocirc;ng cũng kh&ocirc;ng nghĩ đ&oacute; l&agrave; chuyện g&igrave; to t&aacute;t, d&ugrave; sao cũng chỉ l&agrave; giết một con m&egrave;o m&agrave; th&ocirc;i.<br />\r\n<br />\r\n&Ocirc;ng B&igrave;nh lại hỏi Trọng:<br />\r\n<br />\r\n- Vậy chứ quần &aacute;o bẩn thế kia sao c&ograve;n kh&ocirc;ng đi thay đi, tắm rửa đi rồi m&agrave; c&ograve;n đi l&agrave;m?<br />\r\n<br />\r\nTrọng cười, đ&aacute;p lại:<br />\r\n<br />\r\n- Đ&ecirc;m qua con đi tản bộ v&ocirc; t&igrave;nh bị ng&atilde; xuống c&aacute;i cống, người h&ocirc;i qu&aacute; m&agrave; l&uacute;c về nh&agrave; cũng t&iacute;nh định tắm. Nhưng mệt kh&ocirc;ng thở nổi, lại kh&ocirc;ng n&oacute;i nổi c&acirc;u n&agrave;o n&ecirc;n đ&agrave;nh l&ecirc;n giường đi ngủ lu&ocirc;n. Con nhắm mắt một c&aacute;i l&agrave; ngủ say như chết, ngủ đến khi bị con m&egrave;o n&agrave;y đ&aacute;nh thức...<br />\r\n<br />\r\nNghe Trọng đ&aacute;p, &ocirc;ng B&igrave;nh vừa lẩm nhẩm trong miệng:<br />\r\n<br />\r\n- Ngủ say như chết?<br />\r\n<br />\r\nNhưng r&otilde; r&agrave;ng l&agrave; tối đ&ecirc;m qua, &ocirc;ng c&oacute; nghe thấy tiếng động lầm rầm th&igrave; thầm n&oacute;i chuyện ph&aacute;t ra từ ph&ograve;ng của Trọng cơ m&agrave;? Nếu kh&ocirc;ng phải những thứ &acirc;m thanh ấy do Trọng tạo ra, vậy th&igrave; l&agrave; ai l&agrave;m?<br />\r\n<br />\r\nC&oacute; điều, &ocirc;ng cũng chỉ d&aacute;m giữ mẩu suy luận n&agrave;y trong l&ograve;ng, cũng kh&ocirc;ng c&oacute; đem ra hỏi Trọng để hắn giải th&iacute;ch thắc mắc cho &ocirc;ng. Giờ Trọng đ&atilde; lớn, sẽ c&oacute; nhiều chuyện hắn tự quyết định. Cũng kh&ocirc;ng nhất thiết l&agrave; chuyện g&igrave; cũng phải đem ra kể lại cho bố mẹ như hồi c&ograve;n nhỏ.<br />\r\n<br />\r\n&Ocirc;ng B&igrave;nh nghĩ ngợi xong một l&uacute;c rồi liền xua tay n&oacute;i:<br />\r\n<br />\r\n- Th&ocirc;i được rồi, con v&agrave;o tắm rửa qua loa thay quần &aacute;o, rồi ra ăn s&aacute;ng. Giờ bố đi chuẩn bị đồ ăn...<br />\r\n<br />\r\nTrọng gật đầu, kh&ocirc;ng n&oacute;i g&igrave; th&ecirc;m liền quay đầu đi v&agrave;o nh&agrave;.<br />\r\n<br />\r\nC&aacute;i x&aacute;c con m&egrave;o đen rơi xuống đất, với c&aacute;i cổ của n&oacute; quay ngược lại, đ&ocirc;i mắt trực lồi ra ph&ugrave; hợp lại quay về hướng căn nh&agrave;, hướng m&agrave; Trọng đang bước tới.<br />\r\n<br />\r\nCon m&egrave;o đen tuy đ&atilde; chết, nhưng nom sao trong đ&ocirc;i mắt của n&oacute; vẫn như đang chất chứa một điều g&igrave; đ&oacute; cực kỳ th&ugrave; hằn.<br />\r\n<br />\r\n&Ocirc;ng B&igrave;nh chờ cho Trọng đi khuất, th&igrave; liền đem c&aacute;i x&aacute;c con m&egrave;o đen cầm l&ecirc;n tay xem x&eacute;t một l&uacute;c rồi nheo m&agrave;y, chỉ tự n&oacute;i ra hai tiếng kỳ lạ. Sau đ&oacute; &ocirc;ng lựa chọn một ụ đất mềm, đ&agrave;o l&ecirc;n một c&aacute;i hố rồi ch&ocirc;n x&aacute;c con m&egrave;o đen v&agrave;o đ&oacute;.<br />\r\n<br />\r\n...<br />\r\n<br />\r\nTrọng tắm rửa xong bước xuống nh&agrave;, ngồi v&agrave;o b&agrave;n ăn s&aacute;ng th&igrave; đ&atilde; c&oacute; ngay t&ocirc; b&uacute;n &ocirc;ng B&igrave;nh chuẩn bị sẵn. Hắn ăn ngấu nghiến một hồi rồi đứng l&ecirc;n. H&ocirc;m nay Trọng mặc một bộ đồ m&agrave;u đen từ đầu tới ch&acirc;n. &Aacute;o kho&aacute;c đen, &aacute;o sơ mi cũng đen, quần đen v&agrave; gi&agrave;y đen. Ngay cả đ&ocirc;i tất m&agrave; hắn đang đi cũng m&agrave;u đen. Chỉ duy nhất c&aacute;i ca ra v&aacute;t m&agrave; hắn đang đeo lại l&agrave; một m&agrave;u trắng. Hắn ăn mặc như thể hắn sắp sửa đi dự một đ&aacute;m tang n&agrave;o đ&oacute; vậy.<br />\r\n<br />\r\n&Ocirc;ng B&igrave;nh hơi cảm thấy ngạc nhi&ecirc;n v&igrave; phong c&aacute;ch của hắn h&ocirc;m nay. B&igrave;nh thường Trọng rất ưa gu thời trang s&aacute;ng v&agrave; nổi m&agrave;u. Phong c&aacute;ch một bộ đồ m&agrave;u tối chắc chắn kh&ocirc;ng phải l&agrave; sở th&iacute;ch của hắn.<br />\r\n<br />\r\nN&eacute;t mặt của Trọng h&ocirc;m nay th&igrave; dường như rất th&iacute;ch th&uacute; với c&aacute;ch ăn mặc n&agrave;y th&igrave; phải. Thậm ch&iacute; &ocirc;ng B&igrave;nh c&ograve;n c&oacute; thể c&oacute; cảm gi&aacute;c được rằng Trọng đang hết sức thỏa m&atilde;n, tinh thần hắn vui vẻ như mới gặt h&aacute;i được điều g&igrave;, v&agrave; cũng giống như hắn chuẩn bị l&agrave;m một điều g&igrave; đ&oacute; khiến hắn rất sung sướng.<br />\r\n<br />\r\nNhững điều kỳ lạ cứ lu&ocirc;n xuất hiện dồn dập từ tr&ecirc;n người của Trọng từ tối qua đến h&ocirc;m nay. Th&ocirc;i th&uacute;c rất nhiều khiến cho &ocirc;ng B&igrave;nh phải suy x&eacute;t.<br />\r\n<br />\r\nTuy vậy, &ocirc;ng vẫn chỉ lặng thinh chờ hắn ăn xong bữa s&aacute;ng. V&agrave; mỉm cười khi hắn bước ra khỏi nh&agrave; đi l&agrave;m, bước l&ecirc;n chiếc xe &ocirc; t&ocirc; cũng m&agrave;u đen x&igrave; nằm trong số cả bốn chiếc xe &ocirc; t&ocirc; đủ m&agrave;u của gia đ&igrave;nh.<br />\r\n<br />\r\nTh&ocirc;ng thường, hắn sẽ chọn đi một chiếc &ocirc; t&ocirc; m&agrave;u trắng, hoặc l&agrave; một chiếc m&agrave;u bạc nhưng m&agrave;u đen th&igrave; tuyệt đối kh&ocirc;ng bao giờ. Chiếc &ocirc; t&ocirc; m&agrave;u đen đ&oacute; chỉ c&oacute; &ocirc;ng B&igrave;nh thấy hắn kh&ocirc;ng d&ugrave;ng n&ecirc;n hay đi. M&agrave; kh&ocirc;ng hiểu tại sao h&ocirc;m nay hắn lại chọn chiếc &ocirc; t&ocirc; đ&oacute;. C&oacute; lẽ sở th&iacute;ch của hắn đ&atilde; ho&agrave;n to&agrave;n thay đổi?<br />\r\n<br />\r\nTối đến, &ocirc;ng B&igrave;nh lại l&agrave;m cơm bữa tối chờ Trọng về. Cũng như mọi h&ocirc;m, h&ocirc;m nay &ocirc;ng bật sẵn bản tin thời sự để chờ đợi trong thời gian rảnh.<br />\r\n<br />\r\nĐ&uacute;ng bảy rưỡi tối, tiếng động cơ &ocirc; t&ocirc; vang ở b&ecirc;n ngo&agrave;i. C&oacute; d&aacute;ng Trọng loạng choạng bước từ ngo&agrave;i của bước v&agrave;o. Tr&ecirc;n người hắn d&iacute;nh một vệt m&agrave;u n&acirc;u k&eacute;o d&agrave;i v&agrave; xộc xệch, đ&oacute; hẳn l&agrave; vết m&aacute;u. Ngo&agrave;i ra quần &aacute;o tr&ecirc;n người hắn c&ograve;n xộc xệch, chiếc &aacute;o sơ mi r&aacute;ch bươm một miếng thật lớn.<br />\r\n<br />\r\n&Ocirc;ng B&igrave;nh tho&aacute;ng nh&igrave;n qua t&igrave;nh cảnh ấy th&igrave; liền tự hỏi, liệu c&oacute; khi n&agrave;o Trọng lại vừa giết th&ecirc;m một con vật n&agrave;o nữa kh&ocirc;ng chứ? Nhưng con vật n&agrave;o m&agrave; lại c&oacute; thể x&eacute; r&aacute;ch được quần &aacute;o của hắn?<br />\r\n<br />\r\nV&agrave; h&ocirc;m nay, giống như h&ocirc;m qua. Trọng kh&ocirc;ng hề n&oacute;i g&igrave;, m&agrave; chỉ lầm lũi bước v&agrave;o ph&ograve;ng. Cho d&ugrave; &ocirc;ng B&igrave;nh c&oacute; gọi mấy lần để hắn ra ăn cơm, nhưng hắn cũng kh&ocirc;ng đ&aacute;p lại.<br />\r\n<br />\r\n&Ocirc;ng B&igrave;nh đ&agrave;nh phải để d&agrave;nh một phần cơm tr&ecirc;n b&agrave;n cho Trọng, rồi d&ugrave;ng nhanh bữa tối sau đ&oacute; đ&oacute;ng cửa nh&agrave; lại ngồi một m&igrave;nh xem ti vi cho đến mười giờ.<br />\r\n<br />\r\n&Ocirc;ng B&igrave;nh c&oacute; một th&oacute;i quen kh&oacute; bỏ, l&agrave; thường b&aacute;m s&aacute;t tất cả c&aacute;c chương tr&igrave;nh ti vi diễn ra từ khung giờ n&agrave;y. Cho d&ugrave; h&ocirc;m n&agrave;o bận bịu, &ocirc;ng cũng lu&ocirc;n thu xếp để thỏa m&atilde;n c&aacute;i sở th&iacute;ch c&aacute; nh&acirc;n của m&igrave;nh.<br />\r\n<br />\r\nKhi ngồi vắt ch&acirc;n l&ecirc;n ghế xem ti vi, &ocirc;ng B&igrave;nh c&oacute; thể nhận ra được rằng c&oacute; một thứ tiếng động n&agrave;o đ&oacute; ph&aacute;t ra xen lẫn trong tiếng ti vi. M&agrave; dĩ nhi&ecirc;n thứ tiếng động đ&oacute; kh&ocirc;ng thể n&agrave;o ph&aacute;t ra từ chiếc ti vi kia được. Đ&oacute; l&agrave; một thứ tiếng lầm bầm giống hệt như tối h&ocirc;m qua, n&oacute; ph&aacute;t ra từ căn ph&ograve;ng của Trọng. Vẫn l&agrave; tiếng động của rất nhiều người n&oacute;i chuyện, những tiếng th&igrave; th&agrave;o như qu&aacute;t th&aacute;o hết sức kh&oacute; chịu.<br />\r\n<br />\r\n&Ocirc;ng B&igrave;nh kh&ocirc;ng thể n&agrave;o nhẫn nại ngồi xem chương tr&igrave;nh ti vi ưa th&iacute;ch, với những &acirc;m thanh ảnh hưởng một c&aacute;ch kh&oacute; chịu như vậy.<br />\r\n<br />\r\n&Ocirc;ng qu&aacute;t l&ecirc;n:<br />\r\n<br />\r\n- Trọng, m&agrave;y n&oacute;i chuyện g&igrave; th&igrave; cứ n&oacute;i to hẳn l&ecirc;n, cứ x&igrave; x&agrave;o như thế suốt đ&ecirc;m định tra tấn bố hả?<br />\r\n<br />\r\nC&oacute; đ&ocirc;i khi những tiếng động x&igrave; x&agrave;o len lỏi v&agrave;o trong t&acirc;m tr&iacute; người ta, c&ograve;n kh&oacute; chịu hơn những tiếng động qu&aacute;t th&aacute;o to đến giật m&igrave;nh rất nhiều. V&agrave; trong trường hợp n&agrave;y cũng vậy, với một người rất mẫn cảm với &acirc;m thanh như &ocirc;ng B&igrave;nh, đ&acirc;y quả thật l&agrave; một sự tra tấn.<br />\r\n<br />\r\nTiếng &acirc;m thanh kh&ocirc;ng ngắt lại, m&agrave; c&ograve;n c&oacute; xu hướng to dần đều l&ecirc;n. Dường như đang c&oacute; rất nhiều người tranh c&atilde;i trong căn ph&ograve;ng đ&oacute;. &Ocirc;ng B&igrave;nh đứng phắt dậy, n&eacute;m thẳng c&aacute;i điều khiển ti vi xuống mặt ghế s&ocirc; pha. Lại bước phăm phăm thẳng tới trước cửa ph&ograve;ng của Trọng định g&otilde; cửa.<br />\r\n<br />\r\nNgay khi &ocirc;ng đang chuẩn bị l&agrave;m chuyện ấy, th&igrave; đột nhi&ecirc;n từ tr&ecirc;n lầu tầng thượng. Lại ph&aacute;t ra những tiếng &acirc;m thanh ầm ầm rất mạnh, l&agrave; tiếng động ph&aacute;t ra từ m&aacute;i t&ocirc;n. Tiếng động to tới mức muốn xuy&ecirc;n thủng ph&aacute; n&aacute;t cả m&aacute;i t&ocirc;n ở tr&ecirc;n đấy.<br />\r\n<br />\r\n&Ocirc;ng B&igrave;nh hoảng qu&aacute;, vội v&agrave;ng chạy phắt l&ecirc;n cầu thang, muốn cầm chổi chuẩn bị đập chết những con vật đang quấy quả ở tr&ecirc;n ấy, m&agrave; qu&ecirc;n đi những &acirc;m thanh kỳ lạ trong ph&ograve;ng của Trọng vừa rồi.<br />\r\n<br />\r\nTiếng va chạm v&agrave;o m&aacute;i t&ocirc;n ng&agrave;y c&agrave;ng dồn dập, &acirc;m thanh vang đều đặn kh&ocirc;ng hề dừng lại.<br />\r\n<br />\r\nNgay sau đ&oacute;, những tiếng m&egrave;o k&ecirc;u ch&oacute;i tai vang lanh lảnh trong đ&ecirc;m hết sức đ&aacute;ng sợ. Khiến ch&acirc;n tay &ocirc;ng B&igrave;nh đều chuyển sang run rẩy.<br />\r\n<br />\r\nMột tiếng l&agrave;u b&agrave;u chửi rủa ph&aacute;t ra từ miệng &ocirc;ng B&igrave;nh, &ocirc;ng nhẫn nại cầm chắc c&aacute;i c&aacute;n chổi rồi x&ocirc;ng thẳng l&ecirc;n đến lầu thượng.<br />\r\n<br />\r\nL&ecirc;n đến nơi, &ocirc;ng lập tức tri h&ocirc; qu&aacute;t lớn:<br />\r\n<br />\r\n- C&uacute;t ngay!<br />\r\n<br />\r\nRồi d&ugrave;ng c&aacute;i c&aacute;n chổi g&otilde; mạnh v&agrave;o mặt m&aacute;i t&ocirc;n, để xua đuổi thứ vật đang ho&agrave;nh h&agrave;nh tr&ecirc;n đ&oacute;.<br />\r\n<br />\r\nTrong đầu &ocirc;ng tự nhi&ecirc;n vang l&ecirc;n một c&acirc;u hỏi kỳ lạ: &ldquo;Kh&ocirc;ng phải l&agrave; con m&egrave;o đen đ&oacute; đ&atilde; chết rồi hay sao? Tại sao vẫn c&ograve;n c&oacute; tiếng m&egrave;o k&ecirc;u nữa? Hay l&agrave; lại c&oacute; một con m&egrave;o kh&aacute;c?&rdquo;<br />\r\n<br />\r\n&Ocirc;ng B&igrave;nh vừa suy nghĩ như thế, vừa kh&ocirc;ng dừng tay g&otilde; rầm rầm v&agrave;o m&aacute;i t&ocirc;n hết sức tức giận như muốn xua đuổi thứ ở tr&ecirc;n đ&oacute; đi.<br />\r\n<br />\r\nM&atilde;i một l&uacute;c sau, khi &ocirc;ng ngừng lại th&igrave; tiếng động ấy cũng đ&atilde; bẵng đi kh&ocirc;ng c&ograve;n. Chỉ c&ograve;n lại tiếng gi&oacute; hiu hiu thổi nhẹ nh&agrave;ng qua m&aacute;i hi&ecirc;n tầng thượng.<br />\r\n<br />\r\n&Ocirc;ng B&igrave;nh thở ph&agrave;o một hơi, thu c&aacute;i c&aacute;n chổi lại rồi đ&oacute;ng cửa s&acirc;n thượng đi xuống dưới nh&agrave;.<br />\r\n<br />\r\n&Ocirc;ng bước đi thật chậm ở cầu thang, rồi d&ugrave;ng đ&ocirc;i mắt d&ograve; x&eacute;t xung quanh xem liệu rằng c&oacute; khi n&agrave;o con m&egrave;o hoang kia đ&atilde; chui v&agrave;o trong nh&agrave; hay kh&ocirc;ng? Kiểm tra một c&aacute;ch chắc chắn kh&ocirc;ng c&ograve;n vấn đề g&igrave; nữa.<br />\r\n<br />\r\nTh&igrave; trong l&ograve;ng &ocirc;ng l&uacute;c n&agrave;y lại b&ugrave;ng l&ecirc;n linh t&iacute;nh, một sự trực gi&aacute;c m&aacute;ch bảo &ocirc;ng nghĩ đến nơi ch&ocirc;n cất con m&egrave;o đen.<br />\r\n<br />\r\nNhanh như cắt, &ocirc;ng B&igrave;nh loạng choạng chạy ra khỏi nh&agrave;, chạy đến c&aacute;i ụ đất ở ngo&agrave;i nh&agrave;, nơi m&agrave; l&uacute;c s&aacute;ng &ocirc;ng vừa mới ch&ocirc;n cất con m&egrave;o đen ở đ&oacute;.<br />\r\n<br />\r\nKhi đến nơi, &ocirc;ng sững sờ thất thần, cả người quỳ mọp xuống hết sức kinh sợ.<br />\r\n<br />\r\nC&aacute;i ụ đất ấy giờ đ&atilde; lộ ra một c&aacute;i lỗ đen ng&ograve;m, c&aacute;i lỗ ấy ph&ugrave; hợp vừa với th&acirc;n người của con m&egrave;o đen để n&oacute; chui ra. Quanh miệng c&aacute;i lỗ c&ograve;n c&oacute; kh&ocirc;ng &iacute;t vệt m&aacute;u d&agrave;i vương v&atilde;i. Theo vệt m&aacute;u d&agrave;i vương v&atilde;i đ&oacute;, n&oacute; dường như k&eacute;o d&agrave;i đến tận c&aacute;i c&acirc;y cổ thụ ngay cạnh căn nh&agrave;.<br />\r\n<br />\r\nCon m&egrave;o đen ấy đ&atilde; bị g&atilde;y gập cỏ, mắt lồi ra ngo&agrave;i, chảy bao nhi&ecirc;u m&aacute;u như vậy m&agrave; kh&ocirc;ng chết được th&igrave; đ&uacute;ng thật l&agrave; kỳ t&iacute;ch. N&oacute; lại c&ograve;n đội mồ m&agrave; sống dậy. Nếu &ocirc;ng B&igrave;nh kh&ocirc;ng nhầm, th&igrave; hẳn l&agrave; khi n&atilde;y, tiếng động tr&ecirc;n m&aacute;i t&ocirc;n tầng thượng l&agrave; do ch&iacute;nh n&oacute; k&ecirc;u l&ecirc;n chứ kh&ocirc;ng phải bất cứ một con m&egrave;o n&agrave;o kh&aacute;c.<br />\r\n<br />\r\nN&oacute; h&agrave;nh động đi&ecirc;n cuồng v&agrave; dữ dội như vậy, chỉ c&oacute; thể l&agrave; sự l&yacute; giải cho một thứ. Một thứ h&agrave;nh động theo cảm t&iacute;nh của lo&agrave;i người vẫn thường l&agrave;m khi bị tổn thương, l&agrave; sự trả th&ugrave;.<br />\r\n<br />\r\n&Ocirc;ng B&igrave;nh thất thần hồi l&acirc;u, mồ h&ocirc;i v&atilde; ra như tắm. Trong l&ograve;ng &ocirc;ng c&oacute; cảm gi&aacute;c hoảng sợ, &ocirc;ng kh&ocirc;ng d&aacute;m n&aacute;n lại l&acirc;u trước mộ huyệt của con m&egrave;o đen n&agrave;y.<br />\r\n<br />\r\n&Ocirc;ng chạy v&agrave;o nh&agrave;, đ&oacute;ng mạnh cửa rồi n&eacute;p s&aacute;t th&acirc;n người v&agrave;o cửa, mắt mở to miệng thở dốc rất mạnh. Nhịp tim của &ocirc;ng cứ tăng l&ecirc;m đều đặn từng ph&uacute;t m&agrave; kh&ocirc;ng ngừng lại.<br />\r\n<br />\r\nTrong ph&uacute;t thở dốc, đ&ocirc;i mắt &ocirc;ng B&igrave;nh đang h&ograve;a ho&atilde;n trở lại th&igrave; liền chuyển sang mở to đều, trợn mặt như muốn rớt đ&ocirc;i con ngươi ra b&ecirc;n ngo&agrave;i.<br />\r\n<br />\r\nBởi v&igrave; từ l&uacute;c n&agrave;o, con m&egrave;o đen đ&atilde; ngồi đ&oacute;, ngồi ngay tr&ecirc;n chiếc gh&ecirc; s&ocirc; pha. Cả th&acirc;n người n&oacute; hướng về ph&iacute;a chiếc ti vi, nhưng c&aacute;i đầu th&igrave; đang ngoẹo về ph&iacute;a sau nh&igrave;n chằm chằm v&agrave;o &ocirc;ng B&igrave;nh.<br />\r\n<br />\r\n&Ocirc;ng B&igrave;nh thảng thốt k&ecirc;u l&ecirc;n:<br />\r\n<br />\r\n- &Ocirc;i!<br />\r\n<br />\r\nRồi ng&atilde; gục xuống đất, con m&egrave;o đen k&ecirc;u r&eacute; l&ecirc;n một tiếng rồi nhảy vụt ra ngo&agrave;i cửa sổ.<br />\r\n<br />\r\n&Ocirc;ng B&igrave;nh chết ngất v&igrave; tiếng k&ecirc;u của n&oacute;, đến khi &ocirc;ng tỉnh lại. Tiếng động ti vi vẫn mở vang đều, k&egrave;m theo thứ tiếng động lầm bầm vẫn đang ph&aacute;t ra từ căn ph&ograve;ng của Trọng.<br />\r\n<br />\r\n&Ocirc;ng B&igrave;nh run rẩy đứng dậy, ngồi xuống ghế s&ocirc; pha định thần lại một l&uacute;c. C&agrave;ng suy nghĩ &ocirc;ng c&agrave;ng kh&ocirc;ng thể hiểu nổi v&igrave; sao con m&egrave;o kia lại c&oacute; thể sống được, với c&aacute;i cổ bị bẻ ngược một trăm t&aacute;m mươi độ như thế. Đ&ocirc;i mắt n&oacute; s&aacute;ng rỡ trong m&agrave;n đ&ecirc;m, nhưng kh&ocirc;ng phải l&agrave; &aacute;nh s&aacute;ng m&agrave;u xanh như lo&agrave;i m&egrave;o th&ocirc;ng thường, m&agrave; l&agrave; một m&agrave;u đỏ lửa hết sức gh&ecirc; rợn.<br />\r\n<br />\r\nTh&ecirc;m một l&uacute;c nữa, thứ tiếng động lầm bầm ph&aacute;t ra từ căn ph&ograve;ng của Trọng vang l&ecirc;n đ&atilde; ng&agrave;y c&agrave;ng kh&oacute; chịu.<br />\r\n<br />\r\n&Ocirc;ng B&igrave;nh đứng phắt dậy, qu&ecirc;n đi cả sự t&ocirc;n trọng lịch sự thường ng&agrave;y m&agrave; &ocirc;ng vẫn d&agrave;nh cho hắn, l&agrave; g&otilde; cửa trước khi bước v&agrave;o ph&ograve;ng hắn.<br />\r\n<br />\r\n&Ocirc;ng khẽ h&eacute; mở c&aacute;nh cửa ph&ograve;ng, rồi th&ograve; đ&ocirc;i mắt nh&igrave;n trộm qua khe cửa. &Ocirc;ng đang thấy Trọng ngồi đ&oacute;, ngồi gặm một thứ g&igrave; đ&oacute; lui cui ở g&oacute;c giường. Lưng hắn quay về ph&iacute;a &ocirc;ng, căn ph&ograve;ng của hắn h&ocirc;i h&aacute;m lạ thường.<br />\r\n<br />\r\nV&agrave; c&aacute;i thứ tiếng động ấy vẫn cứ kh&ocirc;ng ngừng ph&aacute;t ra, từ một thứ m&agrave; &ocirc;ng kh&ocirc;ng sao đo&aacute;n biết được đ&oacute; l&agrave; thứ g&igrave;. N&oacute; tr&ocirc;ng giống như một tấm da, giống như một miếng da b&ograve; rất lớn. Lại c&oacute; những h&igrave;nh th&ugrave; chằng chịt hết sức kỳ lạ xăm l&ecirc;n tấm da đ&oacute;.<br />\r\n<br />\r\nNhững tia m&aacute;u cứ phun l&ecirc;n kh&ocirc;ng ngừng từ ph&iacute;a Trọng đang ngồi. Tiếng hắn lầm bầm đ&aacute;p lại tấm da, cũng như tiếng ch&iacute;t ch&iacute;t của lo&agrave;i chuột ph&aacute;t ra từ trong l&ograve;ng hắn. Hắn chợt vung tay n&eacute;m ra từ trong l&ograve;ng hắn một con chuột. Một con chuột cống to cỡ con m&egrave;o đ&atilde; bị moi sạch nội tạng, với m&aacute;u bị r&uacute;t hết sạch.<br />\r\n<br />\r\n&Ocirc;ng B&igrave;nh c&oacute; thể nghe thấy tiếng động nhai t&oacute;p t&eacute;p như thỏa m&atilde;n từ Trọng. Nh&igrave;n đến đ&acirc;y &ocirc;ng c&oacute; thể nhận biết đo&aacute;n được ngay, c&aacute;i thứ m&agrave; Trọng lui cui gặm nhấm ch&iacute;nh l&agrave; con chuột đ&atilde; bị moi hết nội tạng n&agrave;y.<br />\r\n<br />\r\nMiệng &ocirc;ng B&igrave;nh h&aacute; hốc kinh sợ kh&ocirc;ng n&oacute;i nổi lời n&agrave;o, &ocirc;ng c&oacute; ch&uacute;t cảm gi&aacute;c buồn n&ocirc;n nhưng cũng cố gắng nhịn lại.<br />\r\n<br />\r\nTrọng l&uacute;c n&agrave;y vẫn đang xoay lưng về ph&iacute;a &ocirc;ng, hắn th&iacute;ch th&uacute; cười như đi&ecirc;n dại. Rồi lại c&uacute;i xuống như cắn mạnh một thứ g&igrave; đ&oacute;. Một tia m&aacute;u d&agrave;i lại phun l&ecirc;n bắn v&agrave;o mặt tường. Tiếng k&ecirc;u đi&ecirc;n dại của con chuột cống kh&aacute;c, hẳn l&agrave; như vậy.<br />\r\n<br />\r\n&Ocirc;ng B&igrave;nh l&ugrave;i lại kh&ocirc;ng d&aacute;m tin v&agrave;o mắt m&igrave;nh, r&oacute;n r&eacute;n đ&oacute;ng lại c&aacute;nh cửa gỗ m&agrave; kh&ocirc;ng để cho Trọng ph&aacute;t hiện, rồi ngồi thụp xuống ghế lau mồ h&ocirc;i.<br />\r\n<br />\r\n&Ocirc;ng B&igrave;nh l&agrave; người theo đạo thi&ecirc;n ch&uacute;a, rất tin v&agrave;o những điều kinh th&aacute;nh đ&atilde; n&oacute;i. Sẽ c&oacute; quỷ xuất hiện, một lo&agrave;i quỷ &aacute;m. Nhưng trước nay &ocirc;ng tin m&agrave; chưa từng gặp phải trường hợp như vậy. M&agrave; nay gặp phải th&igrave; trường hợp đ&oacute; lại ứng nghiệm l&ecirc;n ch&iacute;nh con trai của m&igrave;nh.<br />\r\n<br />\r\nGượm lại một ch&uacute;t, &ocirc;ng B&igrave;nh cố n&eacute;n sợ h&atilde;i m&agrave; leo l&ecirc;n giường đi ngủ ngay. &Ocirc;ng đ&atilde; c&oacute; một quyết định v&agrave;o sớm ng&agrave;y mai. &Ocirc;ng cố gắng xua tan những tiếng động th&igrave; thầm cứ ph&aacute;t ra li&ecirc;n tục ở căn ph&ograve;ng kế b&ecirc;n. Trong tay &ocirc;ng cầm chặt c&acirc;y th&aacute;nh gi&aacute; của ch&uacute;a như chờ đợi sự cứu rỗi.<br />\r\n<br />\r\nSớm h&ocirc;m sau, đ&uacute;ng s&aacute;u giờ s&aacute;ng &ocirc;ng B&igrave;nh lại dậy v&agrave; chuẩn bị bữa s&aacute;ng.<br />\r\n<br />\r\n&Ocirc;ng vẫn tỏ vẻ b&igrave;nh tĩnh như mọi ng&agrave;y v&agrave; kh&ocirc;ng hề để lộ ra sơ hở. V&agrave; c&oacute; vẻ như Trọng cũng l&agrave; một người như vậy, kh&aacute;c hẳn với h&agrave;nh động th&uacute; t&iacute;nh v&agrave; qu&aacute;i vật đ&ecirc;m qua. Trọng l&uacute;c n&agrave;y ăn mặc hết sức lịch sự sang trọng, m&ugrave;i thơm tr&ecirc;n người c&ograve;n li&ecirc;n tục tỏa ra ng&agrave;o ngạt kh&ocirc;ng dứt.<br />\r\n<br />\r\nHắn lại mặc một bộ đồ m&agrave;u đen giống hệt h&ocirc;m qua, với những phong c&aacute;ch đi lại n&oacute;i chuyện điềm tĩnh hệt như ng&agrave;y thường.<br />\r\n<br />\r\nNhững điều hắn đang l&agrave;m, c&agrave;ng khiến cho &ocirc;ng B&igrave;nh cảm thấy gh&ecirc; sợ. &Ocirc;ng kh&ocirc;ng c&ograve;n nhận ra nổi cậu con trai ng&acirc;y thơ ng&agrave;y n&agrave;o của m&igrave;nh nữa. Giờ hắn đ&atilde; như một con qu&aacute;i vật ranh ma, v&agrave; nguy hiểm kh&ocirc;n lường.<br />\r\n<br />\r\nTrọng ăn xong bữa s&aacute;ng, lại mỉm cười ch&agrave;o &ocirc;ng ta v&agrave; đi l&agrave;m.<br />\r\n<br />\r\n&Ocirc;ng B&igrave;nh đứng dậy thu dọn ch&eacute;n b&aacute;t, tản bộ một ch&uacute;t ngo&agrave;i vườn trước khi thực hiện kế hoạch của m&igrave;nh.<br />\r\n<br />\r\nĐi dạo quanh vườn một l&uacute;c, &ocirc;ng B&igrave;nh chợt ph&aacute;t hiện ra trong c&aacute;i khu vực trồng hoa ngay cạnh căn nh&agrave;. Những c&acirc;y hoa hồng đ&atilde; cao th&ecirc;m một ch&uacute;t, cao hơn hẳn hai mươi xăng ti m&eacute;t. Dường như l&agrave; n&oacute; đ&atilde; ph&aacute;t triển rất nhanh với một sự kh&ocirc;ng ph&ugrave; hợp.<br />\r\n<br />\r\nNhưng &ocirc;ng B&igrave;nh kh&ocirc;ng để t&acirc;m đến chuyện đ&oacute; l&acirc;u, m&agrave; chỉ muốn dạo quanh vườn để nh&igrave;n lại c&aacute;i ụ đất, nơi con m&egrave;o &ocirc;ng ch&ocirc;n s&aacute;ng h&ocirc;m qua, c&oacute; c&ograve;n nguy&ecirc;n c&aacute;i lỗ đen giống tối h&ocirc;m qua hay kh&ocirc;ng?<br />\r\n<br />\r\nĐến nơi, &ocirc;ng khẳng định l&agrave; những điều m&agrave; tối qua &ocirc;ng gặp kh&ocirc;ng phải l&agrave; sự tưởng tượng. Bởi v&igrave; c&aacute;i lỗ đen ấy vẫn c&ograve;n đ&oacute;.<br />\r\n<br />\r\n&Ocirc;ng B&igrave;nh kh&ocirc;ng hoảng sợ như buổi đ&ecirc;m, m&agrave; chỉ b&igrave;nh tĩnh tiến lại gần c&aacute;i hố. Lấp n&oacute; đi. Rồi v&agrave;o trong gara &ocirc; t&ocirc; lựa chọn một chiếc xe, l&ecirc;n xe v&agrave; rời khỏi nh&agrave;, muốn đi đến một nơi.</p>', NULL, 16, 1, '2020-09-10 07:58:57', NULL),
(39, 'Con đường âm phủ', 'Chương 2', '<p><em>(Truyện ngắn nằm trong series truyện ngắn kinh dị Lời Thề Huyệt Hoang của t&aacute;c giả Ngạ Quỷ)</em><br />\r\n<br />\r\nNhững tấm bia nhấp nh&ocirc; san s&aacute;t nhau như một rừng bia mộ tại một khu nghĩa địa. Ch&acirc;n hương của những c&aacute;i b&aacute;t nhang đ&atilde; rụng rời v&igrave; từ l&acirc;u kh&ocirc;ng c&oacute; người thắp nhang, v&agrave; cũng l&agrave; v&igrave; mưa gi&oacute; ảnh hưởng khiến cho c&aacute;i b&aacute;t nhang tr&ocirc;ng c&agrave;ng ẩm ướt bẩn thỉu thiếu trang nghi&ecirc;m.<br />\r\n<br />\r\nNhững c&aacute;i miếu nhỏ được người d&acirc;n địa phương tạm thời lập n&ecirc;n để thờ c&uacute;ng cho những người xấu số đ&atilde; chết v&igrave; con đ&egrave;o cả đầy nguy hiểm n&agrave;y...<br />\r\n<br />\r\n***<br />\r\n<br />\r\nChiếc xe tải bon bon chạy tr&ecirc;n cung đường vắng, vắng đến mức tr&ecirc;n cả cung đường ấy chẳng hề c&oacute; một ch&uacute;t &aacute;nh s&aacute;ng n&agrave;o kh&aacute;c ngo&agrave;i chiếc đ&egrave;n xe tải n&agrave;y. Mạnh bu&ocirc;ng chiếc v&ocirc; lăng, thả rũ m&aacute;i t&oacute;c đen nh&aacute;nh của m&igrave;nh m&agrave; phủi những lớp bụi bay tới tấp từ khung cửa k&iacute;nh ngo&agrave;i cabin bay v&agrave;o.<br />\r\n<br />\r\nTuyến đường quốc lộ một trải rất d&agrave;i, n&oacute; v&ograve;ng vo quanh co qua những triền n&uacute;i, k&eacute;o s&acirc;u v&agrave;o tận t&iacute;t b&ecirc;n kia những ngọn đồi l&agrave; những trạm trung chuyển hứa hẹn của c&aacute;nh l&aacute;i xe tải.<br />\r\n<br />\r\nTại nơi ấy, những chuyến h&agrave;ng quen sẽ được bốc dỡ đ&uacute;ng lộ tr&igrave;nh, v&agrave; tiếp tục đổ h&agrave;ng tới những miền đất c&ograve;n c&oacute; thể hoang vu hơn cả tuyến quốc lộ n&agrave;y, những lối đường m&ograve;n.<br />\r\n<br />\r\nRầm!<br />\r\n<br />\r\nK&eacute;t!<br />\r\n<br />\r\nBất chợt, Mạnh phải vội phanh gấp b&aacute;nh xe, c&aacute;i v&ocirc; lăng xoay mạnh một c&aacute;i khiến cả chiếc xe quay m&igrave;nh như sắp đổ. Mạnh đập tay v&ocirc; c&aacute;i v&ocirc; lăng chửi thề một c&acirc;u:<br />\r\n<br />\r\n- Mẹ khiếp, lại sạt n&uacute;i rồi...<br />\r\n<br />\r\nMạnh r&uacute;t một điếu thuốc l&aacute; ra đưa l&ecirc;n miệng ch&acirc;m h&uacute;t ph&igrave; ph&egrave;o rồi nhả kh&oacute;i, nheo mắt nh&igrave;n quanh thật kỹ lại.<br />\r\n<br />\r\nNơi n&agrave;y l&agrave; một nơi vắng vẻ đ&igrave;u hiu, l&agrave; một con đ&egrave;o cao, b&ecirc;n cạnh c&oacute; một c&aacute;i vực s&acirc;u thăm thẳm. Ở cuối chỗ sụt l&uacute;t, l&agrave; một c&aacute;i ng&atilde; rẽ đ&atilde; bị đất đ&aacute; đổ &ugrave;n xuống từ tr&ecirc;n cao chặn qu&aacute; nửa.<br />\r\n<br />\r\nMạnh chậm r&atilde;i từ tốn thưởng thức xong điếu thuốc, rồi mở cửa ca bin nhảy xuống xe xem x&eacute;t t&igrave;nh h&igrave;nh.<br />\r\n<br />\r\nCon đ&egrave;o dốc bị chặn đứng bởi một mớ đất đ&aacute; lổn nhổn nhấp nh&ocirc;, dưới rặng c&acirc;y xanh um t&ugrave;m ph&iacute;a xa, chợt Mạnh nh&igrave;n thấy một đống bia đ&aacute; thấp tho&aacute;ng xa gần mờ mịt.<br />\r\n<br />\r\nMạnh chậm r&atilde;i tiến đến gần, soi c&aacute;i đ&egrave;n pin rọi v&ocirc; ph&iacute;a ấy để xem kĩ đ&oacute; l&agrave; thứ g&igrave;. V&igrave; trời đang buổi đ&ecirc;m, n&ecirc;n kh&ocirc;ng kh&iacute; rất lạnh. Anh vừa bước đi m&agrave; vừa &ocirc;m lấy s&aacute;t th&acirc;n người để cho đỡ lạnh.<br />\r\n<br />\r\nGi&oacute; ở đ&egrave;o cao kh&aacute;c với gi&oacute; ở đồng bằng, nơi n&agrave;y cho d&ugrave; l&agrave; m&ugrave;a h&egrave; đi chăng nữa, th&igrave; thi thoảng cũng c&oacute; thể gặp phải những c&aacute;i lạnh rất kh&oacute; tả m&agrave; hiếm c&oacute; nơi n&agrave;o lại c&oacute; thể bắt gặp được tương tự.<br />\r\n<br />\r\nMạnh rọi đ&egrave;n pin chiếu về hướng ấy, nh&igrave;n thật kĩ thứ nhấp nh&ocirc; sau những lớp đất đ&aacute; đổ dồn dưới ch&acirc;n đồi th&igrave; mới ph&aacute;t hiện ra đ&oacute; l&agrave; những thứ g&igrave;. Đ&oacute; l&agrave; những tấm bia miếu thờ c&uacute;ng, cụ thể l&agrave; những miếu thờ những người trước đ&acirc;y đ&atilde; từng bị đổ đ&egrave;o chết ở c&aacute;i đ&egrave;o cao n&agrave;y.<br />\r\n<br />\r\nNhững tấm bia nhấp nh&ocirc; san s&aacute;t nhau như một rừng bia mộ tại một khu nghĩa địa. Ch&acirc;n hương của những c&aacute;i b&aacute;t nhang đ&atilde; rụng rời v&igrave; từ l&acirc;u kh&ocirc;ng c&oacute; người thắp nhang, v&agrave; cũng l&agrave; v&igrave; mưa gi&oacute; ảnh hưởng khiến cho c&aacute;i b&aacute;t nhang tr&ocirc;ng c&agrave;ng ẩm ướt bẩn thỉu thiếu trang nghi&ecirc;m.<br />\r\n<br />\r\nNhững c&aacute;i miếu nhỏ được người d&acirc;n địa phương tạm thời lập n&ecirc;n để thờ c&uacute;ng cho những người xấu số đ&atilde; chết v&igrave; con đ&egrave;o cả đầy nguy hiểm n&agrave;y.<br />\r\n<br />\r\nMạnh thở d&agrave;i, thương cảm cho những người đồng bạn, đồng cảnh ngộ l&agrave;m nghề l&aacute;i xe như anh phải chết ở c&aacute;i nơi hoang vu hẻo l&aacute;nh. V&igrave; đồng cảm với cảnh ngộ của họ, Mạnh tiến tới gần chỗ những c&aacute;i miếu thờ, chọn một khu đất bằng phẳng nhất bắt đầu khấn v&aacute;i lầm rầm:<br />\r\n<br />\r\n- Thương cảm cho những con người xấu số phải bỏ mạng tại nơi n&agrave;y. T&ocirc;i l&agrave; Ph&ugrave;ng Đức Mạnh, cũng l&agrave; nghiệp l&aacute;i xe. Nay t&ocirc;i l&aacute;i xe qua đ&acirc;y kh&ocirc;ng may gặp phải sạt n&uacute;i, mới được dịp thắp cho anh em n&eacute;n nhang. Anh em nghe thấy t&ocirc;i khấn v&aacute;i th&igrave; xin thương lấy t&ocirc;i l&agrave; người c&ugrave;ng cảnh ngộ m&agrave; sau n&agrave;y ph&ugrave; hộ cho t&ocirc;i chở h&agrave;ng &ecirc;m xu&ocirc;i. Nếu được như vậy th&igrave; t&ocirc;i xin lạy tạ kh&oacute;i hương kh&ocirc;ng ngớt...<br />\r\n<br />\r\nMạnh lầm rầm khấn xong, tuy kh&ocirc;ng c&oacute; que nhang, nhưng vẫn c&oacute; mấy điếu thuốc l&aacute;. Anh cắm vội mấy điếu thuốc l&aacute; xuống miếng đất mềm thay cho que nhang rồi khấn v&aacute;i như thường.<br />\r\n<br />\r\nXong việc, mạnh đứng l&ecirc;n phủi lớp bụi d&iacute;nh v&agrave;o &aacute;o quần, rồi rảo bước quay trở lại chỗ chiếc xe tải của m&igrave;nh, tr&egrave;o l&ecirc;n cabin xe tải v&agrave; bắt đầu nổ m&aacute;y.<br />\r\n<br />\r\nSau khi quan s&aacute;t một hồi kh&aacute; l&acirc;u, Mạnh ph&aacute;t hiện con đường n&agrave;y đ&atilde; bị đất đ&aacute; chặn kh&ocirc;ng c&ograve;n một lối đi n&agrave;o c&oacute; thể l&aacute;ch được nữa cả. Tuy vậy, b&ecirc;n cạnh chỗ vừa bị sụt l&uacute;n, vừa vặn lại xuất hiện một lối m&ograve;n đường đất đ&acirc;m s&acirc;u v&agrave;o trong rừng c&acirc;y bụi.<br />\r\n<br />\r\nChuyến n&agrave;y Mạnh chở rất nhiều h&agrave;ng tr&ecirc;n xe, m&agrave; to&agrave;n l&agrave; h&agrave;ng dễ hỏng. Nếu kh&ocirc;ng thể chở h&agrave;ng cho kịp ng&agrave;y, th&igrave; to&agrave;n bộ số h&agrave;ng h&oacute;a tr&ecirc;n xe đều l&agrave; hoa quả sẽ bị thối rữa hết. Nếu như vậy th&igrave; Mạnh coi như trở th&agrave;nh trắng tay. Quay ngược lại cũng chết, m&agrave; đường đi tiếp th&igrave; cũng kh&ocirc;ng xong.<br />\r\n<br />\r\nCuối c&ugrave;ng Mạnh quyết định đ&aacute;nh liều thử cho chiếc xe chạy v&agrave;o lối m&ograve;n đ&acirc;m s&acirc;u v&agrave;o trong rừng &acirc;m u kia thử xem sao. Anh đo&aacute;n chừng lối m&ograve;n kia, rất c&oacute; thể l&agrave; con đường Trường Sơn huyền thoại, một con đường đ&acirc;m s&acirc;u nhiều lối rừng để tr&aacute;nh bom Mỹ, m&agrave; ch&iacute;nh bộ đội của ta lập n&ecirc;n để chi viện cho chiến tuyến miền Nam.<br />\r\n<br />\r\nNghĩ đoạn, kh&ocirc;ng chần chừ th&ecirc;m nữa, Mạnh đ&atilde; n&agrave;i xe ph&oacute;ng thẳng đến lối đường m&ograve;n. Đ&egrave;n pha xe tải đều được bật l&ecirc;n s&aacute;ng đến mức tối đa, với vận tốc xe thật chậm r&atilde;i để c&oacute; thể nh&igrave;n r&otilde; được con đường.<br />\r\n<br />\r\nD&ugrave; trước nay Mạnh đ&atilde; nhiều lần đi khắp tuyến đường m&ograve;n Hồ Ch&iacute; Minh n&agrave;y, thế nhưng đ&acirc;y cũng l&agrave; lần đầu ti&ecirc;n anh ph&aacute;t hiện ra c&aacute;i lối m&ograve;n lạ hoắc m&agrave; anh chưa nh&igrave;n thấy bao giờ như vậy.<br />\r\n<br />\r\nNghe những đồng bạn ở những chuyến xe kh&aacute;c thường kể rằng, họ cũng c&oacute; đ&ocirc;i lần gặp phải sạt n&uacute;i, v&agrave; cũng đều may mắn tho&aacute;t khỏi được t&igrave;nh trạng ấy nhờ việc đi v&agrave;o những lối đường m&ograve;n m&agrave; trước nay họ chưa từng đi. Những lối m&ograve;n ấy đa phần đều do bộ đội ta tạo ra ng&agrave;y xưa để chi viện cho chiến trường miền Nam, v&igrave; thế cho n&ecirc;n tuy đường c&oacute; thể v&ograve;ng v&egrave;o, nhưng về phương hướng th&igrave; tuyệt đối ch&iacute;nh x&aacute;c. Lựa chọn tuyến đường như vậy tuy hơi tốn thời gian hơn một ch&uacute;t nhưng rất an to&agrave;n.<br />\r\n<br />\r\nĐi s&acirc;u v&agrave;o ph&iacute;a trong con đường, dấu hiệu đường đất ng&agrave;y c&agrave;ng nhỏ dần.Thay v&agrave;o đ&oacute; l&agrave; những c&acirc;y bụi cứ li&ecirc;n tục chắn ngang đường khiến chiều rộng của con đường ng&agrave;y c&agrave;ng nhỏ.<br />\r\n<br />\r\nTuy vậy, con đường d&ugrave; nhỏ đến thế n&agrave;o th&igrave; cũng vẫn vừa đủ cho một chiếc xe tải đi v&agrave;o. Mạnh cứ n&agrave;i xe đi theo con đường ấy, quanh co đến mấy kh&uacute;c th&igrave; cuối c&ugrave;ng cũng đ&atilde; c&oacute; thế thấy một tia &aacute;nh s&aacute;ng lờ mờ ở ph&iacute;a cuối đường.<br />\r\n<br />\r\nAnh mừng rỡ tự reo l&ecirc;n:<br />\r\n<br />\r\n- Kh&ocirc;ng lẽ l&agrave; những chiếc xe của chiều tắc đối diện b&ecirc;n kia, nếu l&agrave; như vậy th&igrave; kh&ocirc;ng l&acirc;u nữa l&agrave; c&oacute; thể quay lại tuyến đường quốc lộ như dự kiến rồi!<br />\r\n<br />\r\nMạnh mừng lắm, anh đột ngột cho tăng vận tốc l&aacute;i xe l&ecirc;n đến gấp đ&ocirc;i. Hy vọng c&oacute; thể sớm gặp được chiếc xe ở chiều đối diện kia c&agrave;ng sớm c&agrave;ng tốt.<br />\r\n<br />\r\nChiếc xe lao nhanh v&ugrave; v&ugrave; ph&oacute;ng về ph&iacute;a trước, trong niềm hy vọng của Mạnh l&agrave; ở tại điểm s&aacute;ng cuối đường kia sẽ l&agrave; một chiếc xe n&agrave;o kh&aacute;c.<br />\r\n<br />\r\nBộp!<br />\r\n<br />\r\nChợt một thứ g&igrave; đ&oacute; đen đ&uacute;a cực kỳ to lớn, bao v&ugrave; qua nắp cabin xe, đập mạnh v&agrave;o nắp cabin xe khiến cho n&oacute;c th&ugrave;ng cabin bị l&otilde;m một mảng rất lớn.<br />\r\n<br />\r\nMạnh giật m&igrave;nh kh&ocirc;ng giữ được tay l&aacute;i, khiến chiếc xe bị chao đ&agrave;o li&ecirc;n tục như muốn chệch ra khỏi lối đường m&ograve;n. Sau c&ugrave;ng anh gắng sức đấu tranh với c&aacute;i b&aacute;nh l&aacute;i, cuối c&ugrave;ng cũng giữ được cho chiếc xe ổn định trở lại v&agrave; quay trở lại lối đường m&ograve;n một c&aacute;ch &ecirc;m xu&ocirc;i.<br />\r\n<br />\r\nL&uacute;c n&agrave;y Mạnh định thần lại được, tự động thốt l&ecirc;n một c&acirc;u:<br />\r\n<br />\r\n- C&aacute;i qu&aacute;i g&igrave; thế nhỉ?</p>', NULL, 16, 1, '2020-09-10 07:59:31', NULL);
INSERT INTO `chapters` (`id`, `name`, `subname`, `content`, `view`, `story_id`, `user_id`, `created_at`, `updated_at`) VALUES
(40, 'Ma hốc củi', 'Chương 3', '<p><em>(Truyện ngắn nằm trong series truyện ngắn kinh dị Ma Rừng của t&aacute;c giả Ngạ Quỷ)</em><br />\r\n<br />\r\nQu&atilde;ng thời gian lịch sử m&agrave; nh&acirc;n loại đ&atilde; trải qua, k&eacute;o d&agrave;i v&ocirc; số năm th&aacute;ng. Cũng k&egrave;m theo đ&oacute; l&agrave; sự xuất hiện những t&iacute;ch chuyện hết sức ly kỳ m&agrave; mỗi một v&ugrave;ng đất đều tự t&ocirc; vẽ th&ecirc;m về sự huyền ảo của n&oacute;.<br />\r\n<br />\r\nRừng r&uacute; c&oacute; lắm ma cỏ, người xu&ocirc;i c&oacute; những con ma chết oan uổng đội mồ sống lại để t&igrave;m kẻ &aacute;c đ&atilde; reo rắc tội lỗi cho họ. Đi đ&acirc;u cũng đều gặp những chuyện mang sắc m&agrave;u đầy yếu tố t&acirc;m linh n&ecirc;n xuất hiện lắm kẻ sinh ra c&aacute;i tục truyền miệng về những điều trong c&otilde;i huyền ảo.<br />\r\n<br />\r\nTrong những c&acirc;u chuyện được truyền miệng ấy, lại c&oacute; thực c&oacute; hư kh&ocirc;ng biết r&otilde; sao m&agrave; tả. Nhưng nếu n&oacute; đ&atilde; tồn tại th&igrave; ắt hẳn l&agrave; phải c&oacute; nguy&ecirc;n nh&acirc;n, hoặc ch&iacute; &iacute;t ra th&igrave; kẻ đ&atilde; bịa đặt ra những thứ ấy để th&ecirc;m mắm th&ecirc;m muối v&agrave;o cho ly kỳ cũng phải nghe từ tai của một kẻ kh&aacute;c.<br />\r\n<br />\r\nKể đến đồng bằng th&igrave; c&oacute; giống lo&agrave;i ma nước, ma da, ma thần v&ograve;ng, ma gọi hồn&hellip; M&agrave; v&ugrave;ng cao th&igrave; cũng c&oacute; lắm loại ma rừng như ma c&agrave; rồng, ma th&aacute;c đuối, ma hốc củi&hellip; Những lo&agrave;i ma n&agrave;y đều mang trong m&igrave;nh những đặc t&iacute;nh kh&aacute;c nhau, nhưng chung quy ra th&igrave; c&aacute;i giống lo&agrave;i ma quỷ nếu đ&atilde; c&ograve;n tr&ecirc;n nh&acirc;n thế ắt hẳn đa phần sẽ quấy quả người sống theo c&aacute;ch của ch&uacute;ng. Quấy quả ở đ&acirc;y c&oacute; thể hiểu theo nghĩa tốt, hoặc theo nghĩa xấu. Nghĩa tốt l&agrave; d&ugrave; ch&uacute;ng c&oacute; chủ t&acirc;m hại người, nhưng sau c&ugrave;ng lại muốn th&uacute;c đẩy con người ta đi đến c&aacute;i thiện để sửa đổi bản th&acirc;n. Nghĩa xấu l&agrave; ch&uacute;ng t&aacute;ng tận d&ugrave;ng thứ năng lực huyền b&iacute; để &aacute;m ảnh, để giết hại, hoặc chỉ l&agrave; một thứ th&oacute;i quen khi l&uacute;c ch&uacute;ng c&ograve;n sống thường hay l&agrave;m vậy, v&ocirc; t&igrave;nh g&acirc;y n&ecirc;n sự kh&oacute; chịu cho người kh&aacute;c.<br />\r\n<br />\r\nNơi v&ugrave;ng cao tồn tại một lo&agrave;i ma quỷ thường xuy&ecirc;n g&acirc;y n&ecirc;n sự kh&oacute; chịu cho d&acirc;n bản xứ. Ch&uacute;ng kh&ocirc;ng c&oacute; &aacute;c &yacute;, nhưng đ&ocirc;i khi sự đ&ugrave;a nghịch một c&aacute;ch th&aacute;i qu&aacute; của ch&uacute;ng đ&atilde; g&acirc;y n&ecirc;n những tội &aacute;c đ&aacute;ng tr&aacute;ch.<br />\r\n<br />\r\n&hellip;<br />\r\n<br />\r\nB&agrave; Chắn dốc g&aacute;nh củi địu l&ecirc;n lưng, rồi b&agrave;n ch&acirc;n b&agrave; nhanh thoăn thoắt đi theo lối đường m&ograve;n ở ven dốc đồi, chạy h&igrave; hục xuống dưới ch&acirc;n đồi để kịp về nh&agrave; trước khi trời tối.<br />\r\n<br />\r\nB&agrave; Chắn năm nay đ&atilde; ngo&agrave;i năm mươi tuổi, vốn cũng đ&atilde; n&ecirc;n nghỉ ngơi ở nh&agrave; với độ tuổi n&agrave;y. Bởi v&igrave; b&agrave; đ&atilde; c&oacute; con, c&oacute; ch&aacute;u. Con b&agrave; cũng ba mươi mấy tuổi đầu rồi, ch&aacute;u b&agrave; th&igrave; cũng đ&atilde; ngấp ngh&eacute; sắp lớn cao bằng ngọn cỏ lau.<br />\r\n<br />\r\nNhưng b&agrave; kh&ocirc;ng nghỉ ngơi, l&agrave; bởi v&igrave; nh&agrave; b&agrave; ngh&egrave;o. Con trai b&agrave; lấy vợ đ&atilde; l&acirc;u m&agrave; chỉ sinh được đứa ch&aacute;u g&aacute;i, kh&ocirc;ng c&oacute; ch&aacute;u trai khỏe mạnh để đi rừng thay b&agrave;. Con trai b&agrave; t&ecirc;n A Sinh, l&agrave; một người đẹp trai khỏe mạnh trong bản.<br />\r\n<br />\r\nA Sinh từ khi lấy vợ th&igrave; chẳng mấy khi ở nh&agrave; m&agrave; to&agrave;n xuống n&uacute;i để đi l&agrave;m thu&ecirc;. Nhưng cũng ch&iacute;nh v&igrave; A Sinh đi l&agrave;m thu&ecirc;, n&ecirc;n cuộc sống của gia đ&igrave;nh b&agrave; mới kh&ocirc;ng bị thiếu đ&oacute;i, kh&ocirc;ng giống như hầu hết c&aacute;c hộ trong bản, chỉ quanh năm m&ugrave;a rẫy n&ecirc;n kh&ocirc;ng đủ ăn.<br />\r\n<br />\r\nSắc trời dần ngả sang m&agrave;u v&agrave;ng &oacute;ng, rồi chuyển mỡ g&agrave;, lại đến đỏ au. Đấy ch&iacute;nh l&agrave; &aacute;nh ho&agrave;ng h&ocirc;n theo c&aacute;ch mặt trời lặn ở v&ugrave;ng n&agrave;y.<br />\r\n<br />\r\nỞ đ&acirc;y người ta c&oacute; tục thường tối kị kh&ocirc;ng hay về muộn, đặc biệt l&agrave; trời v&agrave;o đ&ecirc;m th&igrave; họ sẽ kh&ocirc;ng bao giờ băng qua rừng, lội qua suối. Họ cho rằng nếu họ l&agrave;m như vậy l&agrave; phải tội với thần rừng, sẽ bị ma rừng theo đuổi, ph&aacute; hại.<br />\r\n<br />\r\nKhi đ&ecirc;m xuống, nếu chưa kịp về bản, th&igrave; tốt nhất l&agrave; n&ecirc;n t&igrave;m một c&aacute;i ch&ograve;i canh rẫy n&agrave;o gần đấy, chui v&agrave;o đ&oacute; đốt lửa ngủ tạm. Nếu trốn trong ch&ograve;i canh rẫy v&agrave; c&oacute; &aacute;nh lửa, lũ ma rừng sẽ kh&ocirc;ng biết rốt cuộc trong chiếc ch&ograve;i đ&oacute; c&oacute; bao nhiều người, v&igrave; vậy m&agrave; kh&ocirc;ng d&aacute;m giở tr&ograve; tr&ecirc;u chọc.<br />\r\n<br />\r\nKhi b&agrave; Chắn ở tr&ecirc;n đỉnh đồi, cạnh m&eacute;p rừng tre th&igrave; trời đ&atilde; đổ mỡ g&agrave;. V&agrave; giờ đ&acirc;y khi b&agrave; xuống đến lưng chừng đồi, th&igrave; sắc mặt trời đ&atilde; chuyển sang đỏ au. Vậy c&oacute; nghĩa l&agrave; đ&ecirc;m nay b&agrave; kh&ocirc;ng c&ograve;n kịp về nh&agrave; nữa. V&igrave; từ lưng chừng đồi cho tới bản, đi cũng phải mất hơn một tiếng đồng hồ, c&oacute; về kiểu g&igrave; cũng d&iacute;nh phải trời đ&ecirc;m.<br />\r\n<br />\r\nB&agrave; Chắn suy nghĩ hồi l&acirc;u, b&egrave;n g&aacute;c b&oacute; củi để tạm qua một chỗ, rồi bắt đầu đảo mắt nh&igrave;n quanh t&igrave;m rẫy. Khu vực n&agrave;y đ&atilde; l&agrave; ở rất xa bản Cọ, vốn rất &iacute;t người sẽ tới đ&acirc;y để canh t&aacute;c. Thế nhưng kh&ocirc;ng hẳn l&agrave; kh&ocirc;ng c&oacute; người l&agrave;m nương ở đ&acirc;y. Qua sắc s&aacute;ng nh&aacute; nhem của trời chiều muộn, b&agrave; Chắn c&oacute; thể dễ d&agrave;ng nom thấy c&aacute;ch đấy kh&ocirc;ng xa, giữa một đ&aacute;m c&acirc;y bụi um t&ugrave;m c&oacute; một c&aacute;i ch&ograve;i canh nương cũ kĩ. C&aacute;i ch&ograve;i ấy ẩn nấp sau đ&aacute;m c&acirc;y dại, rất kh&oacute; m&agrave; nh&igrave;n thấy. Nhưng đối với một người đi rừng tinh tường như b&agrave; Chắn th&igrave; kh&ocirc;ng kh&oacute; để nhận ra nơi đ&oacute; c&oacute; một c&aacute;i ch&ograve;i.<br />\r\n<br />\r\nC&aacute;i ch&ograve;i cao độ đầu người, s&agrave;n c&aacute;ch mặt đất chỉ độ bảy mươi xen ti m&eacute;t. Độ cao như thế chỉ để người canh nương tr&aacute;nh những loại d&atilde; th&uacute; nhỏ.<br />\r\n<br />\r\nB&agrave; Chắn kh&ocirc;ng ngần ngại đi thẳng về ph&iacute;a ấy, d&ugrave;ng dao ph&aacute;t tạm đ&aacute;m c&acirc;y bụi cho lộ ra một lối đường đi quang đ&atilde;ng. C&aacute;i ch&ograve;i cũ kĩ dần lộ ra trước mắt b&agrave;. Gỗ ch&ograve;i đ&atilde; chuyển m&agrave;u đen, trong ch&ograve;i sực nức l&ecirc;n m&ugrave;i nấm mốc rất kh&oacute; chịu.<br />\r\n<br />\r\nC&aacute;i ch&ograve;i n&agrave;y hẳn l&agrave; một c&aacute;i ch&ograve;i canh nương v&agrave;o m&ugrave;a, m&agrave; trước đ&acirc;y người chủ của n&oacute; đ&atilde; dựng tạm l&ecirc;n. Chỉ c&oacute; điều nh&igrave;n t&igrave;nh cảnh n&agrave;y th&igrave; b&atilde;i nương n&agrave;y đ&atilde; bị bỏ hoang đến mấy m&ugrave;a rẫy, bằng kh&ocirc;ng đ&aacute;m cỏ dại cũng chẳng thể mọc tốt được đến như thế.<br />\r\n<br />\r\nGiữa khu rừng vắng, lại c&oacute; một căn ch&ograve;i nghiễm nhi&ecirc;n đứng sừng sững, khiến cho người ta c&oacute; cảm gi&aacute;c hơi u tịch. C&aacute;i m&agrave;u đỏ au của bầu trời với c&aacute;i sắc tối đen của kh&ocirc;ng gian khiến người ta nh&igrave;n v&agrave;o m&agrave; chỉ như muốn ngộp thở bởi những khung cảnh qu&aacute; đỗi b&iacute; b&aacute;ch.<br />\r\n<br />\r\nB&agrave; Chắn mau lẹ mở c&aacute;nh cửa gỗ, rồi chui tọt v&agrave;o trong ch&ograve;i. Lại đem mồi lửa ra nh&oacute;m tạm v&agrave;i thanh củi. G&aacute;nh củi n&agrave;y vừa đủ để gia đ&igrave;nh b&agrave; Chắn sống qua c&aacute;i lạnh trong v&ograve;ng một tuần. Vốn ra th&igrave; chỉ đi một h&ocirc;m l&agrave; c&oacute; đủ củi, m&agrave; vẫn về kịp. Nhưng h&ocirc;m nay cũng bởi v&igrave; b&agrave; Chắn vướng bận một chuyện n&ecirc;n th&agrave;nh ra mới về muộn như thế.<br />\r\n<br />\r\nChuyện l&agrave; hồi chiều b&agrave; đi ngang qua con suối gần rừng nứa, b&agrave; ph&aacute;t hiện c&oacute; một con nai đang bị mắc kẹt ở một c&aacute;i khe đ&aacute; dưới suối. N&oacute; v&ugrave;ng vẫy cố tho&aacute;t hồi l&acirc;u m&agrave; kh&ocirc;ng được n&ecirc;n đ&atilde; thấm mệt v&agrave; nằm gục xuống mặt nước. B&agrave; Chắn khi đi qua chỗ ấy thấy con nai bị mắc kẹt th&igrave; mừng lắm. B&agrave; vụng về tr&egrave;o ra chỗ khe đ&aacute; nơi con nai bị mắc kẹt, t&iacute;nh lấy d&acirc;y rừng buộc v&agrave;o cổ n&oacute;. Rồi gỡ cho ch&acirc;n n&oacute; khỏi mắc kẹt. Lại đem n&oacute; về nh&agrave; th&igrave; cũng được một bữa thịt thơm ngon, c&ograve;n c&oacute; thể b&aacute;n được ch&uacute;t tiền nữa.<br />\r\n<br />\r\nNhưng con nai đ&oacute; quả thật l&agrave; kh&ocirc;n lanh, ban đầu n&oacute; giả vờ ngoan ngo&atilde;n nằm im để b&agrave; Chắn gỡ c&aacute;i ch&acirc;n bị mắc kẹt của n&oacute; ra khỏi khe đ&aacute;. Rồi ngay sau đ&oacute;, chỉ chờ khi tho&aacute;t được c&aacute;i ch&acirc;n ra l&agrave; n&oacute; đ&atilde; v&ugrave;ng l&ecirc;n lồng lộn chạy như bay. B&agrave; Chắn cũng đ&atilde; c&oacute; tuổi, vốn sức khỏe cũng đ&atilde; c&oacute; phần giảm s&uacute;t. Thế nhưng kh&ocirc;ng chỉ v&igrave; l&yacute; do ấy m&agrave; b&agrave; l&agrave;m vuột mất con nai. M&agrave; l&yacute; do b&agrave; l&agrave;m vuột mất con nai ấy l&agrave; bởi v&igrave; qu&aacute; đỗi bất ngờ trước h&agrave;nh động của n&oacute;. N&oacute; vụt khỏi tay b&agrave; khiến cho b&agrave; ng&atilde; chới theo, đầu đập xuống mặt đ&aacute;, m&aacute;u chảy be b&eacute;t. Cũng c&ograve;n đỡ l&agrave; trong c&aacute;i rủi c&ograve;n c&oacute; c&aacute;i may. B&agrave; bị đập đầu v&agrave;o đ&aacute; nhưng kh&ocirc;ng bị thương nặng, chỉ bị xước s&aacute;t nhẹ sơ sơ.<br />\r\n<br />\r\nV&igrave; vừa vụt mất con nai đang tiến ngẩn tiếc ngơ n&ecirc;n b&agrave; Chắn cứ ngồi y&ecirc;n một chỗ như mất hồn một l&uacute;c l&acirc;u. Sau đ&oacute; lại c&ograve;n phải tốn thời gian đi t&igrave;m mớ l&aacute; nhai vụn đắp l&ecirc;n vết thương ở tr&aacute;n để cầm m&aacute;u. Th&agrave;nh ra một buổi đi rừng lần n&agrave;y khiến b&agrave; Chắn bị lỡ mất giờ về sớm.<br />\r\n<br />\r\nGiờ n&agrave;y ngồi trong căn ch&ograve;i, b&agrave; chắn đem b&atilde; l&aacute; nhai vụn gỡ xuống để xem x&eacute;t. M&aacute;u tr&ecirc;n tr&aacute;n b&agrave; đ&atilde; rịt hẳn kh&ocirc;ng c&ograve;n chảy nữa, nhưng vẫn c&ograve;n rất đau. B&agrave; Chắn ngồi tựa v&agrave;o m&eacute;p ch&ograve;i l&agrave;u b&agrave;u đi l&agrave;u b&agrave;u lại tr&aacute;ch cứ m&igrave;nh dại dột. Đ&aacute;ng lẽ ra b&agrave; phải n&ecirc;n cẩn trọng buộc một đầu d&acirc;y v&agrave;o một nơi chắc chắn, rồi mới gỡ c&aacute;i ch&acirc;n bị mắc kẹt cho con nai.<br />\r\n<br />\r\nNghĩ đi nghĩ lại th&igrave; mọi sự dại dột cũng đ&atilde; qua rồi, b&agrave; Chắn chỉ c&oacute; thể ngồi đ&oacute; than v&atilde;n m&agrave; th&ocirc;i.<br />\r\n<br />\r\nB&agrave; Chắn r&uacute;t từ đ&aacute;y g&aacute;nh gủi ra v&agrave;i thanh củi nhỏ, chất th&agrave;nh đống cho ngay ngắn rồi cho mồi lửa đốt l&ecirc;n. Đ&ecirc;m tối ở nơi vắng vẻ, lại kh&ocirc;ng chuẩn bị th&ecirc;m đồ ăn để sẵn. B&agrave; Chắn chỉ c&oacute; thể ngồi &ocirc;m c&aacute;i bụng đ&oacute;i đang s&ocirc;i r&eacute;o l&ecirc;n s&ugrave;ng sục, cố nhắm chặt mắt lại để ngủ. Mong sao giấc ngủ c&oacute; thể xua tan đi được c&aacute;i đ&oacute;i, c&aacute;i lạnh.<br />\r\n<br />\r\nĐ&ecirc;m vắng, tiếng động loẹt quẹt b&ecirc;n ngo&agrave;i căn ch&ograve;i cứ vang l&ecirc;n đều đều. Rồi d&acirc;n dần chợt xuất hiện &acirc;m thanh như tiếng bước ch&acirc;n người tiến đến gần căn ch&ograve;i. Nhưng những bước ch&acirc;n ấy lại chỉ d&aacute;m lởn vởn ở b&ecirc;n ngo&agrave;i chứ kh&ocirc;ng d&aacute;m tiến th&ecirc;m. Tiếng th&igrave; thầm b&agrave;n t&aacute;n x&ocirc;n xao vang l&ecirc;n đều đặn như của người n&oacute;i chuyện với nhau. B&agrave; Chắn nghe thấy hết, nhưng vẫn giả vờ nằm im kh&ocirc;ng hề ph&aacute;t ra một tiếng động, chỉ cố nh&eacute;t th&ecirc;m củi v&agrave;o đ&aacute;m than để lửa ch&aacute;y đượm thật to.<br />\r\n<br />\r\nMột l&uacute;c sau, c&aacute;i tiếng động th&igrave; th&agrave;o ban n&atilde;y mới dần biến mất, tiếng bước ch&acirc;n cũng nhỏ hẳn v&agrave; xa dần. B&agrave; Chắn đo&aacute;n rằng đ&oacute; l&agrave; lũ ma rừng, ch&uacute;ng đi tuần đ&ecirc;m v&agrave; chỉ chờ c&oacute; người n&agrave;o lạc l&otilde;ng l&agrave; sẽ bắt mất hồn, đem hồn về x&agrave;o huyệt của ch&uacute;ng ở trong n&uacute;i để giết hại.<br />\r\n<br />\r\nLũ ma rừng ấy thường rất &aacute;c độc, ch&uacute;ng h&agrave;nh động t&agrave;n bạo v&agrave; vi phạm những lu&acirc;n thường đạo l&yacute; của tự nhi&ecirc;n. Ch&uacute;ng l&agrave; những kẻ chết oan uổng, những kẻ c&oacute; t&iacute;nh c&aacute;ch lập dị v&ocirc; t&igrave;nh chết, hoặc đ&ocirc;i khi cũng chỉ l&agrave; những tay thợ săn chết bởi lũ lợn l&ograve;i hiện hồn lởn vởn xung quanh để dọa dẫm người.<br />\r\n<br />\r\nLũ ma rừng thường đi th&agrave;nh bầy th&agrave;nh lũ, ch&uacute;ng gặp kẻ bơ vơ th&igrave; sẽ dụ giỗ cho v&agrave;o th&aacute;c đ&aacute;, cho tự u m&ecirc; m&agrave; đập đầu v&agrave;o đ&aacute; chết đi để người sống nhập bọn với ch&uacute;ng. C&ograve;n khi gặp nhiều người đi c&ugrave;ng nhau, th&igrave; ch&uacute;ng kh&ocirc;ng d&aacute;m tiến lại gần. V&igrave; khi ấy người ta c&oacute; thể cảnh gi&aacute;c cho nhau, một kẻ u m&ecirc; th&igrave; c&oacute; thể sẽ được một kẻ kh&aacute;c cảnh tỉnh. Lũ ma rừng thường chỉ l&agrave; những loại ma vất vưởng n&ecirc;n kh&ocirc;ng c&oacute; nhiều năng lực để c&oacute; thể đem nhiều người c&ugrave;ng dụ giỗ. V&igrave; l&yacute; do ấy, khi gặp một nh&oacute;m người đ&ocirc;ng ở lại rừng, ch&uacute;ng sẽ chỉ đi nh&igrave;n qua rồi bỏ đi. Giống như một cục xương kh&oacute; gặm m&agrave; vẫn muốn nh&igrave;n ngắm một lần cho thỏa.<br />\r\n<br />\r\nLần n&agrave;y, ch&uacute;ng đi ngang qua căn ch&ograve;i v&agrave; c&oacute; cảm gi&aacute;c l&agrave; c&oacute; người sống. Nhưng lại kh&ocirc;ng r&otilde; trong ch&ograve;i c&oacute; bao nhi&ecirc;u người. &Aacute;nh lửa ph&aacute;t ra mạnh n&ecirc;n mắt của lo&agrave;i ma rừng bị l&oacute;a. Thế n&ecirc;n v&igrave; kh&ocirc;ng đo&aacute;n biết được, vừa rồi bọn ch&uacute;ng cũng chỉ d&aacute;m lượn lờ một chốc quanh căn ch&ograve;i rồi bỏ đi ngay.<br />\r\n<br />\r\nB&agrave; Chắn nghĩ lại, thường ng&agrave;y b&agrave; vẫn hay đem những chuyện n&agrave;y kể lại cho đứa ch&aacute;u g&aacute;i nhỏ nghe. Nhưng b&agrave; cũng chỉ muốn dọa nạt n&oacute;, chứ đời b&agrave; chưa bao giờ gặp phải chuyện ấy. Vậy m&agrave; đ&ecirc;m nay ch&iacute;nh tai b&agrave; đ&atilde; nghe thấy lũ ma rừng b&agrave;n t&aacute;n với nhau, lại c&ograve;n ở trong ch&iacute;nh c&aacute;i t&igrave;nh cảnh m&agrave; l&uacute;c n&agrave;o cũng c&oacute; thể bị ch&uacute;ng bức hại.<br />\r\n<br />\r\nB&agrave; Chắn cố nhắm mắt lại, mong sao trời đ&ecirc;m c&oacute; thể mau ch&oacute;ng tr&ocirc;i qua để b&agrave; c&ograve;n c&oacute; thể về nh&agrave;. Thi thoảng, ở b&ecirc;n ngo&agrave;i căn ch&ograve;i, lũ ma rừng lại c&oacute; lượn lờ quay lại, nhưng cũng một chốc rồi lại bỏ đi ngay. Những khi ấy, b&agrave; Chắn phải nhanh tr&iacute; dụi củi cho lửa ch&aacute;y thật to để dọa cho ch&uacute;ng sợ, rồi mới d&aacute;m ngủ tiếp.<br />\r\n<br />\r\nĐến s&aacute;ng, b&agrave; Chắn vẫn đang trong t&igrave;nh trạng mơ mơ m&agrave;ng m&agrave;ng. Khi tia nắng mai đầu ti&ecirc;n chiếu rọi xuống, l&agrave; b&agrave; Chắn đ&atilde; ngồi d&acirc;y v&agrave; thu lượm lại hết chỗ củi c&ograve;n thừa lại đ&ecirc;m qua để đem về nh&agrave;. Chỉ qua một đ&ecirc;m th&ocirc;i m&agrave; số lượng củi b&agrave; Chắn đốt mất đ&atilde; kha kh&aacute;. B&agrave; đang nghĩ ngợi xem m&igrave;nh c&oacute; n&ecirc;n quay lại rừng nứa một lần nữa để lấy th&ecirc;m củi. Nhưng khi b&agrave; bước ra khỏi căn ch&ograve;i, bước xuống mặt đất th&igrave; b&agrave; đ&atilde; nom thấy c&oacute; một g&aacute;nh củi sẵn ở đ&oacute; dựa s&aacute;t v&agrave;o m&eacute;p ch&ograve;i.<br />\r\n<br />\r\nB&agrave; Chắn reo l&ecirc;n:<br />\r\n<br />\r\n- May mắn cho t&ocirc;i rồi!<br />\r\n<br />\r\nB&agrave; kh&ocirc;ng nghĩ ngợi liền đem b&oacute; củi đ&oacute; hợp với b&oacute; củi của m&igrave;nh, tạo th&agrave;nh một b&oacute; lớn. B&agrave; địu g&aacute;nh củi tr&ecirc;n lưng rồi phăm phăm bước theo lối đường m&ograve;n ở sườn đồi về bản.<br />\r\n&nbsp;</p>', NULL, 16, 1, '2020-09-10 10:37:43', NULL),
(41, 'Lý Thất Dạ 1', 'Chương 1', '<p>asd</p>', NULL, 7, 1, '2020-09-10 10:37:43', NULL),
(42, 'Dã chủng của người đàn ông lạ mặt', 'Chương 2', '<p>&quot;N&oacute;i cho m&agrave;y hay! Tối đ&oacute; m&agrave;y uống rượu do tao chế, đừng n&oacute;i dục cầu bất m&atilde;n, tao đ&atilde; c&oacute; l&ograve;ng tốt t&igrave;m cho m&agrave;y hai t&ecirc;n cao to rồi, ai ngờ m&agrave;y lại kh&ocirc;ng biết tốt xấu, tự dưng đi chui v&agrave;o ph&ograve;ng của thằng n&agrave;o đ&oacute;, đ&atilde; thế c&ograve;n kh&ocirc;ng ch&uacute;t kh&aacute;ch kh&iacute; c&ugrave;ng người ta...&quot; Ninh Tuyết Lạc ch&aacute;n gh&eacute;t mở miệng: &quot;Anh Diễn c&oacute; l&ograve;ng tốt, sợ m&agrave;y tỉnh dậy kh&ocirc;ng chịu nổi n&ecirc;n mới n&oacute;i người c&ugrave;ng m&agrave;y tối h&ocirc;m đ&oacute; l&agrave; anh ấy th&ocirc;i!&quot;<br />\r\n<br />\r\n&quot;M&agrave;y...&quot; Ninh Tịch tức run l&ecirc;n, nghe tới đ&acirc;y, c&ocirc; kh&ocirc;ng nhịn nổi nữa, liền b&oacute;p chặt lấy cổ tay Ninh Tuyết Lạc: &quot;Sao m&agrave;y lại l&agrave;m thế với tao? Tại sao! M&agrave;y hại tao như thế c&ograve;n chưa đủ sao?&rdquo;<br />\r\n<br />\r\nNinh Tuyết Lạc mới đầu c&ograve;n c&acirc;ng c&acirc;ng l&ecirc;n, định đẩy Ninh Tịch ra, đ&uacute;ng l&uacute;c đ&oacute; lại thấy T&ocirc; Diễn ph&iacute;a sau Ninh Tịch, giọng điệu lập tức nhũn xuống, tỏ ra điềm đạm đ&aacute;ng y&ecirc;u: &quot;Chị, em biết em sai rồi, chị muốn đ&aacute;nh muốn mắng em thế n&agrave;o cũng được, nhưng đừng tr&aacute;ch anh Diễn...&quot;<br />\r\n<br />\r\nNinh Tịch ngẩn ra, ngay sau đ&oacute;, lại thấy Ninh Tuyết Lạc tự nhi&ecirc;n ng&atilde; lăn ra đất, tr&ocirc;ng như bị c&ocirc; đẩy vậy.<br />\r\n<br />\r\n&quot;Ninh Tịch! Em l&agrave;m g&igrave; vậy......&quot; Tiếng quở tr&aacute;ch vang l&ecirc;n.</p>\r\n\r\n<p>Ninh Tịch ngạc nhi&ecirc;n quay lại li&ecirc;n thấy vẻ mặt lạnh l&ugrave;ng của T&ocirc; Diễn.<br />\r\n<br />\r\nT&ocirc; Diễn l&aacute;ch qua người c&ocirc;, đỡ Ninh Tuyết Lạc dậy: &quot;Tuyết Lạc, em kh&ocirc;ng sao chứ?&quot;<br />\r\n<br />\r\nCả người Ninh Tuyết Lạc như treo tr&ecirc;n người T&ocirc; Diễn: &quot;Anh Diễn, em kh&ocirc;ng cố &yacute;, em thật sự biết sai rồi... Chuyện th&agrave;nh ra thế n&agrave;y đều tại em c&oacute; lỗi với chị...&quot;<br />\r\n<br />\r\n&quot;Được rồi, c&oacute; chuyện g&igrave; cũng c&oacute; anh ở đ&acirc;y rồi!&quot; T&ocirc; Diễn vỗ vai Ninh Tuyết Lạc, đưa c&ocirc; l&ecirc;n xe: &quot;Để anh n&oacute;i r&otilde; với Ninh Tịch.&quot;<br />\r\n<br />\r\nĐầu Ninh Tịch trống rỗng, c&ocirc; thấy T&ocirc; Diễn đi về ph&iacute;a m&igrave;nh, rồi lại thấy miệng T&ocirc; Diễn cứ mở ra rồi kh&eacute;p lại.<br />\r\n<br />\r\nAnh ta n&oacute;i g&igrave; đ&oacute; rất l&acirc;u.<br />\r\n<br />\r\nN&oacute;i về việc những năm th&aacute;ng họ l&agrave; thanh mai tr&uacute;c m&atilde;, n&oacute;i về việc anh đ&atilde; đấu tranh thế n&agrave;o khi y&ecirc;u Ninh Tuyết Lạc, n&oacute;i anh cũng tức giận khi biết Ninh Tuyết Lạc hại c&ocirc;, n&oacute;i về việc anh cảm thấy rất khiếp sợ v&agrave; &aacute;y n&aacute;y khi biết Ninh Tịch mang thai, n&oacute;i anh đ&atilde; chấp nhận lời xin lỗi của Ninh Tuyết Lạc...<br />\r\n<br />\r\nCuối c&ugrave;ng, anh n&oacute;i: &quot;Ninh Tịch, xin lỗi, anh kh&ocirc;ng thể lấy em. Kh&ocirc;ng phải anh gh&eacute;t bỏ em v&igrave; chuyện ng&agrave;y h&ocirc;m đ&oacute; hay đứa trẻ trẻ n&agrave;y, m&agrave; v&igrave; anh kh&ocirc;ng thể phụ l&ograve;ng Tuyết Lạc, anh cũng kh&ocirc;ng thể lại lừa dối t&igrave;nh cảm của ch&iacute;nh m&igrave;nh th&ecirc;m nữa.&rdquo;<br />\r\n<br />\r\nMấy th&aacute;ng nay, g&atilde; đều đưa Ninh Tuyết Lạc ra nước ngo&agrave;i, sớm chiều đều d&iacute;nh lấy nhau kh&ocirc;ng rời, mặc d&ugrave; bề ngo&agrave;i g&atilde; v&igrave; muốn b&ugrave; đắp lại tổn thương cho Ninh Tịch n&ecirc;n mới nhận tối h&ocirc;m đ&oacute; ch&iacute;nh g&atilde; đ&atilde; quan hệ với c&ocirc;, nhưng từ tận s&acirc;u trong đ&aacute;y l&ograve;ng, g&atilde; đ&atilde; sớm chọn Ninh Tuyết Lạc rồi.</p>\r\n\r\n<p>Thế n&ecirc;n sau khi biết tin Ninh Tịch mang thai, g&atilde; kh&ocirc;ng đ&agrave;nh l&ograve;ng tới Ninh gia n&oacute;i r&otilde; chuyện n&agrave;y với hai b&aacute;c m&agrave; muốn n&oacute;i r&otilde; sự thật với Ninh Tịch trước.<br />\r\n<br />\r\n&quot;N&oacute;i vậy c&oacute; nghĩa l&agrave;... T&ocirc; Diễn... Ngay từ đầu anh đ&atilde; biết việc Ninh Tuyết Lạc bỏ thuốc để hủy hoại trinh tiết của t&ocirc;i? V&igrave; muốn bao che cho n&oacute;, n&ecirc;n anh mới n&oacute;i với t&ocirc;i người đ&agrave;n &ocirc;ng tối h&ocirc;m đ&oacute; l&agrave; anh?&quot; Ninh Tịch cuối c&ugrave;ng cũng t&igrave;m về được giọng n&oacute;i của m&igrave;nh, hồn bay ph&aacute;ch lạc nh&igrave;n vẻ mặt b&igrave;nh tĩnh của T&ocirc; Diễn.<br />\r\n<br />\r\n&quot;Ninh Tịch, Tuyết Lạc c&ocirc; ấy kh&ocirc;ng cố t&igrave;nh, c&ocirc; ấy c&ograve;n nhỏ, t&iacute;nh t&igrave;nh manh động....&quot;<br />\r\n<br />\r\n&quot;Vậy c&ograve;n t&ocirc;i th&igrave; sao?&quot; Ninh Tịch ngẩng l&ecirc;n nh&igrave;n T&ocirc; Diễn, tr&agrave;n đầy tuyệt vọng: &quot;C&aacute;c người c&oacute; từng nghĩ tới t&ocirc;i một ch&uacute;t n&agrave;o kh&ocirc;ng?&quot;<br />\r\n<br />\r\nT&ocirc; Diễn c&acirc;m bặt, một l&uacute;c sau, hắn mới vươn tay k&eacute;o Ninh Tịch: &quot;Ở đ&acirc;y nắng qu&aacute;, ch&uacute;ng ta về nh&agrave; trước đ&atilde;...&quot;<br />\r\n<br />\r\n&quot;Đừng c&oacute; chạm v&agrave;o t&ocirc;i...&quot; Ninh Tịch hất tay T&ocirc; Diễn ra, c&ocirc; bỗng cười ph&aacute; l&ecirc;n.<br />\r\n<br />\r\nSống tới tận giờ, Ninh Tịch bỗng cảm thấy cuộc sống của m&igrave;nh như một tr&ograve; hề.<br />\r\n<br />\r\nĐể tới được th&agrave;nh phố của T&ocirc; Diễn, c&ocirc; đ&atilde; cố gắng thi v&agrave;o được đại học B.Để lấy l&ograve;ng T&ocirc; Diễn, c&ocirc; từ bỏ cả ước ở l&agrave;m diễn vi&ecirc;n của m&igrave;nh.<br />\r\n<br />\r\nĐể m&ocirc;n đăng hộ đối với anh ta, c&ocirc; từ bỏ cả cha mẹ nu&ocirc;i để về Ninh gia, cả ng&agrave;y chỉ biết vụng về nịnh nọt c&aacute;i nh&acirc;n vật được gọi l&agrave; nổi tiếng n&agrave;y.<br />\r\n<br />\r\nCuối c&ugrave;ng, chỉ để đổi lại một c&acirc;u: &quot;Anh kh&ocirc;ng thể phụ l&ograve;ng Tuyết Lạc.&quot;<br />\r\n<br />\r\nNinh Tuyết Lạc, kh&ocirc;ng chỉ chiếm lấy danh phận của c&ocirc;, cướp mất cha mẹ đẻ của c&ocirc;, giờ... c&ograve;n cướp mất người trong l&ograve;ng c&ocirc;!<br />\r\n<br />\r\nNinh Tuyết Lạc c&ograve;n nhỏ, l&agrave;m sai c&oacute; thể được tha thứ?</p>', NULL, 1, 1, '2020-09-11 03:28:52', NULL),
(43, 'Còn muốn trốn sao?', 'Chương 1', '<p>Edit: V&acirc;n Anh<br />\r\n<br />\r\nDiệp Oản Oản mở mắt.<br />\r\n<br />\r\nĐối mặt l&agrave; đ&ocirc;i m&ocirc;i khiến cho c&ocirc; sợ h&atilde;i đến ngay cả linh hồn đều run rẩy con ngươi.<br />\r\n<br />\r\n&quot;A--&quot;<br />\r\n<br />\r\nĐ&ocirc;i tay t&aacute;i nhợt của c&ocirc; g&aacute;i đột nhi&ecirc;n cầm c&aacute;i chăn ngăn chặn vật dưới th&acirc;n thể.<br />\r\n<br />\r\nCảm gi&aacute;c t&ecirc; liệt thống khổ, rốt cuộc bản th&acirc;n vẫn sống sờ sờ m&agrave; vẫn phải trải qua một lần nữa.</p>\r\n\r\n<p>Chẳng lẽ nơi n&agrave;y l&agrave; Địa Ngục sao?<br />\r\n<br />\r\nTại sao, r&otilde; r&agrave;ng c&ocirc; đ&atilde; chết, nhưng lại trở về nơi &agrave;y, trở lại b&ecirc;n cạnh t&ecirc;n &aacute;c ma n&agrave;y?<br />\r\n<br />\r\nTrong đầu của c&ocirc; lập tức bị nhiệt độ n&oacute;ng bỏng của người đ&agrave;n &ocirc;ng bốc hơi l&ecirc;n đến hỗn loạn tưng bừng, theo bản năng chống cự, &quot;Đừng đụng v&agrave;o t&ocirc;i!&quot;<br />\r\n<br />\r\nĐộng t&aacute;c của người đ&agrave;n &ocirc;ng ngưng lại một hồi, ngay sau đ&oacute; giống như bị đụng v&agrave;o vảy ngược, khu&ocirc;n mặt giống quỷ lệ kh&aacute;t m&aacute;u nhất thời u &aacute;m, m&ocirc;i mỏng lạnh gi&aacute; t&agrave;n nhẫn cắn x&eacute; đi xuống, giống như muốn nuốt hết m&aacute;u v&agrave; xương của c&ocirc; v&agrave;o bụng.<br />\r\n<br />\r\nDiệp Oản Oản bị đau đến kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o suy nghĩ bất cứ chuyện g&igrave;, chỉ c&oacute; thể v&ocirc; &yacute; thức lầm bầm,&quot; Tại sao... tại sao l&agrave; t&ocirc;i... Tư Dạ H&agrave;n... Tại sao nhất định phải l&agrave; t&ocirc;i...&quot;<br />\r\n<br />\r\n&quot;Bởi v&igrave;, t&ocirc;i chỉ cần em.&quot;<br />\r\n<br />\r\nB&ecirc;n tai truyền tới thanh &acirc;m trầm thấp của người đ&agrave;n &ocirc;ng, giống như g&ocirc;ng xiềng, ngay cả linh hồn của c&ocirc; cũng đồng thời bị giam cầm.<br />\r\n<br />\r\nNghe c&acirc;u trả lời của người đ&agrave;n &ocirc;ng c&ugrave;ng kiếp trước giống nhau như đ&uacute;c, Diệp Oản Oản ho&agrave;n to&agrave;n l&acirc;m v&agrave;o h&ocirc;n m&ecirc;.<br />\r\n<br />\r\n...<br />\r\n<br />\r\nThời điểm mở mắt ra lần nữa, b&ecirc;n ngo&agrave;i cửa sổ đ&atilde; từ đ&ecirc;m tối biến th&agrave;nh ban ng&agrave;y.<br />\r\n<br />\r\nTrong kh&ocirc;ng kh&iacute; tr&agrave;n ngập một cổ m&ugrave;i hoa, &aacute;nh mặt trời ấm &aacute;p xuy&ecirc;n qua chấn song rơi, khiến cho t&acirc;m t&igrave;nh của con người kh&ocirc;ng tự chủ thanh tĩnh lại.<br />\r\n<br />\r\nNhưng m&agrave;, một gi&acirc;y kế tiếp, Diệp Oản Oản đột nhi&ecirc;n thần kinh căng thẳng.<br />\r\n<br />\r\nCảm gi&aacute;c &aacute;p b&aacute;ch mạnh mẽ theo người đ&agrave;n &ocirc;ng tỉnh lại ở to&agrave;n bộ b&ecirc;n trong kh&ocirc;ng gian lan tr&agrave;n.</p>\r\n\r\n<p>B&ecirc;n h&ocirc;ng đột nhi&ecirc;n bị một c&aacute;nh tay nắm chặt, c&ocirc; giống như l&agrave; gối &ocirc;m, bị &ocirc;m v&agrave;o trong ngực của người đ&agrave;n &ocirc;ng.<br />\r\n<br />\r\n&quot;C&ograve;n muốn trốn sao?&quot;<br />\r\n<br />\r\nB&ecirc;n tai truyền tới thanh &acirc;m l&agrave;m cho người ta rợn cả t&oacute;c g&aacute;y.<br />\r\n<br />\r\nTheo bản năng muốn sống, Diệp Oản Oản lập tức lắc đầu một c&aacute;i.<br />\r\n<br />\r\nCũng kh&ocirc;ng biết người đ&agrave;n &ocirc;ng c&oacute; tin c&ocirc; hay kh&ocirc;ng, &aacute;nh mắt ở tr&ecirc;n mặt của c&ocirc; dừng lại một c&aacute;i, chợt k&eacute;o c&ocirc; đến h&ocirc;n m&ocirc;i của c&ocirc;, cằm của c&ocirc;, cổ...<br />\r\n<br />\r\nHơi thở n&oacute;ng bỏng th&ocirc; nặng ch&ocirc;n ở trong cổ của c&ocirc;, t&ugrave;y thời khắc đều c&oacute; thể tản ra t&iacute;n hiệu nguy hiểm.<br />\r\n<br />\r\nDiệp Oản Oản giống như nai con bị cắn, kh&ocirc;ng d&aacute;m l&agrave;m một cử động nhỏ n&agrave;o.<br />\r\n<br />\r\nKh&ocirc;ng biết qua bao l&acirc;u, người đ&agrave;n &ocirc;ng rốt cuộc thả lỏng c&ocirc; ra.<br />\r\n<br />\r\nMột gi&acirc;y kế tiếp, Diệp Oản Oản đ&aacute;y mắt đụng v&agrave;o một h&igrave;nh ảnh v&ocirc; c&ugrave;ng kiều diễm.<br />\r\n<br />\r\nNgười đ&agrave;n &ocirc;ng lấy th&acirc;n thể nửa trần xuống giường, tia phản quang buộc v&ograve;ng th&acirc;n thể thon d&agrave;i c&ugrave;ng h&ocirc;ng gầy của anh.<br />\r\nCảnh sắc như vậy chỉ c&oacute; một c&aacute;i chớp mắt, người đ&agrave;n &ocirc;ng liền nhặt quần &aacute;o ở b&ecirc;n m&eacute;p giường, những ng&oacute;n tay thon d&agrave;i cẩn thận c&agrave;i chiếc n&uacute;t &aacute;o sơ mi được mang tới vi&ecirc;n thứ nhất.<br />\r\n<br />\r\nR&otilde; r&agrave;ng trước đ&acirc;y c&ograve;n t&agrave;n bạo giống như d&atilde; th&uacute;, vậy m&agrave; giờ khu&ocirc;n mặt lại tuấn mĩ như vậy, lạnh như băng kh&ocirc;ng nhiễm một tia kh&oacute;i lửa nh&acirc;n gian.<br />\r\n<br />\r\nCho đến khi thanh &acirc;m đ&oacute;ng cửa vang l&ecirc;n, d&acirc;y thần kinh căng thẳng cuối c&ugrave;ng sắp g&atilde;y mất của Diệp Oản Oản mới bu&ocirc;ng lỏng xuống.<br />\r\n<br />\r\nRốt cuộc c&oacute; thể xử l&yacute; t&igrave;nh cảnh của ch&iacute;nh m&igrave;nh v&agrave;o l&uacute;c n&agrave;y.<br />\r\n<br />\r\nC&ocirc; chậm r&atilde;i đ&aacute;nh gi&aacute; trang tr&iacute; bốn ph&iacute;a, c&ugrave;ng với ch&iacute;nh m&igrave;nh ở trong gương tr&ecirc;n b&agrave;n trang điểm đối diện&mdash;&mdash;<br />\r\n<br />\r\nC&ocirc; g&aacute;i trong gương m&ocirc;i son m&agrave;u đen bị gậm nhắm đến chỉ c&ograve;n s&oacute;t lại một ch&uacute;t &aacute;m sắc, phấn trang điểm tr&ecirc;n mặt đ&atilde; bị nước mắt v&agrave; mồ h&ocirc;i tẩy đi, th&acirc;n thể phủ đầy dấu t&iacute;m bầm c&ugrave;ng vết h&ocirc;n, cuối c&ugrave;ng l&agrave; một mảng h&igrave;nh xăm m&aacute;u tanh kinh khủng.<br />\r\n<br />\r\nBộ d&aacute;ng n&agrave;y, r&otilde; r&agrave;ng l&agrave; c&ocirc; khi hai mươi tuổi!<br />\r\n<br />\r\nKhi đ&oacute;, v&igrave; muốn n&eacute; tr&aacute;nh Tư Dạ H&agrave;n, c&ocirc; đ&atilde; cố &yacute; đem ch&iacute;nh m&igrave;nh biến th&agrave;nh c&aacute;i bộ dạng xấu x&iacute; ch&aacute;n gh&eacute;t n&agrave;y.<br />\r\n<br />\r\nC&ocirc; sống lại... Thật sống lại...<br />\r\n<br />\r\nTrong ph&uacute;t chốc, sự sợ h&atilde;i to lớn v&agrave; tuyệt vọng cơ hồ l&agrave;m c&ocirc; h&iacute;t thở kh&ocirc;ng th&ocirc;ng.<br />\r\n<br />\r\nTại sao...<br />\r\n<br />\r\nTại sao lại muốn cho c&ocirc; trở lại bảy năm trước!<br />\r\n<br />\r\nCho d&ugrave; chết, c&ocirc; cũng kh&ocirc;ng muốn lại trở lại nơi n&agrave;y, trở lại b&ecirc;n t&ecirc;n ma quỷ n&agrave;y b&ecirc;n người.<br />\r\n<br />\r\nC&ocirc; nhớ r&otilde;, đ&acirc;y l&agrave; lần đầu ti&ecirc;n c&ocirc; c&ugrave;ng Tư Dạ H&agrave;n ph&aacute;t sinh quan hệ, m&agrave; sau đ&oacute; v&ocirc; số buổi tối, n&agrave;ng đều bị h&agrave;nh hạ đến sống kh&ocirc;ng bằng chết.<br />\r\n<br />\r\nC&ocirc; bị nhốt ở nơi n&agrave;y, mất đi người y&ecirc;u, th&acirc;n nh&acirc;n, mất đi t&ocirc;n nghi&ecirc;m, mất đi tự do, mất đi hết thảy.<br />\r\n<br />\r\nHết thảy c&aacute;c thứ n&agrave;y, chẳng lẽ c&ocirc; c&ograve;n phải lần nữa trải qua một lần nữa?<br />\r\n<br />\r\nKh&ocirc;ng, nếu &ocirc;ng trời cho c&ocirc; cơ hội l&agrave;m lại một lần nữa, c&ocirc; nhất định phải thay đổi hết thảy mọi thứ!</p>', NULL, 2, 1, '2020-09-11 03:30:04', NULL),
(64, 'Cô dám chơi tôi, tôi liền lên giường với người đàn ông của cô (1)', 'Chương 1', '<p>Khi cảnh s&aacute;t đến trước cửa ph&ograve;ng của kh&aacute;ch sạn, l&uacute;c đấy Yến Thanh Ti cũng vừa mới từ tr&ecirc;n giường của Nhạc Th&iacute;nh Phong bước xuống<br />\r\n<br />\r\nBầu kh&ocirc;ng kh&iacute; &nbsp;trong ph&ograve;ng vẫn c&ograve;n vương đầy m&ugrave;i t&igrave;nh dục, quần &aacute;o vứt lung tung tr&ecirc;n mặt đất, c&oacute; thể thấy &ldquo;trận chiến&rdquo; đ&ecirc;m qua kịch liệt đến thế n&agrave;o.<br />\r\n<br />\r\n&ldquo;Đoang, Đoang&rdquo; h&agrave;ng chục tiếng đập cửa gấp g&aacute;p vang l&ecirc;n m&agrave; Yến Thanh Ti vẫn từ tốn tắm rửa, mặc quần &aacute;o, trang điểm giống như l&agrave; đ&acirc;y l&agrave; ph&ograve;ng của m&igrave;nh vậy.</p>', NULL, 3, 1, '2020-09-11 05:37:29', NULL),
(65, 'Thằng bé ở quán bar', 'Chương 3', '<p>Năm năm sau. Tại h&agrave;nh lang tầng cao nhất qu&aacute;n bar Eton.<br />\r\n<br />\r\nNịch Tịch hầu hạ chủ đầu tư uống rượu cả một đ&ecirc;m, đầu đau muốn nứt ra, vốn định t&igrave;m một chỗ y&ecirc;n tĩnh để tỉnh rượu ch&uacute;t kh&ocirc;ng ngờ lại đụng mặt Thường Lị, v&igrave; vậy chỉ c&oacute; thể xốc lại tinh thần ch&agrave;o hỏi c&ocirc; ta: &quot;Chị Thường c&oacute; việc g&igrave; &agrave;?&quot;<br />\r\n<br />\r\n&quot;Ninh Tịch, chị hỏi, c&oacute; phải l&agrave; em đăng k&iacute; tham gia thử vai nữ ch&iacute;nh của Thi&ecirc;n Hạ đ&uacute;ng kh&ocirc;ng?&quot;<br />\r\n<br />\r\n&quot;Dạ đ&uacute;ng, sao thế?&quot;<br />\r\n<br />\r\n&quot;Ng&agrave;y mai em kh&ocirc;ng được đi!&quot;<br />\r\n<br />\r\nThường Lị cũng coi như người quản l&iacute; của c&ocirc;, thế m&agrave; lại suốt ng&agrave;y ngăn c&ocirc; đi thử vai tại c&aacute;c c&ocirc;ng ty giải tr&iacute; h&agrave;ng đầu. Lần n&agrave;y Ninh Tịch cũng kh&ocirc;ng bất ngờ, chỉ hơi nhướng m&agrave;y hỏi: &quot;L&yacute; do?&quot;</p>\r\n\r\n<p>&quot;Em giấu chị rồi tự chủ trương c&ograve;n d&aacute;m hỏi l&iacute; do? C&ocirc;ng ty đ&atilde; sắp xếp để Tuyết Lạc đi thử vai rồi em c&ograve;n kh&ocirc;ng biết sao?&quot;<br />\r\n<br />\r\n&quot;Chuyện của em với chuyện c&ocirc;ng ty sắp xếp h&igrave;nh như chẳng c&oacute; g&igrave; m&acirc;u thuẫn với nhau.&quot; Ninh Tịch cười m&agrave; như kh&ocirc;ng nh&igrave;n c&ocirc; ta: &quot;Ninh Tuyết Lạc bảo chị đến t&igrave;m t&ocirc;i sao? Chẳng lẽ c&ocirc; ta sợ một diễn vi&ecirc;n hạng b&eacute;t như t&ocirc;i cướp được vai của c&ocirc; ta sao?&quot;<br />\r\n<br />\r\n&quot;C&ocirc; c&oacute; bản lĩnh cướp vai của Tuyết Lạc? N&oacute;i vớ vẩn! T&ocirc;i n&oacute;i cho c&ocirc; biết, đừng ph&iacute; c&ocirc;ng v&ocirc; &iacute;ch, bộ phim lần n&agrave;y Ninh gia đ&atilde; đầu tư ba ngh&igrave;n vạn, ngầm chỉ định cho Tuyết Lạc rồi!&quot;<br />\r\n<br />\r\n&quot;Nếu thế chị c&ograve;n lo lắng c&aacute;i g&igrave;?&quot;<br />\r\n<br />\r\n&quot;C&ocirc; l&agrave; l&agrave; nghệ sĩ dưới trướng của t&ocirc;i, phải nghe t&ocirc;i sắp xếp!&quot; Thường Lị ra vẻ đương nhi&ecirc;n n&oacute;i.<br />\r\n<br />\r\n&quot;&Agrave;, h&oacute;a ra chị Thường cũng biết t&ocirc;i l&agrave; nghệ sĩ dưới trướng của chị cơ đấy.&quot;<br />\r\n<br />\r\n&quot;Ninh Tịch, t&ocirc;i kh&ocirc;ng rảnh để m&agrave; c&atilde;i nhau với c&ocirc;, nếu c&ocirc; rượu mời kh&ocirc;ng uống, th&iacute;ch uống rượu phạt th&igrave; cũng đừng c&oacute; tr&aacute;ch t&ocirc;i!&quot;<br />\r\n<br />\r\nVừa dứt lời, Ninh Tịch đ&atilde; cảm thấy một lực mạnh mẽ x&ocirc;ng tới, bất ngờ kh&ocirc;ng kịp đề ph&ograve;ng khiến c&ocirc; bị đẩy tới kho h&agrave;ng tr&ecirc;n tầng cao nhất của qu&aacute;n bar, đồng thời di động cũng bị cướp đi.<br />\r\n<br />\r\n&quot;Ầm&quot; một tiếng, cửa bị đ&oacute;ng lại.<br />\r\n<br />\r\n...<br />\r\n<br />\r\nTiếng bước ch&acirc;n ngo&agrave;i cửa c&agrave;ng l&uacute;c c&agrave;ng xa.</p>', NULL, 1, 1, '2020-09-11 05:38:37', NULL),
(66, 'Thiên kim hào môn (1)', 'Chương 1', '<p><strong>Chương 1&nbsp;Thi&ecirc;n kim h&agrave;o m&ocirc;n (1)</strong><br />\r\n<br />\r\nThời S&ecirc;nh l&agrave; một t&aacute;c giả chuy&ecirc;n đ&agrave;o hố kh&ocirc;ng lấp, &ldquo;th&aacute;i gi&aacute;m&rdquo;* v&ocirc; số, l&agrave; loại t&aacute;c giả mẹ kế chuy&ecirc;n bức hại nam nữ ch&iacute;nh điển h&igrave;nh, trong một ng&agrave;y nắng ấm, mặt trời l&ecirc;n cao rực rỡ, cuối c&ugrave;ng đ&atilde; hiểu ra một đạo l&yacute;&hellip;<br />\r\n<br />\r\nĐ&agrave;o hố kh&ocirc;ng lấp, sẽ gặp b&aacute;o ứng.<br />\r\n<br />\r\n<em>*Th&aacute;i gi&aacute;m: chơi chữ. Xuất ph&aacute;t từ t&iacute;ch cũ về Kỉ Hiểu Lam.</em><br />\r\n<br />\r\n<em>Một h&ocirc;m, một đ&aacute;m th&aacute;i gi&aacute;m quấn qu&iacute;t lấy Kỉ Hiểu Lam, bảo &ocirc;ng kể chuyện xưa. Kỉ Hiểu Lam kh&ocirc;ng muốn n&oacute;i nhưng đ&aacute;m th&aacute;i gi&aacute;m n&agrave;y cứ l&agrave;m phiền kh&ocirc;ng bu&ocirc;ng, kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o kh&aacute;c, &ocirc;ng n&oacute;i: &quot;Được rồi, để t&ocirc;i kể một c&acirc;u chuyện vậy... Ng&agrave;y xưa, c&oacute; một th&aacute;i gi&aacute;m&hellip;&quot; Sau đ&oacute; &ocirc;ng khoanh tay m&agrave; đứng, bọn th&aacute;i gi&aacute;m đợi hơn nửa ng&agrave;y cũng kh&ocirc;ng thấy &ocirc;ng kể tiếp kh&uacute;c sau, v&igrave; thế liền hỏi: &quot;Phần dưới đ&acirc;u?&quot; Kỉ Hiểu Lam nh&uacute;n vai, n&oacute;i: &quot;Phần dưới? Phần dưới đ&atilde; kh&ocirc;ng c&ograve;n nữa!&quot; Sau đ&oacute; cười to m&agrave; đi&hellip;</em><br />\r\n<br />\r\n<em>Trong văn chương nếu viết tiểu thuyết m&agrave; bỏ dở kh&ocirc;ng ho&agrave;n th&agrave;nh th&igrave; kh&ocirc;ng kh&aacute;c g&igrave; th&aacute;i gi&aacute;m &ldquo;tr&ecirc;n c&oacute;, dưới kh&ocirc;ng c&oacute;&rdquo;, v&igrave; thế n&oacute; hay được d&ugrave;ng để chơi chữ với c&aacute;c t&aacute;c giả chuy&ecirc;n đ&agrave;o hố kh&ocirc;ng lấp.</em><br />\r\n<br />\r\nHiện tại c&ocirc; đang ngồi trước một quyển s&aacute;ch cao tầm nửa người, trong suốt l&oacute;ng l&aacute;nh như thủy tinh, t&acirc;m t&igrave;nh nhức nhối.<br />\r\n<br />\r\nR&otilde; r&agrave;ng một gi&acirc;y trước đ&acirc;y c&ocirc; c&ograve;n đang viết tới một t&igrave;nh tiết cực kỳ cẩu huyết l&agrave; nh&acirc;n vật nam ch&iacute;nh bị nh&acirc;n vật nam phụ bẻ cong, nữ chủ chuẩn bị đi m&agrave;i thẳng lại nam phụ, để nam ch&iacute;nh trở về th&acirc;n FA, sao chớp mắt một c&aacute;i lại ở đ&acirc;y rồi?<br />\r\n<br />\r\nB&agrave; đ&acirc;y phải về nh&agrave;!<br />\r\n<br />\r\n[Điều kiện trở về t&ocirc;i đ&atilde; đề cập qua, c&ocirc; c&oacute; 5 gi&acirc;y để chuẩn bị, vượt qu&aacute; thời hạn coi như bỏ qua.] Thanh &acirc;m cứng nhắc, lạnh l&ugrave;ng như băng, kh&ocirc;ng chứa ch&uacute;t sức sống n&agrave;o vang l&ecirc;n.</p>', NULL, 13, 1, '2020-09-11 05:39:47', NULL),
(67, 'Thiên kim hào môn (2)', 'Chương 2', '<p><strong>Chương 2&nbsp;Thi&ecirc;n kim h&agrave;o m&ocirc;n (2)</strong><br />\r\n<br />\r\nTrong đại sảnh trang ho&agrave;ng lộng lẫy, &acirc;m nhạc du dương, tuấn nam mỹ nữ đứng th&agrave;nh từng tốp nhẹ nh&agrave;ng n&oacute;i chuyện với nhau.<br />\r\n<br />\r\nThời S&ecirc;nh một trong g&oacute;c, biểu t&igrave;nh tự nhi&ecirc;n, b&igrave;nh tĩnh, kh&ocirc;ng c&oacute; ch&uacute;t kh&ocirc;ng khỏe n&agrave;o.<br />\r\n<br />\r\nYến hội h&ocirc;m nay được tổ chức để mừng sinh nhật Nam Cung phu nh&acirc;n, cũng đ&uacute;ng l&agrave; ng&agrave;y m&agrave; nguy&ecirc;n chủ k&eacute;o T&ocirc; Y Y tới trước mặt cha mẹ Nam Cung Cảnh.<br />\r\n<br />\r\nDi nguyện của nguy&ecirc;n chủ l&agrave; rời xa g&atilde; đ&agrave;n &ocirc;ng cặn b&atilde; kia, bảo vệ tốt Hứa gia, hiếu k&iacute;nh với cha mẹ, khiến cho bọn họ c&oacute; thể an hưởng tuổi gi&agrave;.<br />\r\n<br />\r\n&ldquo;Thừa Nguyệt, sao c&ocirc; lại ngồi đ&acirc;y? Sao lại kh&ocirc;ng ở b&ecirc;n Cảnh thiếu vậy?&rdquo; Một b&oacute;ng người xinh đẹp ngồi xuống cạnh Thời S&ecirc;nh, người n&agrave;y t&oacute;c uống quăn, gương mặt dễ thương, đ&aacute;ng y&ecirc;u.<br />\r\n<br />\r\nThời S&ecirc;nh lục t&igrave;m trong đầu m&igrave;nh tư liệu về người n&agrave;y.<br />\r\n<br />\r\nLam Tuyết, c&oacute; quan hệ bạn b&egrave; tốt với nguy&ecirc;n chủ, đương nhi&ecirc;n, c&aacute;i n&agrave;y cũng chỉ l&agrave; do nguy&ecirc;n chủ nghĩ vậy.<br />\r\n<br />\r\nL&uacute;c trước khi Hứa gia ph&aacute; sản, Hứa Thừa Nguyệt đi nhờ Lam Tuyết hỗ trợ, Lam Tuyết chẳng những kh&ocirc;ng gi&uacute;p m&agrave; c&ograve;n ch&acirc;m chọc, mỉa mai Hứa Thừa Nguyệt một trận.</p>\r\n\r\n<p>Nguy&ecirc;n chủ biết chuyện Nam Cung Cảnh v&agrave; T&ocirc; Y Y c&oacute; quan hệ với nhau cũng l&agrave; từ miệng người n&agrave;y n&oacute;i ra.<br />\r\n<br />\r\n&ldquo;Kh&ocirc;ng biết.&rdquo; Thời S&ecirc;nh vẻ mặt kh&ocirc;ng cảm x&uacute;c trả lời.<br />\r\n<br />\r\nC&aacute;ch xa bọn kỹ nữ t&acirc;m cơ!<br />\r\n<br />\r\nC&aacute;ch xa bọn bạch li&ecirc;n hoa!<br />\r\n<br />\r\nLam Tuyết hồ nghi nhưng cũng kh&ocirc;ng suy nghĩ nhiều, chỉ nghĩ l&agrave; t&acirc;m t&igrave;nh c&ocirc; kh&ocirc;ng tốt m&agrave; th&ocirc;i. Rốt cuộc l&agrave;m g&igrave; c&oacute; c&ocirc; g&aacute;i n&agrave;o c&oacute; thể trơ mắt nh&igrave;n vị h&ocirc;n phu của m&igrave;nh đi &ocirc;m &ocirc;m ấp ấp nữ nh&acirc;n kh&aacute;c chứ?<br />\r\n<br />\r\n&ldquo;Thừa Nguyệt, vừa rồi t&ocirc;i c&oacute; gặp T&ocirc; Y Y.&rdquo; Lam Tuyết n&oacute;i với giọng tr&agrave;n đầy lo lắng, nhưng biểu hiện tr&ecirc;n mặt lại chẳng hề lo lắng ch&uacute;t n&agrave;o. &ldquo;Cảnh thiếu l&agrave; vị h&ocirc;n phu của c&ocirc;, c&ocirc; kh&ocirc;ng thể để con khốn kia chiếm hết nổi bật được.&rdquo;<br />\r\n<br />\r\nBởi v&igrave; cha của Nam Cung Cảnh vẫn c&ograve;n sống n&ecirc;n mọi người vẫn thường vọi Nam Cung Cảnh l&agrave; Cảnh thiếu.<br />\r\n<br />\r\nThời S&ecirc;nh liếc nh&igrave;n c&ocirc; ta một c&aacute;i, Lam Tuyết lu&ocirc;n chưa từng che giấu sự giả dối, nguy&ecirc;n chủ n&agrave;y rốt cuộc ng&acirc;y thơ tới mức n&agrave;o m&agrave; lại kh&ocirc;ng nh&igrave;n ra vẻ giả tạo của c&ocirc; ta nhỉ?<br />\r\n<br />\r\nTrong tiểu thuyết, chỉ số th&ocirc;ng minh của nữ phụ lu&ocirc;n rất thấp, c&acirc;u n&agrave;y n&oacute;i quả nhi&ecirc;n kh&ocirc;ng sai ch&uacute;t n&agrave;o.<br />\r\n<br />\r\n&ldquo;Sao c&ocirc; ta v&agrave;o được?&rdquo; Thời S&ecirc;nh cũng kh&ocirc;ng muốn lập tức x&eacute; r&aacute;ch da mặt với Lam Tuyết n&ecirc;n thuận tiện hỏi một c&acirc;u.<br />\r\n<br />\r\nC&ocirc; đương nhi&ecirc;n đ&atilde; biết nữ ch&iacute;nh v&agrave;o đ&acirc;y bằng c&aacute;ch n&agrave;o, trong mỗi cuốn tiểu thuyết, ngoại trừ nam ch&iacute;nh c&ograve;n c&oacute; rất nhiều nam phụ đối với nữ ch&iacute;nh lu&ocirc;n một bộ dạng t&igrave;nh t&acirc;m nghĩa trọng..<br />\r\n<br />\r\nT&ocirc; Y Y l&agrave; đi theo nam phụ số một - Lăng Hạo - v&agrave;o đ&acirc;y.<br />\r\n<br />\r\n&ldquo;Tới c&ugrave;ng với tiểu thiếu gia của Lăng gia. Con khốn đ&oacute; quyến rũ Cảnh thiếu m&agrave; c&ograve;n chạy đi quyến rũ cả Lăng thiếu, cũng kh&ocirc;ng biết học được c&aacute;i thủ đoạn n&agrave;y ở đ&acirc;u, thật kh&ocirc;ng biết xấu hổ l&agrave; g&igrave;.&rdquo; Lam Tuyết tỏ vẻ giận dữ.<br />\r\n<br />\r\nThời S&ecirc;nh đang định mở miệng n&oacute;i chuyện th&igrave; b&ecirc;n tai lại vang l&ecirc;n một giọng n&oacute;i đ&agrave;n &ocirc;ng, cẩn thận nghe th&igrave; c&oacute; thể thấy ngữ điệu của anh ta rất kh&ocirc;ng ki&ecirc;n nhẫn.<br />\r\n<br />\r\n&ldquo;C&ocirc; ở đ&acirc;y l&agrave;m g&igrave;?&rdquo;<br />\r\n<br />\r\n&ldquo;Cảnh thiếu.&rdquo; Lam Tuyết lập tức đứng l&ecirc;n, trong l&ograve;ng thấp thỏm, kh&ocirc;ng biết những lời c&ocirc; ta vừa n&oacute;i c&oacute; bị Cảnh thiếu nghe thấy kh&ocirc;ng nữa?<br />\r\n<br />\r\nNam Cung Cảnh l&atilde;nh đạm gật đầu với Lam Tuyết, Lam Tuyết l&agrave;m mặt quỷ với Thời S&ecirc;nh rồi xoay người rời đi, để kh&ocirc;ng gian lại cho hai người.</p>', NULL, 13, 1, '2020-09-11 05:49:06', NULL),
(68, 'Thiên kim hào môn (3)', 'Chương 3', '<p><strong>Chương 3&nbsp;Thi&ecirc;n kim h&agrave;o m&ocirc;n (3)</strong><br />\r\n<br />\r\nThời S&ecirc;nh v&agrave; Nam Cung phu nh&acirc;n đợi đến khi bữa tiệc bắt đầu mới c&ugrave;ng nhau đi xuống, Nam Cung Cảnh đ&atilde; kh&ocirc;ng thấy đ&acirc;u, c&oacute; lẽ anh ta kh&ocirc;ng y&ecirc;n t&acirc;m về T&ocirc; Y Y.<br />\r\n<br />\r\nĐối với loại sự t&igrave;nh x&atilde; giao n&agrave;y, Thời S&ecirc;nh rất kh&ocirc;ng th&iacute;ch, nhưng trong giới thượng lưu n&agrave;y, x&atilde; giao lại v&ocirc; c&ugrave;ng cần thiết.<br />\r\n<br />\r\n&ldquo;Hứa tiểu thư c&agrave;ng lớn c&agrave;ng xinh đẹp, Cảnh thiếu đ&uacute;ng l&agrave; c&oacute; ph&uacute;c lớn, kh&ocirc;ng giống tiểu tử nh&agrave; ch&uacute;ng t&ocirc;i, giờ vẫn chẳng thấy g&igrave;.&rdquo; Một b&agrave; phu nh&acirc;n h&acirc;m mộ n&oacute;i với Nam Cung phu nh&acirc;n, mấy vị b&ecirc;n cạnh cũng li&ecirc;n tục gật đầu đồng t&igrave;nh.<br />\r\n<br />\r\n&ldquo;Cho n&ecirc;n con d&acirc;u tốt l&agrave; phải sớm t&igrave;m.&rdquo;<br />\r\n<br />\r\n&ldquo;Aizz, c&aacute;i đ&oacute; cũng phải xem c&oacute; ph&uacute;c kh&iacute; kh&ocirc;ng mới được. Mọi người nh&igrave;n L&acirc;m gia xem, cũng l&agrave; con d&acirc;u t&igrave;m từ b&eacute; đấy, kết quả thế n&agrave;o? L&agrave;m cả một nh&agrave; n&aacute;o loạn kh&ocirc;ng được y&ecirc;n b&igrave;nh. Aizz, t&ocirc;i kh&ocirc;ng n&oacute;i Hứa tiểu thư đ&acirc;u, Hứa tiểu thư đừng suy nghĩ nhiều.&rdquo;<br />\r\n<br />\r\nC&oacute; v&agrave;i người cứ th&iacute;ch n&oacute;i l&ograve;ng v&ograve;ng quanh co, tưởng người ta nghe kh&ocirc;ng hiểu sao?<br />\r\n<br />\r\nThời S&ecirc;nh cố gắng nhếch miệng cười, &aacute;nh mắt đầy vẻ kh&ocirc;ng ki&ecirc;n nhẫn, nếu kh&ocirc;ng phải khi tỉnh lại đ&atilde; ở tr&ecirc;n xe th&igrave; c&ocirc; tuyệt đối sẽ kh&ocirc;ng tới đ&acirc;y.<br />\r\n<br />\r\nBa người đ&agrave;n b&agrave; l&agrave; th&agrave;nh một s&acirc;n khấu, từng n&agrave;y người đ&agrave;n b&agrave; th&igrave; kh&ocirc;ng biết sẽ diễn được bao nhi&ecirc;u vở nữa?<br />\r\n<br />\r\n&ldquo;B&aacute;c g&aacute;i, ba con tới rồi, con đi qua đ&oacute; một ch&uacute;t.&rdquo; Thời S&ecirc;nh nh&igrave;n thấy &ocirc;ng Hứa liền lập tức lấy cớ trốn đi.</p>\r\n\r\n<p>Những người n&agrave;y c&oacute; tiền c&oacute; thế, c&aacute;i g&igrave; cũng c&oacute;, ngo&agrave;i th&iacute;ch bu&ocirc;n chuyện ra cũng chỉ th&iacute;ch so đo với nhau.<br />\r\n<br />\r\nSo cha, so chồng, so con, cứ như thể người n&agrave;o hơn th&igrave; người ấy đẳng cấp hơn vậy.<br />\r\n<br />\r\n&ldquo;Ba.&rdquo; Thời S&ecirc;nh đi tới trước mặt &ocirc;ng Hứa, &ocirc;n h&ograve;a gọi một tiếng.<br />\r\n<br />\r\n&ldquo;Sao con b&eacute; n&agrave;y lại tới sớm như thế, sao kh&ocirc;ng ở chung một chỗ với A Cảnh?&rdquo; &Ocirc;ng Hứa nh&igrave;n một v&ograve;ng kh&ocirc;ng thấy Nam Cung Cảnh đ&acirc;u th&igrave; hơi nghi hoặc.<br />\r\n<br />\r\n&ldquo;A, anh ấy bận rồi.&rdquo; Nụ cười tr&ecirc;n mặt Thời S&ecirc;nh rất ch&acirc;n th&agrave;nh. &ldquo;Sao mẹ kh&ocirc;ng tới đ&acirc;y?&rdquo;<br />\r\n<br />\r\n&ldquo;C&ocirc;ng ty chi nh&aacute;nh c&oacute; việc n&ecirc;n mẹ qua đ&oacute; rồi, sao h&ocirc;m nay nha đầu con lại quan t&acirc;m tới &ocirc;ng b&agrave; gi&agrave; ch&uacute;ng ta thế? Ba c&ograve;n tưởng trong mắt con chỉ c&oacute; t&igrave;nh lang chứ?&rdquo; &Ocirc;ng Hứa tr&ecirc;u ghẹo, vẻ mặt vui mừng, c&oacute; thể thấy &ocirc;ng y&ecirc;u nguy&ecirc;n chủ đến chừng n&agrave;o.<br />\r\n<br />\r\n&ldquo;Con lớn rồi m&agrave;.&rdquo; Thời S&ecirc;nh chớp chớp mắt.<br />\r\n<br />\r\n&ldquo;Được được được, nha đầu nh&agrave; ch&uacute;ng ta lớn rồi.&rdquo;<br />\r\n<br />\r\nTr&aacute;i tim cũng thay đổi rồi, kh&ocirc;ng c&ograve;n như xưa nữa.<br />\r\n<br />\r\nDi nguyện lớn nhất của nguy&ecirc;n chủ ch&iacute;nh l&agrave; hiếu k&iacute;nh thật tốt với cha mẹ.<br />\r\n<br />\r\n&Ocirc;ng Hứa mang theo Thời S&ecirc;nh tới ch&uacute;c mừng sinh nhật Nam Cung phu nh&acirc;n, sau đ&oacute; lại đem c&ocirc; đi dạo một v&ograve;ng ch&uacute;c rượu bạn l&agrave;m ăn, ai cần gặp đều gặp cả.<br />\r\n<br />\r\n&Ocirc;ng Hứa rất vui mừng khi thấy con g&aacute;i h&ocirc;m nay ngoan ngo&atilde;n như thế, quả nhi&ecirc;n đ&atilde; lớn rồi.<br />\r\n<br />\r\nThời S&ecirc;nh uống một ch&uacute;t rượu n&ecirc;n đầu hơn chếnh cho&aacute;ng, c&ocirc; n&oacute;i với &ocirc;ng Hứa một c&acirc;u rồi đi ra ngo&agrave;i vườn hoa cho tỉnh t&aacute;o.<br />\r\n<br />\r\nVườn hoa của nh&agrave; Nam Cung rất lớn, Thời S&ecirc;nh t&ugrave;y tiện t&igrave;m một chỗ ngồi, ban đ&ecirc;m gi&oacute; lạnh l&agrave;m cho đầu &oacute;c cũng tỉnh t&aacute;o hơn nhiều.<br />\r\n<br />\r\n&ldquo;Vừa rồi em v&agrave; hắn đang l&agrave;m g&igrave;?&rdquo; Thanh &acirc;m c&oacute; ch&uacute;t tức giận vang l&ecirc;n l&agrave;m cho Thời S&ecirc;nh đang hơi buồn ngủ phải bừng tỉnh.<br />\r\n<br />\r\nThời S&ecirc;nh xoa xoa mặt, quay lưng nh&igrave;n lại ph&iacute;a sau, l&agrave; một h&agrave;ng c&acirc;y, c&oacute; hai th&acirc;n ảnh đang đứng ph&iacute;a sau h&agrave;ng c&acirc;y đ&oacute;, b&oacute;ng d&aacute;ng mơ hồ.<br />\r\n<br />\r\nGiọng n&oacute;i kia h&igrave;nh như l&agrave; Nam Cung Cảnh?<br />\r\n<br />\r\n&ldquo;Em&hellip; Em kh&ocirc;ng c&oacute;&hellip;&rdquo;<br />\r\n<br />\r\nThanh &acirc;m đ&aacute;nh chết c&ocirc; cũng kh&ocirc;ng nhận nhầm n&agrave;y l&agrave; của T&ocirc; Y Y.<br />\r\n<br />\r\nHai người n&agrave;y đang l&agrave;m g&igrave; vậy?</p>', NULL, 13, 1, '2020-09-11 05:49:49', NULL);
INSERT INTO `chapters` (`id`, `name`, `subname`, `content`, `view`, `story_id`, `user_id`, `created_at`, `updated_at`) VALUES
(69, 'Thanh ngưu trấn', 'Chương 2', '<p>Đ&acirc;y l&agrave; một thị th&agrave;nh nhỏ, tuy n&oacute;i l&agrave; một t&ograve;a tiểu th&agrave;nh, nhưng thực ra n&oacute; chỉ to hơn thị trấn b&igrave;nh thường một &iacute;t th&ocirc;i, m&agrave; t&ecirc;n của n&oacute; cũng được gọi l&agrave; Thanh Ngưu trấn, chỉ những kẻ sơn d&atilde; phụ cận &iacute;t kiến thức mới gọi Thanh Ngưu trấn th&agrave;nh Thanh Ngưu thanh m&agrave; th&ocirc;i. C&aacute;ch gọi n&agrave;y cũng đ&atilde; th&agrave;nh th&oacute;i quen mấy chục năm ở đ&acirc;y mất rồi.<br />\r\n<br />\r\nThanh ngưu trấn đ&iacute;ch x&aacute;c kh&ocirc;ng lớn lắm, con đường chủ đạo Thanh ngưu chạy dọc trấn theo hướng Đ&ocirc;ng-T&acirc;y. M&agrave; kh&aacute;ch sạn ở đ&acirc;y th&igrave; cũng chỉ c&oacute; đ&uacute;ng một c&aacute;i Thanh ngưu kh&aacute;ch sạn. Kh&aacute;ch sạn nằm t&iacute;t cuối trấn về ph&iacute;a t&acirc;y. Kh&aacute;ch v&atilde;ng lai qua đ&acirc;y, nếu kh&ocirc;ng muốn phải nằm ngo&agrave;i đường th&igrave; kh&ocirc;ng c&ograve;n c&aacute;ch n&agrave;o kh&aacute;c l&agrave; phải l&agrave;m kh&aacute;ch tại kh&aacute;ch sạn n&agrave;y.<br />\r\n<br />\r\nV&agrave;o l&uacute;c n&agrave;y, nếu ch&uacute; &yacute; ra ngo&agrave;i, từ ph&iacute;a T&acirc;y thị trấn c&oacute; một đo&agrave;n xe ngựa đang tiến v&agrave;o Thanh ngưu trấn. Rất nhanh đo&agrave;n xe đi tới trước cửa Thanh ngưu kh&aacute;ch sạn, đo&agrave;n xe cũng kh&ocirc;ng dừng lại m&agrave; tiếp tục đi sau v&agrave;o trong trấn, đến khi đi tới trước cửa Xu&acirc;n Hương tửu l&acirc;u th&igrave; mới dừng lại.<br />\r\n<br />\r\nXu&acirc;n Hương tửu l&acirc;u cũng kh&ocirc;ng t&iacute;nh l&agrave; lớn, thậm ch&iacute; n&oacute; c&ograve;n c&oacute; d&aacute;ng vẻ hơi cũ cũ, tuy vậy người ta vẫn cảm nhận được từ n&oacute; n&eacute;t g&igrave; đ&oacute; cổ k&iacute;nh. Cũng bởi v&igrave; b&acirc;y giờ đang l&agrave; ch&iacute;nh ngọ, kh&aacute;ch nh&acirc;n d&ugrave;ng bữa tại tửu lầu vẫn c&ograve;n kh&aacute; đ&ocirc;ng đ&uacute;c, cho n&ecirc;n b&ecirc;n trong tửu l&acirc;u hầu như kh&ocirc;ng c&ograve;n chỗ trống nữa.<br />\r\n<br />\r\nTừ tr&ecirc;n xe xuống l&agrave; một l&atilde;o b&eacute;o mặt tr&ograve;n c&oacute; bộ r&acirc;u quai n&oacute;n dắt theo một đứa trẻ đen đ&uacute;a tầm mười tuổi. Người đ&agrave;n &ocirc;ng n&agrave;y khệnh khạng dẫn tiểu h&agrave;i tử trực tiếp bước v&agrave;o tửu l&acirc;u. Kh&aacute;ch h&agrave;ng trong tửu l&acirc;u cũng c&oacute; người nhận ra l&atilde;o b&eacute;o, biết rằng l&atilde;o l&agrave; chưởng quĩ&nbsp;<em>&quot;H&agrave;n b&eacute;o mập&quot;</em>&nbsp;của tửu l&acirc;u, c&ograve;n tiểu h&agrave;i tử kia th&igrave; kh&ocirc;ng một ai nhận biết ra.<br />\r\n&nbsp;</p>', NULL, 12, 1, '2020-09-11 05:52:02', NULL),
(70, 'Thất huyền môn', 'Chương 3', '<p>Kh&ocirc;ng kh&iacute; trong xe kh&ocirc;ng được trong l&agrave;nh cho lắm, điều n&agrave;y cũng phải th&ocirc;i, hơn ba chục tiểu h&agrave;i tử chen ch&uacute;c trong một chiếc xe. Tuy n&oacute;i l&agrave; tiểu h&agrave;i tử nhỏ hơn nhiều so với người trưởng th&agrave;nh, nhưng với số lương thế n&agrave;y, cũng đủ để l&agrave;m bầu kh&ocirc;ng kh&iacute; trong xe trở n&ecirc;n kh&oacute; thở.<br />\r\n<br />\r\nH&agrave;n Lập tinh ranh đ&atilde; chui ngay v&agrave;o một g&oacute;c nằm s&acirc;u trong xe, len l&eacute;n đ&aacute;nh gi&aacute; mấy tiểu h&agrave;i đồng c&ograve;n lại trong xe.<br />\r\n<br />\r\nTới tham gia kỳ khảo th&iacute; nhập m&ocirc;n lần n&agrave;y, từ c&aacute;ch ăn mặc, ăn n&oacute;i dễ d&agrave;ng nhận thấy c&oacute; ba dạng người.<br />\r\n<br />\r\nLoại người thứ nhất ngồi ở giữa xe, đang bị đ&aacute;m trẻ con v&acirc;y quanh ủng hộ. Thiếu ni&ecirc;n mặc cẩm y ch&iacute;nh l&agrave; thuộc dạng người thứ nhất n&agrave;y.</p>\r\n\r\n<p>Thiếu ni&ecirc;n n&agrave;y t&ecirc;n gọi l&agrave; Vũ Nham, năm nay mới mười ba tuổi, l&agrave; đứa trẻ lớn tuổi nhất ở trong xe l&uacute;c n&agrave;y. Vốn tuổi của hắn đ&atilde; vượt qu&aacute; qui định, nhưng hắn c&oacute; một vị biểu tỷ được gả cho một nh&acirc;n vật c&oacute; đại quyền trong Thất huyền m&ocirc;n, n&ecirc;n cho d&ugrave; vượt qu&aacute; tuổi qui định th&igrave; cũng kh&ocirc;ng phải l&agrave; vấn đề g&igrave; to t&aacute;t. Gia đ&igrave;nh Vũ Nham mở một v&otilde; qu&aacute;n, gia trung c&oacute; ch&uacute;t gi&agrave;u c&oacute;, tuổi tuy c&ograve;n nhỏ nhưng cũng đ&atilde; c&oacute; tập v&otilde; qua. C&ocirc;ng phu tuy kh&ocirc;ng lấy l&agrave;m cao minh cho lắm, nhưng để đối ph&oacute; lại với những đứa b&eacute; một điểm kh&iacute; lực cũng kh&ocirc;ng c&oacute; như H&agrave;n Lập th&igrave; c&oacute; lẽ l&agrave; dư sức.<br />\r\n<br />\r\nRất hiển nhi&ecirc;n, loại người như Vũ Nham c&oacute; tiền, c&oacute; thế, tự nhi&ecirc;n sẽ trở th&agrave;nh&nbsp;<em>&quot;đại ca&quot;</em>&nbsp;của đại bộ phận đ&aacute;m tiểu h&agrave;i tử trong xe.<br />\r\n<br />\r\nMột loại người kh&aacute;c tựu l&agrave; những h&agrave;i đồng ủng hộ Vũ Nham. Những h&agrave;i đồng n&agrave;y xuất th&acirc;n từ đủ loại gia cảnh như c&oacute; người trong nh&agrave; c&oacute; cửa h&agrave;ng, c&oacute; người th&igrave; phải đi l&agrave;m c&ocirc;ng&hellip; n&oacute;i chung những h&agrave;i tử n&agrave;y đều c&oacute; điểm chung: đều lớn l&ecirc;n trong th&agrave;nh trấn, c&oacute; cơ hội &iacute;t nhiều quan s&aacute;t c&aacute;ch xử sự những người c&oacute; học vấn, trục lợi nhi h&agrave;nh (c&oacute; lợi th&igrave; l&agrave;m) đ&atilde; th&agrave;nh bản sự rồi. Do đ&oacute; m&agrave; những đứa trẻ n&agrave;y dốc sức ủng hộ Vũ Nham, cứ một điều&nbsp;<em><em>&quot;Vũ thiếu gia&quot;</em></em>&nbsp;hai điều&nbsp;<em><em>&quot;Vũ thiếu gia&quot;</em></em>&nbsp;m&agrave; gọi. Vũ Nham trước sự t&ocirc;n vinh của bọn trẻ như vậy cũng coi như b&igrave;nh thường, hắn c&ograve;n c&oacute; cảm gi&aacute;c hưởng thụ sự t&ocirc;n vinh đ&oacute;.<br />\r\n<br />\r\nC&ograve;n loại người cuối c&ugrave;ng, tất nhi&ecirc;n l&agrave; dạng người giống như H&agrave;n Lập. Loại người n&agrave;y đến từ v&ugrave;ng đất hoang vu hẻo l&aacute;nh, gia cảnh hầu như đều bần h&agrave;n, c&oacute; g&igrave; ăn đ&oacute;. Loại người như vậy trong xe rất l&agrave; &iacute;t, chỉ c&oacute; tầm năm s&aacute;u đứa trẻ m&agrave; th&ocirc;i. Thần th&aacute;i của bọn ch&uacute;ng đa phần l&agrave; e d&egrave;, kh&ocirc;ng d&aacute;m n&oacute;i năng với ai c&aacute;i g&igrave;, chỉ ngồi m&agrave; nh&igrave;n người kh&aacute;c n&oacute;i cười m&agrave; th&ocirc;i. Những tiểu h&agrave;i tử n&agrave;y c&ugrave;ng với những đứa trẻ đang huy&ecirc;n n&aacute;o c&ograve;n lại tạo th&agrave;nh một khung cảnh đối lập.<br />\r\n<br />\r\nXe ngựa xuất ph&aacute;t từ Thanh ngưu trấn theo hướng T&acirc;y m&agrave; thẳng tiến, tr&ecirc;n lộ tr&igrave;nh c&ograve;n đi qua th&ecirc;m v&agrave;i địa phương nữa, cũng tiếp th&ecirc;m v&agrave;i tiểu h&agrave;i đồng nữa. Cuối c&ugrave;ng đến chiều ng&agrave;y thứ năm th&igrave; cũng đi tới được Th&aacute;i H&agrave; sơn &ndash; tổng m&ocirc;n của Thất huyền m&ocirc;n.<br />\r\n<br />\r\nHầu hết tiểu h&agrave;i tử khi xuống xe đều bị cảnh quan tươi đẹp của Th&aacute;i H&agrave; sơn l&agrave;m cho m&ecirc; mẩn. Chỉ đến khi Vương hộ ph&aacute;p l&ecirc;n tiếng th&uacute;c giục, mọi người mới thanh tỉnh trở lại rồi tiếp tục đi tới.</p>', NULL, 12, 1, '2020-09-11 06:03:57', NULL),
(71, 'Luyện cốt nhai', 'Chương 4', '<p>Nhạc đường chủ đứng trước mọi người lớn tiếng n&oacute;i:&nbsp;<em>&quot;Mọi người nghe cho kỹ, đi theo con đường nhỏ trong rừng tr&uacute;c ph&iacute;a trước l&agrave; c&oacute; thể đi tới luyện cốt nhai của Thất huyền m&ocirc;n. Đoạn đường đầu ti&ecirc;n l&agrave; tr&uacute;c l&acirc;m đoạn lộ, đi qua đ&oacute; sẽ l&agrave; giải đất b&iacute;ch nhai, v&agrave; cuối c&ugrave;ng sẽ đến một c&aacute;i sơn nhai. Ai c&oacute; thể l&ecirc;n được đến đỉnh sơn nhai th&igrave; c&oacute; thể tiến v&agrave;o được Thất huyền m&ocirc;n. Trước ch&iacute;nh ngọ kh&ocirc;ng thể tới đ&iacute;ch, tuy kh&ocirc;ng thể ch&iacute;nh thức trở th&agrave;nh đệ tử nhưng nếu biểu hiện kh&aacute; th&igrave; cũng c&oacute; thể trở th&agrave;nh k&yacute; danh đệ tử.<br />\r\n<br />\r\nH&agrave;n Lập tự nhi&ecirc;n kh&ocirc;ng r&otilde; h&agrave;m nghĩa của &quot;</em>k&yacute; danh đệ tử<em>&quot;, chỉ biết rằng trước khi hết thời gian phải đi được đến đ&iacute;ch mới l&agrave; đạt kết quả. Hắn nh&igrave;n về ph&iacute;a trước nh&igrave;n tho&aacute;ng qua, đ&oacute; l&agrave; một sườn n&uacute;i kh&ocirc;ng t&iacute;nh nổi độ dốc, kh&aacute; nhiều th&acirc;n tr&uacute;c th&ocirc; tế bất đồng mọc tr&ecirc;n đ&oacute;, tựa hồ sẽ kh&ocirc;ng c&oacute; g&igrave; kh&oacute; khăn a.<br />\r\n<br />\r\nH&agrave;n Lập nh&igrave;n sang những người kh&aacute;c, hắn kh&ocirc;ng muốn phải thua dưới những người đồng lứa tuổi, m&agrave; những h&agrave;i đồng kh&aacute;c trong l&uacute;c đ&oacute;, kh&iacute; chất cũng tựu thay đổi, ph&uacute;t chốc bỗng trở l&ecirc;n khẩn trương.<br />\r\n<br />\r\nNhạc đường chủ nh&igrave;n mặt trời l&oacute; l&ecirc;n, n&oacute;i: &quot;</em>&nbsp;Thời gian cũng đến rồi, chuẩn bị xuất ph&aacute;t ba! Kh&ocirc;ng phải sợ h&atilde;i, c&aacute;c sư huynh sẽ ở ph&iacute;a sau bảo trụ mọi người, sẽ kh&ocirc;ng để c&aacute;c ngươi phải gặp nguy hiểm.&quot;<br />\r\n<br />\r\nH&agrave;n Lập quay đầu lại nh&igrave;n người thanh ni&ecirc;n đứng gần hắn, nguy&ecirc;n lai người n&agrave;y được gọi l&agrave; sư huynh, đại kh&aacute;i l&agrave; đệ tử được thu v&agrave;o trước đ&oacute;, nếu như m&igrave;nh được gia nhập, c&oacute; phải kh&ocirc;ng sẽ được mặc bộ quần &aacute;o oai phong thế n&agrave;y!</p>', NULL, 12, 1, '2020-09-11 06:04:46', NULL);

-- --------------------------------------------------------

--
-- Cấu trúc bảng cho bảng `failed_jobs`
--

CREATE TABLE `failed_jobs` (
  `id` bigint(20) UNSIGNED NOT NULL,
  `connection` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `queue` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `payload` longtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `exception` longtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `failed_at` timestamp NOT NULL DEFAULT current_timestamp()
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

